css:
@-moz-document domain("tweetdeck.twitter.com") {
.app-columns-container { 
  background: url(https://media.idownloadblog.com/wp-content/uploads/2016/02/Firewatch-Wallpaper-desktop-2560x1440-yellow.jpg) no-repeat center center fixed !important; 
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;
}


.js-app-header-inner, .js-app-header-inner a, .js-app-header-inner nav , .js-app-header-inner h1 {
	background-color: #2F343F;    
}

span.prf-img, img.avatar { 
    border: 0 !important; 
    background: 0 !important;
    box-shadow: 1px 1px 1px transparent;
}


.js-column-header {
    color: #D9DDE0;
    background-color: #2F343F;
}

.stream-item {
    color: #2F343F;
    background-color: #D9DDE0;
}

::-webkit-scrollbar-thumb {
	border-radius: 0px !important;
	background-color: #2F343F !important;
}

.stream-item .icon {
    color: #2F343F;
}

.stream-item span {
    color: rgba(47, 52, 63, 0.75);
}

.app-columns {
    display: inline;
    justify-content: flex-end;
}

.app-columns:after {
	width: 100%;
}



.column {
    margin: 2vh 2vw;
    height: 95vh;
    min-width: 325px;
    width: auto;
    max-width: 30vw !important;

}

.js-app-add-panel {
    display: none;
    opacity: 0;
}

.med-fullpanel {
    background-color: rgba(20,20,20,0);
}

.mdl {
    box-shadow: none;
}

.ovl, .overlay {
    background: rgba(17, 17, 17, 0.75);
}
}
