css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yoember.com") {
  
  /* == Page Sections ======================= */
  body { background-color: #000000 !important;}
  div > p { color: #66afe9 !important; }
  section > p { color: #66afe9 !important; }
  .container { background-color: #000000 !important;}
  
  /* == Headlines =========================== */
  h1 { color: #FFF; text-shadow: 0px 0px 9px rgba(150, 234, 55, 1) !important;}
  h2 { color: #FFF; text-shadow: 0px 0px 9px rgba(150, 234, 55, 1) !important;}
  h3 { color: #FFF; text-shadow: 0px 0px 9px rgba(150, 234, 55, 1) !important;}
  h4 { color: #FFF; text-shadow: 0px 0px 9px rgba(150, 234, 55, 1) !important;}
  
  /* == Mini Syntax Highlighting ============ */
  code { color: #fff !important; background-color: #0d3349 !important; }
  
  /* == Syntax Highlighting ================= */
  div[class*='highlighter-rouge'] > pre[class='highlight'] { color: #fff !important; background-color: #0d3349 !important; }
  
  /* Comments */
  .c1 { color: #777777 !important;}
  
  /* General */
  .nx { color: #BBBAAA !important;; }
  
  /* Keywords */
  .nt { color: #f0ad4e !important; }
  .k { color: #f0ad4e !important; }
  .nn { color: #f0ad4e !important; }
  .kd { color: #f0ad4e !important; }
  .kr { color: #f0ad4e !important; }
  
  /* String */
  .s { color: #61BB76 !important; }
  .s1 { color: #61BB76 !important; }
  .s2 { color: #61BB76 !important; }
  
  /* Boolean */
  .kc { color: aquamarine !important;}
  
  /* Properties */
  .na { color: #5bc0de !important; }
  .k + .nv { color: #f0ad4e !important; }
  .nv { color: #5bc0de !important; }
  .nv + .o + .nv { color: #fff !important; }
  
  /* Symbols, Errors */
  .err { color: #f0ad4e !important; background-color: #0d3349 !important; }
  .o { color: #f0ad4e !important; }
  
  /* OLD-STYLE-SYNTAX-COLORING
  .nt { color: #f0ad4e !important; }
  .k { color: #f0ad4e !important; }
  .nn { color: #f0ad4e !important; }
  .s { color: #01ff70 !important; }
  .s1 { color: #01ff70 !important; }
  .s2 { color: #01ff70 !important; }
  .na { color: #5bc0de !important; }
  .nv { color: #5bc0de !important; }
  */
  
  /* == Page Sections ======================= */
  
  .navbar > div  { background-color: #0d3349 !important; }
  .blog-masthead { background-color: #0d3349 !important; }
  .navbar-brand { color: #FFF !important; text-decoration: none !important; text-shadow: 3px 3px 10px rgba(182, 191, 249, 1);}
  
  /* == Text Sections ======================= */
  li { color: #ccc; }
  blockquote > p { color: #ccc; }
  a { color: #FFCC33 !important; font-style: italic; text-decoration: underline;}
}
