._48gf{
bottom:10px !important;
}

.fbNubButton{
background: rgba(255, 255, 255, 0.6) !important;
-webkit-backdrop-filter: blur(5px) !important;
}

/*浮動圖片窗樣式*/
._3qx, ._25u7, ._3ixn
{ 
    background-color:rgba(250, 250, 250, .6) !important;
    -webkit-backdrop-filter: blur(5px) !important;
}
div._n3
{
    border: 1px solid #ccc !important;
}

.fbNubFlyout 
{ 
    height: 500px !important; 
}

/*聊天室對話動畫*/
div._5wdf._5w1r,
div._51lq
{
	-webkit-animation:fadeInUp 0.5s !important;
	animation:fadeInUp 0.5s !important;
}
/*聊天室本體淡出效果*/
.fbNubFlyout,
div.fbNub._50-v._50mz._50m-
{
    -webkit-animation:fadeIn 0.5s !important;
	animation:fadeIn 0.5s !important;
}
/*動態載入時淡出*/
div._4-u2
{
	-webkit-animation:fadeInUp 1s !important;
	animation:fadeInUp 1s !important;
}
/*“上傳檔案”彈出框下落動畫*/
div._1yv
{
	-webkit-animation:fadeInDown 0.5s !important;
	animation:fadeInDown 0.5s !important;
}
/*彈出圖片框下落動畫*/
div._n3
{
	-webkit-animation:fadeInDown 0.5s !important;
	animation:fadeInDown 0.5s !important;
}

/*keyframes*/
@-webkit-keyframes fadeIn 
{
	0% { opacity: 0; }
	100% { opacity: 1; }
}
@keyframes fadeIn 
{
	0% { opacity: 0; }
	100% { opacity: 1; }
}
@-webkit-keyframes fadeInUp
{
    0% 
    {
		opacity: 0;
		-webkit-transform: translateY(10px);
		transform: translateY(10px);
	}
    100% 
    {
		opacity: 1;
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
@keyframes fadeInUp
{
    0% 
    {
		opacity: 0;
		-webkit-transform: translateY(10px);
		transform: translateY(10px);
	}
    100% 
    {
		opacity: 1;
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
@-webkit-keyframes fadeInDown
{
    0% 
    {
		opacity: 0;
		-webkit-transform: translateY(-10px);
		transform: translateY(-10px);
	}
    100% 
    {
		opacity: 1;
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
@keyframes fadeInDown
{
    0% 
    {
		opacity: 0;
		-webkit-transform: translateY(-10px);
		transform: translateY(-10px);
	}
    100% 
    {
		opacity: 1;
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
}
