css:
@-moz-document domain(vk.com) {
/*Post likes*/
.post_like_count {
    display: none;
}

.wk_like_count{
    display: none;
}
.pv_like_count {
    display: none;
}
/*Post reposts*/
.post_share_count {
    display: none;
}

/*Likes and reposts pop-up*/
.like_tt {
    display: none !important;
}

/*Comment likes*/
.wall_module .reply .like_wrap .like_count{
    display: none;
}

/*Increase comment like clickable area*/
.wall_module .reply .like_wrap{
    padding-left: 30px;
    margin-left: -30px;
    padding-top: 30px;
    margin-top: -30px;
}

/*Show comment like button even if it has no likes*/
.wall_module .reply .no_likes .like_icon, .wall_module .reply .reply_action{
    visibility: visible;
    opacity: 0.1;
}

/*Decrease opacity of comment like button*/
.wall_module .like_icon{
    opacity: 0.1;
}
}
