css:
@-moz-document domain("qz.com") {
/* geral */

body {
    background-color: #181818;
}

#chyron-message {
    background: #000;
    color: #c5c5c5;
    border-top: 0px solid #e2e2e2;
}

/* home */

.headline-title a {
    color: #eee;
}

.feature-article-title a {
    color: #eee;
}

.feature-article-summary {
    color: #c5c5c5;
}

.daily-brief-tagline {
    color: #c5c5c5;
}

.daily-brief-testimonial-quote {
    color: #969696;
}

.daily-brief-line {
    border-top: 0px solid #e2e2e2;
}

.home-promo-2-wrapper:before {
    border-bottom: 0px solid #e2e2e2;
}

#home-engage.is-hidden {
    border-bottom: 0px solid #000;
}

#home-feature-chart {
    background-color: #181818;
   	background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#101010), to(transparent));
	background-image: -webkit-linear-gradient(#101010, transparent);
	background-image: -moz-linear-gradient(#101010, transparent);
	background-image: -o-linear-gradient(#101010, transparent);
	background-image: linear-gradient(#101010, transparent) ;
}

#home-feature-chart h2 a {
    color: #eee;
}


#home-feature-chart .see-more {
    border: 1px solid #000;
    color: #969696;
    background: #000;
}

.home-gray-background-wrapper {
    background-color: #181818;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#101010), to(transparent));
	background-image: -webkit-linear-gradient(#101010, transparent);
	background-image: -moz-linear-gradient(#101010, transparent);
	background-image: -o-linear-gradient(#101010, transparent);
	background-image: linear-gradient(#101010, transparent) ;
}

#home-obsession {
    border-top: 0px solid #e2e2e2;
}

.home-obsession-article .home-obsession-title a {
    color: #eee;
}

.editions-article .article-title a {
    color: #c5c5c5;
}

.editions-article .article-summary {
    color: #969696;
}

#home-promotion-2 .article-title {
    color: #c5c5c5;
}

#home #home-promotion-2 {
    background-color: #181818;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#101010), to(transparent));
	background-image: -webkit-linear-gradient(#101010, transparent);
	background-image: -moz-linear-gradient(#101010, transparent);
	background-image: -o-linear-gradient(#101010, transparent);
	background-image: linear-gradient(#101010, transparent) ;
}

.home-recent-stories-header {
    background-color: #101010;
    border-bottom: 0px solid #e2e2e2;
}

.home-recent-stories-header h3 {
    text-align: center;
}

.home-recent .queue-article {
    background-color: #181818;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#101010), to(transparent));
	background-image: -webkit-linear-gradient(#101010, transparent);
	background-image: -moz-linear-gradient(#101010, transparent);
	background-image: -o-linear-gradient(#101010, transparent);
	background-image: linear-gradient(#101010, transparent) ;
}

#site-footer {
    border-top: 0px solid #e2e2e2;
    background: #181818;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#101010), to(transparent));
	background-image: -webkit-linear-gradient(#101010, transparent);
	background-image: -moz-linear-gradient(#101010, transparent);
	background-image: -o-linear-gradient(#101010, transparent);
	background-image: linear-gradient(#101010, transparent) ;
}

/* header */

#header {
    background: #000;
    border-bottom: 0px solid #e2e2e2;
}

#qz-logo svg {
    fill: #eee;

}

#header .left a, #header .left button {
    float: left;
    border-right: 0px solid #c5c5c5;
}

#header .left a.active, #header .left a.active i, #header .left a:hover, #header .left a:hover i, #header .left button.active, #header .left button.active i, #header .left button:hover, #header .left button:hover i, #header .right a.active, #header .right a.active i, #header .right a:hover, #header .right a:hover i, #header .right button.active, #header .right button.active i, #header .right button:hover, #header .right button:hover i {
    color: #eee;
}

#header .right a, #header .right button {
    float: right;
    border-left: 0px solid #e2e2e2;
}

#header .left a, #header .left button, #header .right a, #header .right button {
    color: #eee;
    fill: #eee;
}

/* header dark on index */

#header.transparent.dark-theme .left a, #header.transparent.dark-theme .left button, #header.transparent.dark-theme .right a, #header.transparent.dark-theme .right button {
    color: #eee;
}

.transparent.dark-theme #qz-logo svg {
    fill: #eee;
}

/* post */

article.item {
    background-color: #181818;
    border-bottom: 0px solid #333;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#101010), to(transparent));
	background-image: -webkit-linear-gradient(#101010, transparent);
	background-image: -moz-linear-gradient(#101010, transparent);
	background-image: -o-linear-gradient(#101010, transparent);
	background-image: linear-gradient(#101010, transparent) ;
}

.item-header h1 {
    color: #ddd;
}

.item-body p {
    color: #969696;
}

/*
url
*/

.article-footer .short-url input {
    color: #969696;
    border: 1px solid #101010;
    background: #101010;
}

.popular-stories-module article h3 {
    color: #969696;
}

/*
come back to
*/

#queue-toggle {
	color: #c5c5c5;
    background-color: #101010;
    border: 1px solid #101010;
}

.daily-brief-inline {
    border-top: 0px solid #e0e0e0;
}

.daily-brief-inline-signup-form-guts h3 {
    color: #969696;
}

.item-body blockquote p {
    color: #95BA9F;
}

.item-body blockquote::before {
    background-color: #95BA9F;
}

.item-body h3 {
    color: #DA7F00;
}

/* section */

#content-articles {
    background: #181818;
}

.queue-article-title a {
    color: #ddd;
}

.queue-article-summary {
    color: #969696;
}

/*.featured-image img {
    -webkit-filter: grayscale(100%);
}*/

#marquee-arrow {
    display: none;
}

.queue-article {
    border-bottom: 0px solid #333;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#101010), to(transparent));
    background-image: -webkit-linear-gradient(#101010, transparent);
    background-image: -moz-linear-gradient(#101010, transparent);
    background-image: -o-linear-gradient(#101010, transparent);
    background-image: linear-gradient(#101010, transparent) ;
}

.ad-engage {
    background: #101010;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#181818), to(transparent));
    background-image: -webkit-linear-gradient(#181818, transparent);
    background-image: -moz-linear-gradient(#181818, transparent);
    background-image: -o-linear-gradient(#181818, transparent);
    background-image: linear-gradient(#181818, transparent) ;
}

.item-body h1, .item-body h2 {
    color: #c5c5c5;
}

.featured-image-caption:after {
    border-bottom: 0px solid #e2e2e2;
}

article.item.bulletin {
    background-color: #181818;
}

dl, .item-body ol li, .item-body ul li {
    color: #969696;
}

/*
see all
*/

.term-banner {
    border-bottom: 0px solid #e2e2e2;
    background: #101010;
    text-align: center;
}

/* search */

#search-input {
    color: #c5c5c5;
}

#queue.searching,#well.searching { 
	background: url('data:image/svg+xml;utf8,<svg width="44" height="44" viewBox="0 0 44 44" xmlns="http://www.w3.org/2000/svg" stroke="#eee"><g fill="none" fill-rule="evenodd" stroke-width="2"><circle cx="22" cy="22" r="1"><animate attributeName="r" begin="0s" dur="1.8s" values="1; 20" calcMode="spline" keyTimes="0; 1" keySplines="0.165, 0.84, 0.44, 1" repeatCount="indefinite"/><animate attributeName="stroke-opacity" begin="0s" dur="1.8s" values="1; 0" calcMode="spline" keyTimes="0; 1" keySplines="0.3, 0.61, 0.355, 1" repeatCount="indefinite"/></circle><circle cx="22" cy="22" r="1"><animate attributeName="r" begin="-0.9s" dur="1.8s" values="1; 20" calcMode="spline" keyTimes="0; 1" keySplines="0.165, 0.84, 0.44, 1" repeatCount="indefinite"/><animate attributeName="stroke-opacity" begin="-0.9s" dur="1.8s" values="1; 0" calcMode="spline" keyTimes="0; 1" keySplines="0.3, 0.61, 0.355, 1" repeatCount="indefinite"/></circle></g></svg>') center center no-repeat; 
}

#container #search-input-container {
    border-bottom: 0px solid #333;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#000), to(transparent));
	background-image: -webkit-linear-gradient(#000, transparent);
	background-image: -moz-linear-gradient(#000, transparent);
	background-image: -o-linear-gradient(#000, transparent);
	background-image: linear-gradient(#000, transparent) ;
}
}
