css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {

/* Facebook Niskie Skladki tlo - based on PERFECT FACEBOOK 2016 */
  
/* change background image */

     body{
          background: url("http://i1380.photobucket.com/albums/ah194/jankowalski/brexit-background_zpsfeggpl39.jpg") fixed center !important;
          background-size: cover !important;  
     }  

/* hide elements */     
     
.fbFeedTicker{
     display:none !important;
}

 

#adsNav{
     display:none !important;
}

 

/*[[pagelinks]]*/

 

      
     
/* font size and face */

     @font-face {
         font-family: sarala;
         font-style: normal;
         font-weight: normal;
         src: url(https://fonts.gstatic.com/s/sarala/v1/s2t9exVCKzFG-HPaQHVGEA.woff2);
     }
        
     *{
          font-family: "sarala" !important;
          font-weight: normal !important;
     }  

     #userNav{
           font-size: 1.2em !important; 
      } 
          
    #u_jsonp_12_a{
          font-size:70% !important;
          display: none !important;
     }
  
/* global changes */  
     
     .fixed_elem{
          position: static !important;
     }
     
     ._64a > div:nth-child(2){
          height: auto !important;
     }
  
/* home page: left column */
         
     .navHeader{
          display: none !important;
     }
              
     .home #leftCol{
          position:fixed! important;
     }

     #leftCol{ 
          background-color: rgba(229,229,229,0.8);    
          margin-left: -1px !important;
          top: 40px !important;
          box-shadow: -10px 0px 0px 0px rgba(229,229,229,0.8);
          -webkit-border-bottom-left-radius: 10px;
          border-bottom-left-radius: 10px;  
     }
    

/* home page: news feed */
     
     
     #pagelet_feed_switcher{
          display: none !important;
     }
     
     #stream_pagelet{
          border: none !important;
          padding: 0 !important;
          margin: 0 !important;   
          background-color: rgba(255,255,255,0.9) !important; 
          box-shadow: 0px 0px 0px 12px rgba(255,255,255,0.9);              
     }  
  
     #contentCol {
          background-color: transparent !important; 
          padding-top: 0px !important;
     }   
  
/* home page: right column */     

    #rightCol  ._5rzs{
         position: static !important;
         padding-top: 10px !important;
         padding-left: 10px !important;
         margin-left: 2px !important;
         margin-top: -12px !important;
         box-shadow: 10px 0px 0px 0px rgba(229,229,229,0.8);
         background-color: rgba(229,229,229,0.8) !important;
         -webkit-border-bottom-right-radius: 10px;
         border-bottom-right-radius: 10px;
     }                
     
     #rightCol{
          padding-top: 0px !important;
     }
     
     
/* chat sidebar */   
         
     .fbChatSidebar{
          background-color: rgba(255,255,255,0.9) !important; 
          border: none !important;
          margin: 0 !important;
          box-shadow: none !important;
     }
     
/* timeline: main feed */

     ._5h60{
          background-color: transparent !important;
     }
 
     .timelineLayout #contentArea{
          background-color: rgba(255,255,255,0.9) !important; 
          margin: 0 !important;
          border: none !important;
          box-shadow: 0px 0px 0px 10px rgba(255,255,255,0.9);  
     }
     
     
     
 
     
/* pages: main feed */
    
     #pagelet_timeline_main_column{
          background-color: transparent !important;  
          box-shadow: none !important;
     }
      
     ._2gqw > div:nth-child(1) > div:nth-child(1){
          background-color: rgba(255,255,255,0.9) !important; 
          margin: 0 !important;
          border: none !important;
          box-shadow: 0px 0px 0px 10px rgba(255,255,255,0.9); 
     }
 
     
/* pages: timeline year selector */    
     
     [id^="PageScrubberPagelet"] {       
         background-color: rgba(229,229,229,0.8) !important; 
         position: fixed !important;
         width: 80px !important;
         height: auto !important;
         margin-left: -2px !important;
         margin-top: -11px !important;
         padding-left: 10px !important; 
         padding-right: 0px !important;
         padding-top: 10px !important;
         padding-bottom: 10px !important;
         -webkit-border-bottom-right-radius: 10px;
         border-bottom-right-radius: 10px; 
     }
        
     a._3d5b{
         color: #333 !important;
     }
         
     #pagelet_ego_pane, ._18fs{
          display: none !important;
     }       
     
/* messaging full page */ 
     
     #pagelet_web_messenger{
          background-color: rgba(255,255,255,0.9) !important; 
          margin: 0 !important;
          border: none !important;
          box-shadow: 0px 0px 0px 12px rgba(255,255,255,0.9); 
          
     }
     
     
/* events: upcoming  events page */      
     
     #pagelet_calendar_content{
          background-color: rgba(255,255,255,0.9) !important; 
          margin: 0 !important;
          border: none !important;
          box-shadow: 0px 0px 0px 12px rgba(255,255,255,0.9);  
     }
     
     #pagelet_birthday_this_week{
          padding-top: 5px !important;
     }
     
/* events: single event page */      
     
     
     body._4-u5 ._3557{
          background-color: rgba(255,255,255,0.9) !important; 
          margin-right: -10px !important;
          border: 0 !important;
          padding: 0 !important; 
          box-shadow: 0px 0px 0px 10px rgba(255,255,255,0.9); 
     }      

     ._3556{
          background-color: rgba(229,229,229,0.8);    
          margin-right: 10px !important;
          -webkit-border-bottom-left-radius: 10px;
          border-bottom-left-radius: 10px;             
     }

     ._fbEventsPermalink__permalinkLayout{
          background: transparent !important;
     }
     
     ._5w40{
          border: none !important;
     }
     
/* groups page */         

     #pagelet_group_{
          background-color: rgba(255,255,255,0.9) !important; 
          box-shadow: 0px 0px 0px 12px rgba(255,255,255,0.9);      
     }
     
     .groupsJumpBarContainer{
         background-color: #fff !important;
     }
     
     ._3v07{
          background-color: rgba(255,255,255,0.9) !important;
          box-shadow: 10px 0px 0px 0px rgba(229,229,229,0.8);
          margin-left: -12px !important;
     }
     
     
     ._4-u2{
          border-radius: 0px !important;
          -webkit-border-radius: 0px !important;
     }
     
/* hide advertisments */

.fbTimelineSideAds, .UIStandardFrame_SidebarAds, #rightCol #pagelet_ego_pane .ego_column:nth-child(1),
#pagelet_side_ads, .ego_column, #sidebar_ads, .fbPhotoSnowboxAds, .fbPhotoSnowboxAdsSide, #pagelet_adbox,
.fbEmu.fbEmuBlock.fbEmuEgo[id*="-id_"], #ego, a[href*="/campaign/111111"], .fbEmuMarketplace,
#egoRefreshAds, #pagelet_ego_pane_m, #pagelet_ego_pane_w, .profile_sidebar_ads, .sidebar_ads, .adcolumn_header, 
.admarket_ad, .ad_story, .social_ad_advert, .ads_feedback, .next_ad_button, .more_ads, #pagelet_ego_pane{
     background: none !important;
     display: none !important;
} 

#pagelet_rhc_footer{
     display: none !important;
     background: none !important;
}

.fbFeedTicker{
     display:none !important;
}
     

  

}
