css:
@-moz-document url-prefix("https://forum.yoyogames.com/") {
.message .messageInfo {
    background-color: #fff;
}
.messageText {
    color: rgb(0, 0, 0);
}
#content .pageContent {
    background-color: #eee;
}
#header {
    background-color: rgb(24, 51, 51);
}
.tagList .tag {
    background: rgb(205, 253, 159);
    border: 1px solid rgb(117, 173, 63);
    color: black;
}
.profilePage .primaryUserBlock .secondaryContent {
    background-color: #f9f9f9;
}
.section.primaryUserBlock .tabs.mainTabs.Tabs {
    background-color: #f9f9f9;
}

.newMessagesNotice.messagesSinceReplyingNotice{
    border-radius: 0;
    border: 2px solid rgb(0, 100, 36);
    background: rgb(238, 238, 238)
}

.newMessagesNotice.messagesSinceReplyingNotice a{
	color: rgb(15, 167, 20);
    font-weight:bold;
}


.redactor_toolbar li li:hover a{
	background-color:rgba(0,0,0,0);
}

/*Float*/

.xenOverlay .section,
.xenOverlay .sectionMain {
    background-color: rgb(255, 255, 255);
    border-radius: 10px;
    border: 7px solid #dddddd;
}
.xenOverlay .section h2 {
    color: black;
}
.profilePage .tabs.mainTabs li.active a {
    background-color: rgb(238, 238, 238);
}
.event .content .snippet {
    color: black;
}
.button.OverlayCloser.OverlayTrigger {
    color: black;
}
.button.OverlayCloser.OverlayTrigger:hover {
    color: white;
    font-weight: bold;
}
.mainProfileColumn .userBlurb .muted {
    color: #4d7f32;
}
.mainProfileColumn .userBlurb {
    color: #5f5c5c;
}
.profilePage .primaryUserBlock .userStatus {
    color: rgb(37, 164, 117);
}
/*Calendar*/

#caltitle {
    color: rgb(77, 127, 50);
    font-weight: bold;
}
#calroot {
    background-color: white;
}
.calweek a {
    color: black;
}
.calweek a.caloff {
    color: gray;
}
#calcurrent {
    background-color: rgb(136, 136, 136);
    color: white;
}
.calweek a:hover {
    color: white;
    background-color: rgb(84, 107, 71);
    ;
}
#caldays {
    height: 16px;
    border-bottom: 1px solid rgb(107, 148, 84);
}
#caldays span {
    font-weight: bold;
}
.attachedFiles .attachedFilesHeader {
    color: white;
    background-color: #566495;
}
.attachmentList.SquareThumbs {
    background: white;
}
.discussionListItem {
    background-color: white;
}
.article .blockquote {
    border: 0;
}
.baseHtml.noticeContent {
    color: white;
}
.PanelScroller .scrollContainer,
.PanelScrollerOff .panel {
    border: 0;
}
.messageText.SelectQuoteContainer.ugc.baseHtml div {
    border: 0;
}
.messageText.SelectQuoteContainer.ugc.baseHtml b {
    border: 0;
}
.messageText.SelectQuoteContainer.ugc.baseHtml font {
    border: 0;
    font-weight: normal;
}
.message .newIndicator {
    color: rgb(255, 255, 255);
    background-color: rgb(60, 60, 60);
    border: 0;
}
.message .newIndicator span {
    background-color: #505050;
}
.xenOverlay .formOverlay {
    background-color: rgb(255, 255, 255);
    border-radius: 5px;
    border: 10px solid rgba(0, 0, 0, 0.25);
}
.button.primary {
    color: white;
    background-color: rgb(118, 185, 83);
}
.button.primary:hover {
    background-color: rgb(31, 52, 21);
}
.button {
    color: rgb(13, 55, 6);
    background-color: rgb(215, 215, 215);
    border-radius: 5px;
}
.bbCodeQuote .quoteContainer .quoteExpand {
    color: rgb(196, 255, 164);
}

.bbCodeBlock.bbCodeQuote{
	border-radius:0;
}

.xenTooltip.flipped {
    background-color: rgb(66, 66, 66);
}
#searchBar #QuickSearch {
    background-color: rgb(77, 127, 50);
    width: 250px;
    height: 26px;
}
#QuickSearch .secondaryControls {
    background: rgb(226, 226, 226);
    border-radius: 0;
    border: 3px solid rgb(77, 127, 50);
}
.textCtrl.AutoComplete,
.textCtrl.date,
.textCtrl.StatusEditor.UserTagger.Elastic,
#QuickSearchQuery {
    background-color: white;
}
textarea.textCtrl:focus {
    background-color: white;
}
.titleBar h1 em {
    color: rgb(162, 162, 162);
}
.formPopup .controlsWrapper {
    background: rgb(226, 226, 226);
}
.formPopup .secondaryControls {
    padding: 0;
}
.button.moreOptions.Tooltip {
    color: black;
    background-color: #c1c1c1;
}
.button.moreOptions.Tooltip:hover {
    color: white;
    background-color: #4d7f32;
}
body p {
    color: white;
}
/*Menu "Alerts"*/

.section ul li .primaryContent {
    background-color: white;
    color: rgb(50, 92, 25);
    font-weight: bold;
}
.section ul li .primaryContent:hover {
    background-color: rgb(118, 174, 63);
    color: white;
}
.xenOverlay.lightBox #LbUpper,
.xenOverlay.lightBox #LbLower {
    background-color: rgb(255, 255, 255);
}
.lightBox .thumbsContainer {
    background-color: rgba(0, 0, 0, 0.15);
}
.lightBox .imageContainer {
    background: rgba(255, 255, 255, 0.5);
}
.hasJs .bbCodeSpoilerText {
    background-color: rgb(226, 226, 226);
}
.button.smallButton {
    color: black;
}
.mainContentBlock.section.sectionMain.insideSidebar p {
    color: #363636;
}
.redactor_box .placeholder span {
    color: white;
}

*::selection {
	color:white;
    background-color:#7eba43;
}


*::-moz-selection {
	color:white;
    background-color:#7eba43;
    /* Gecko Browsers */
}
div.baseHtml.signature.messageText.ugc aside {
    border: 0;
}
.PageNav a:hover {
    color: rgb(216, 255, 12);
}
.tagList .tag:hover {
    background: rgb(78, 117, 40);
    color: white;
}
.messageUserBlock div.avatarHolder {
    border: 1px solid #d6d6d6;
    border-bottom: 0;
}
.messageUserBlock h3.userText {
    border: 1px solid #d6d6d6;
    border-top: 0;
}
.messageUserBlock .extraUserInfo {
    border: 1px solid #d6d6d6;
}
.messageUserBlock h3 {
    border-top: 0;
}
.breadcrumb {
    border: 0;
    border-radius: 0;
    background-color: rgb(226, 226, 226);
}
.crumb span {
    color: white;
}
.crumb span:hover {
    color: #ffef9e;
}
.breadcrumb .crust a.crumb {
    border-bottom: 0;
    background-color: rgb(60, 60, 60);
}
.breadcrumb .crust .arrow span {
    border-left-color: rgb(60, 60, 60);
}
.node .forumNodeInfo.unread .nodeIcon,
.node .categoryForumNodeInfo.unread .nodeIcon {
    background: url(https://dl.dropboxusercontent.com/u/39289049/node-sprite2.png) no-repeat;
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    border-bottom: 1px solid rgb(210, 210, 210);
}

.nodeList li .nodeInfo.forumNodeInfo.primaryContent .nodeText .nodeStats.pairsInline dl dt{
	color:#8b8b8b;
}

a.callToAction span{
	color:white;
    background-color: rgb(76, 76, 76);
    border-radius:2px;
}

.tabLinks.forumsTabLinks .secondaryContent a{
    border-radius:0;
}

.threadListItem .additionalRow.muted, .threadListItem .additionalRow.muted .forumLink{
	color:#8d8d96;
}

.threadListItem .messageInfo .messageContent .title a{
	color:rgb(96, 153, 59);;
}

.section.visitorPanel{
	background-color:#677f5b;
}


.section.visitorPanel .secondaryContent .visitorText .stats .pairsJustified dt{
    color:#686868;
}



a.callToAction{
    border-radius:2px;
}

a.callToAction:hover{
	border-color:rgb(77, 127, 50);
}

a.callToAction:hover span{
	background-color:black;
}

.posterDate.muted .itemPageNav a{
    background-color: #6b6b6b;
    color: white;
}

.posterDate.muted .itemPageNav a:hover{
    background-color: #3f443c;
}


.sectionMain,
.node .nodeLastPost .nodeInfo,
.nodeList .categoryStrip {
    border-radius: 0;
}
.navTabs {
    background-color: rgb(21, 44, 44);
    border-radius:0;
}

#pageDescription, #pageDescription a{
	color:#6f6f6f;
}

.secondaryContent {
    background-color: #eee;
    padding: 4.5px;
}
.discussionListItem .stats dl {
    border-left: 1px solid rgb(179, 179, 179);
    border-right: 1px solid rgb(179, 179, 179);
}
.message .messageInfo {
    border: 1px solid #d6d6d6;
}
.bbCodeBlock .type {
    background-color: #4d7f32;
    border-radius: 0;
}
.bbCodeBlock pre,
.bbCodeBlock .code {
    background-color: rgb(249, 249, 249);
}
.message .messageText > *:first-child {
    border-radius: 10px 10px 0 0;
    border: 1px solid rgb(226, 226, 226);
    font-weight: bold;
}
.bbCodeBlock {
    color: black;
    border-radius: 10px 10px 0 0;
    border: 1px solid rgb(226, 226, 226);
    -webkit-box-shadow: 4px 4px 4px #dcdcdc;
}
.discussionListItem .title {
    font-weight: bold;
}
#SignupButton .inner {
    background-color: #58822f;
    border: 2px solid #58822f;
    color: white;
    text-shadow: 0 0 0;
}
#SignupButton .inner:hover {
    background-color: #8cd248;
    border: 2px solid #8cd248;
    color: white;
}
.secondaryContent h3 {
    text-align: center;
    background-color: rgb(222, 222, 222);
}
.message .likesSummary {
    border: none;
    color: #333;
}
.titleBar {
    color: #333
}
.messageUserBlock div.avatarHolder,
.messageUserBlock h3.userText,
.messageUserBlock .extraUserInfo {
    background-color: #fff;
    color: #333
}
.messageUserBlock .arrow span {
    border-left-color: #fff;
    -moz-border-left-color: #fff
}
.bbCodeQuote .attribution {
    color: rgb(255, 255, 255);
    background-color: #4D7F32;
}
.bbCodeQuote .quoteContainer {
    background-color: #eee;
    color: #000;
}
.bbCodeQuote {
    background-color: #999;
    border-color: #999;
    -webkit-border-radius: 10px;
    -moz-border-radius: 10px;
    -khtml-border-radius: 10px;
    border-radius: 10px;
    overflow: auto;
    -webkit-box-shadow: 0px 0px 2px #aaa;
    -moz-box-shadow: 0px 0px 2px #aaa;
    -khtml-box-shadow: 0px 0px 2px #aaa;
    box-shadow: 0px 0px 2px #aaa;
}
#QuickSearch {
    background-color: rgba(28, 28, 28, 0);
}
.formPopup {
    background-color: rgba(28, 28, 28, 0);
}
#QuickReply {
    background-color: #fff;
}
html {
    color: #000;
    background-color: #fff;
}
#copyright {
    color: #000;
}
.sectionMain {
    padding: 0;
}
.sectionMain,
.node .nodeLastPost {
    background-color: #fff;
}
.node .nodeDescription {
    color: #000;
}
body {
    color: #333;
    background-color: #183333;
    font-family: Arial, "Helvetica Neue", Helvetica, sans-serif;
}
.discussionListItem .posterAvatar,
.discussionListItem .stats {
    background-color: #fff;
}
.discussionListItem {
    border-bottom: 1px solid rgb(179, 179, 179);
}
.xenOverlay.memberCard {
    color: #000;
    background-color: #fff;
}
.textHeading,
.xenForm .sectionHeader,
.event .content .description em {
    color: #000;
}
.profilePage .tabs.mainTabs {
    background-color: #eee;
}
.navTabs .navTab.PopupClosed .navLink {
    color: #fff;
}

.Responsive .navTabs .navTab.PopupClosed:hover {
    background-color: #102121;
}

.sectionFooter {
    color: #999;
}
#DiscussionListOptionsHandle a {
    color: #fff;
    background-color: #8CD248;
    border: 1px none rgb(128, 128, 128);
}
body .dimmed,
body a.dimmed,
body .dimmed a {
    color: #000;
}
.tabs {
    font-size: 11px;
    background-color: #e2e2e2;
    border-bottom: 1px solid rgb(191, 190, 190);
}
.tabs li a,
.tabs.noLinks li {
    background-color: #999999;
}
.tabs li a:hover {
    background-color: #4d7f32;
}
.tabs li.active a,
.tabs.noLinks li.active {
    background-color: rgb(238, 238, 238);
    color: black;
}
.primaryContent.memberListItem {
    border-bottom: 1px solid rgb(191, 190, 190);
}
.primaryContent {
    border-bottom: 1px solid rgb(161, 161, 161);
}
.comment.secondaryContent {
    background-color: #ffffff;
    border: 1px solid rgb(191, 190, 190);
}
.xenOverlay .formOverlay .heading {
    color: rgb(85, 107, 88);
}
.message .messageText > *:first-child {
    border: 0;
}
.Menu .secondaryContent {
    background-color: #fff;
}
.blockLinksList a,
.blockLinksList label {
    color: #999;
}
.statusEditorCounter {
    color: #8CD248;
}
.Menu {
    background-color: #fff;
}
#AccountMenu .menuHeader .links .fl a,
.sectionFooter a {
    color: #8CD248;
}
.separate_thread_important {
    background-color: #28401b;
    color: #fff;
}
.PageNav a {
    color: #fff;
    background-color: #999;
}
.PageNav a.currentPage {
    color: #fff;
    background-color: #8CD248;
}
.userBanner.bannerStaff,
.userBanner.AdminBanner,
.userBanner.ElderBanner,
.userBanner.YYGBanner {
    border-color: rgba(38, 38, 38, 0);
}
.textCtrl {
    color: #000;
    background-color: #fff;
}
.searchResult .meta a {
    color: #000;
}
.searchResult .snippet a {
    color: #666;
}
.prefix {
    border-color: rgba(1, 1, 1, 0);
}
.footer .pageContent {
    background-color: rgb(202, 202, 202);
}
.footer a,
.footer a:hover {
    color: rgb(58, 58, 58);
}
}
