css:
@namespace url(http://www.w3.org/1999/xhtml); 

@-moz-document domain("sasisa.ru") { 

  /* THEME 7 */

  body {font-weight: normal; font-size: 12px; color: #40C738;  font-family: sans-serif; background-color: #000000; margin: 1px; background: #28342d; }
  a {color: #1FA354; font-size: 12px; text-decoration: none; }
  a:hover {text-decoration: underline; color: #FF0000; }
  div {margin: 0px 0px 0px 0px; padding: 2px 1px 1px 2px; word-wrap: break-word; border-right: 1px solid #E0E0E0; border-left: 1px solid #E0E0E0;}
  fieldset { border-bottom: 1px solid #E0E0E0; border-top: 1px solid #E0E0E0; border-left: 1px solid #E0E0E0; border-right: 1px solid #E0E0E0; }
  .red, .error { color: #FF0000; }
  .yellow { color: #FFFF00; }
  .dgreen { color: #5C6400; }
  .error { font-size: small; }
  div.main {background-color: #FFFB8E; border: 0px solid #91DB12; color: #FFFFFF; }
  div.sec {padding: 2px 1px 1px 2px; background-color: #28342D; border-bottom: 2px solid #3F573E; border-top: 2px solid #81F34E;  border-left: 2px solid #81F34E; border-right: 2px solid #81F34E; color: #FFFFFF; }
  div.news {padding: 2px 1px 1px 2px; background-color: #28342D; border-bottom: 2px solid #B0EF4C; border-top: 2px solid #D9FFA0; border-left: 2px solid #D9FFA0; border-right: 2px solid #D9FFA0; color: #A7051B; }
  div.news2, div.header, div.c1 {padding: 2px 1px 1px 2px; background-color: #28342D; border-bottom: 2px solid #B0EF4C; border-top: 2px solid #D9FFA0;  border-left: 2px solid #D9FFA0; border-right: 2px solid #D9FFA0; color: #A7051B; }
  div.news2 { font-size: 11px; }
  div.men {padding: 2px 1px 1px 2px; background-color: #FFFFFF; border: 0px solid #91DB12; color: #A7051B; }
  div.ten, div.c3 , div.c2 {padding: 2px 1px 1px 2px; background-color: #3F4C3E; border-bottom: 2px solid #84DE00; border-top: 2px solid #CDFF7F;  border-left: 2px solid #CDFF7F; border-right: 2px solid #CDFF7F; color: #000000; }
  div.zero {padding: 2px 1px 1px 2px; background-color: #3F4C3E; border: 0px solid #84DE00; color: #A7051B; }
  .zero2, .item {padding: 3px 1px 2px 2px; background-color: #28342d; border-bottom: 1px solid #E0E0E0; border-top: 1px solid #B0EF4C; border-left: 1px solid #E0E0E0; border-right: 1px solid #E0E0E0; color: #A7051B; word-wrap: break-word; overflow: hidden; }
  div.quat { color: #00D900 }
  div.ie {padding:2px 1px 1px 2px; background-color: #FFFB8E; border: 0px solid #FFFB8E; color: #A7051B; text-align: left;}
  div.q { background-color: #F7F8F7; margin-left: 5px; border: 1px solid #C6FF72; margin-top: 2px; margin-bottom: 2px; }
  dt {border-bottom: 1px solid #E0E0E0; border-top: 1px solid #FFFFFF; padding: 2px;}
  dl {margin-top: 0px; margin-bottom: 0px; margin-left: 0px;}
  hr {height: 1px; border-width: 0px; background-color: #E0E0E0; color: #FFFFFF; margin-top:2px; margin-bottom:1px; margin-left: -2px; margin-right: -1px;}
  input.ibutton { font-size: 1em; border-radius: 3px; padding: 1px 3px; margin: 2px; line-height: 120%; color: #000000; background-color: #AEEF4D; border: 1px solid #000000;}
  table { border-color: #AEEF4D; border-style: solid; border-width: 0 1px 1px 0; }
  td, th { border-color: #AEEF4D; border-style: solid; border-width: 1px 0 0 1px; }
  th { background-color: #AEEF4D; }
  img { border: 0; vertical-align: middle; max-width: 100%; }
  a.block { text-decoration: none; display: block; }
  a.block:hover { text-decoration: none; }
  span.text { color: #1FA354; }
  div.zero2 span.text { color: #A7051B; }
  div.item span.text { color: #A7051B; }
  a.button { margin: 2px; line-height: 120%; background-color: #AEEF4D; border: 1px solid #000000; border-radius: 3px; padding: 1px 3px; text-align: center; text-decoration: none; display: inline-block; }

  .pages {text-align: center; padding: 3px 0; }

  .pages a, .pages b {
      display: inline-block;
      cursor: pointer;
      text-decoration: none;
      border: 1px solid #84DE00;
      border-radius: 3px 3px 3px 3px;
      padding: 3px 6px;
      vertical-align: middle;
      margin: 2px;
      background-color: #F7F8F7; 
  }

  .pages b {
      border: 1px solid #84DE00;
      background-color: #AEEF4D; 
  }

  .pages fieldset {
    border: 0; padding: 0; 
  }

  .pages form {
    margin: 0; 
  }

ul {
list-style-type:disc;
margin-left: 0; padding-left: 0;
}

li ul, li ol {
margin:0 1.5em;
}
ul, ol {
margin:0 1.5em 1.5em 1.5em;
}

/* disable zooming on focus */
textarea {
    font-size: 1em;
    line-height: 1.5em;
}

input[type=text] {
    font-size: 1em;
    line-height: 1.5em;
}

