css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("coursera.org") {
  
  /* Common */
  htmll, body {
    background-color: #150c0c !important;
    color: #aaa !important;
  }
  
  /* Main content background */
  #rendered-content,
  .rc-CatalogApplication {
    background-color: #571919 !important;
  }
  
  /* Main Header */
  .rc-PageHeader .c-ph-nav {
    background-color: #150c0c !important;
  }
  
  /* Buttons */
  button.nostyle, button.button-link {
    filter: invert(100%) !important;
  }
  
  /* Search > Course items */
  .rc-OfferingCard .offering-content,
  /* Home > Course items */
  .card-no-action, .card-rich-interaction, .card-one-clicker, .card-one-clicker:hover, .modal.card-rich-interaction, .modal.card-one-clicker, .modal.card-one-clicker:hover, .selected[class*="card-"], ul.styleguide.dropdown {
    background-color: #180c0c !important;
    border: 1px solid rgba(0,0,0,0.1) !important;
  }
  
  /* Search > Course items > Title */
  .color-primary-text, ul.styleguide.dropdown li button, ul.styleguide.dropdown li button.secondary {
    color: #aaa !important;
  }
  
  /* Course description */
  .c-coursePage-body-container {
    background-color: #571919 !important;
  }
  
  .c-phoenix-template-page {
    background-color: #210f0f !important;
  }
  
  .rc-Menu {
    background-color: #210f0f !important;
  }
  
  .rc-OndemandApp [class*="card-"] {
    background-color: #241010 !important;
  }
  
  .rc-HomeLayout .rc-HomeLayoutBody {
    background-color: #571919 !important;
  }

  /* Course Navigation */
  /* Top left back button */
  .rc-ItemPrimaryNav .back-button {
    color: rgba(255, 255, 255, 0.5) !important;
  }

  .rc-ItemPrimaryNav .side-nav-button span, .rc-ItemPrimaryNav .side-nav-button:focus span, .rc-ItemPrimaryNav .side-nav-button:active span {
    color: rgba(255, 255, 255, 0.5) !important;
  }
  
  /* Video */
  .theme-light {
    background-color: #571919 !important;
  }
  
  .theme-white {
    background-color: #210f0f !important;
  }
  
  .color-secondary-text {
    color: rgba(255, 255, 255, 0.5);
  }
  
  .content-container {
    background-color: #000 !important;
    color: #aaa !important; /* has no effect? */
  }

  legend {
    color: #aaa !important; /* has an effect */
  }
  
  .rc-WeekDrawer a.week-link {
    color: rgba(192, 192, 192, 0.9);
  }

  .rc-Phrase {
    color: rgba(192, 192, 192, 0.8);
  }
  
  .coursera-assess-assessment-in-video,
  .coursera-assess-assessment-in-video .coursera-assess-assessment-in-video-bar {
    background-color: #1e1616;
  }
  
  .alert-feedback {
    color: rgba(192, 192, 192, 0.5);
  }

}
