css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.zhihu.com") {
.zu-top {
  top: -40px !important;
  -moz-transition: top 200ms ease-out 500ms;
  -webkit-transition: top 200ms ease-out 500ms;
  transition: top 200ms ease-out 500ms;
}

.zu-top:hover {
  top: 0 !important;
  -moz-transition-delay: 0s;
  -webkit-transition-delay: 0s;
  transition-delay: 0s;
}

A.zm-item-vote-count.js-expand.js-vote-count{display:none;}
IMG.zm-item-img-avatar{display:none;}
FORM#zh-top-search-form.zu-top-search-form{display:none;}
DIV.zu-main-sidebar{display:none}
DIV.zm-meta-panel{display:none}
DIV.zm-votebar.goog-scrollfloater{display:none;}
IMG.zm-item-img-avatar{display:none !important;}
DIV.zm-item-vote-info{display:none !important;}

DIV.zu-main-content-inner{width:960px;}

/*inner page*/
DIV.zm-votebar{display:none !important;}
DIV.body.clearfix{display:none !Important;}
SPAN.bio{display:none !important;}
DIV.zh-summary.summary.clearfix{display:none !important;}
}
