css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("startpage.com"), domain("ixquick.com") {

#home_wrap, #results, #content-box, #wrapper, #page-wrapper,.sidebar-collapse, .text-fields, input, .input_field, .wrapper-dropdown, .dropdown li,
.slide-menu li, #footer, .home-wrapper, #pics-lemmon-slider-id, .dropdown li:hover, #menu-id ul li a:hover, .set-heading, .toggle-content,
.toggle-label, .navbar .navbar-header {
    background-color: #202020 !important;
}
.navbar .navbar-header {
    background-image: none !important;
}
#pagenavigation #jumpsbar span.active, #pagenavigation .numbers span.active {
    color: #000000 !important;
    background-color: #648AC6 !important;
    background-image: none !important;
}
.result {
    margin-bottom: 3px !important;
}
.sidebar-collapse {
    margin-bottom: 3px !important;
}
#field_size, .settings .form-group .input-group input {
    border: 1px #648AC6 solid !important;
}
#footer, .direct_results_box {
    border-top: 1px #648AC6 solid !important;
}
.side_list_menu {
    border-bottom: none !important;
}
.a:hover, #menu-id ul li a:hover {
    text-decoration: underline !important;
}
#sponsored_container1 {
    display: none !important;
}
.set-heading {
    margin-bottom: 3px !important;
    border-radius: 15px !important;
}
#head_left a, .result_url_heading, .result, #results .result h3 a, .side_list_menu li a, #head_left a, .slide-wrapper ul li a,
#results .result h3 a, .slide-wrapper, .classified .relatedlist_wrap p a, #bookmark_tip, p.sitelinks a:visited, p.sitelinks a, .sellerrating a:visited,
.side_list_menu li a, #head_left.non_js_tab a.active, #pagenavigation a {
    color: #5778AD !important;
}
input {
    color: #FFFFFF !important;
}
.result .url, #side_bar2 ul#settings li a {
    color: #3C733D !important;
}
.result .desc, input#submit1, input#query_top, input#query, .classified .relatedtext {
    color: #A5A5A5 !important;
}
#results .result h3 a:visited, .result a:visited .result_url_heading {
    color: #7B6296 !important;
}
#head_left {
    border-bottom: none !important;
}
.navbar-default, .slide-wrapper .slide-menu>li:last-child {
    border-bottom: solid 1px #5778AD  !important;
}
#field_size, .set-heading {
    border: 1px #5778AD solid !important;
}
.navbar-default.navbar-static-side {
    border-bottom: none !important;
}
.slide-wrapper .slide-menu, .slide-wrapper .slide-menu>li, .classified, .links_n.active {
    border-color: #5778AD !important;
}
.no-js .slide-wrapper {
    border-bottom: none !important;
}
.slide-wrapper {
    border-top: solid 1px #5778AD !important;
}
.slide-wrapper li a, .dropdown li a, .toggle-label {
    color: #9E9E9E !important;
}
.proxy, p#caption a, .top_menu a {
    color: #666 !important;
}
.settings_cont.inner-pages.three_line_menu {
    background: none;
}
.numbers_st .i_next, .numbers_st .i_prev {
      display: none;
}
.navbar-brand, #main_link {
    opacity: 0.8;
}
div#links, div.footer_links_bottom_right, div#enhanced, span.go-btn, span.small-go-btn, div.intro, span.pe_start, ul#settings {
    display: none;
}
.clear_search {
    margin-right: -45px;
}
}
