css:
@-moz-document domain(megamozg.kz) {

.navbar.navbar-default.navbar-fixed-top{
    background-color: #ffffff;
    border-color: #ffffff;
    box-shadow: 1px 0px 4px 2px #CCC;

}


.navbar.navbar-default.navbar-fixed-top .container
{
        margin: 0 auto;
    max-width: 1065px;
    
}

body
{
        font-family: Cuprum, Arial;
}


header, #content3, body, .content-box {
    background: rgb(243, 243, 243);
}

#content3 .content-box {
    margin-top: 25px;
      background: rgba(255, 255, 255, 0);
    padding: 0 !important;
}

.container_16, .container_16 .grid_11, #testing-question-variants
{
    margin: 0;
}
.container_16 .grid_11{
    background: #fff;
        margin-left: 10px;
       box-shadow: 1px 0px 15px 1px #ccc;
}

#testing-div>div 
{
    min-height: inherit !important;
}

#testin-basic-results
{
        padding: 25px 0 25px 25px;
    
    /*background: #609fd9;
    color: #fff;
*/
}

#testin-basic-results 
{
    
    font-family: Cuprum, Arial !important;
}

#testing-rules #testing-h2 {
    text-align: left;
    background: #ffffff;
    border-top: 1px solid #e2e2e2;
}

#testing-loading
{
margin: 0;
}

div#testing-question-variants>div {
    padding: 10px 0 10px 10px;
    margin-bottom: 0;
}


div#testing-question-variants>div:hover {
    background: #609fd9;
    color: #fff;
    margin: 0px -15px;
    padding-left: 25px;
}


#testing-variant {
    margin: 2px -15px;
    padding: 5px 0 5px 20px;
}

#testing-list-questions
{
     background-color: #ffffff;
}

#testing-table-questions {
    width: 100%;
    margin: 0;
}

#submit_data_button2 {
    border: 0;
    background: #d9606e;
        cursor: pointer;
}

#testing-list-questions-title2 {
    padding: 10px;
}

#testing-table-questions div {
    background: #ffffff;
    font-size: 18px;
    color: #000;
}

#testing-table-questions td:hover {
     opacity: 1;
    background: #ccc;
}

#testin-basic-results~ #testing-list-questions-title, #testin-basic-results #testing-list-questions-title{
    display: inline-block;
    position: relative;
        background: #d9606e;
    border: 1px solid #d9606e;
}

#testing-div #testing-list-questions-title::after {
    content: " ";
    position: absolute;
    right: -43px;
    top: -1px;
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 25px 0 25px 43.3px;
    border-color: transparent transparent transparent #d9606e;
}

#testing-question-text {
    padding: 10px;
}

#testing-rules {
    text-align: left;
    background: #ffffff;
    border-top: 1px solid #e2e2e2;
}

div#testing-question2 {
    /* display: inline-block; */
    float: right;
    margin-top: -65px;
    width: calc(100% - 185px);
}

div#testing-no-yes {
    margin-top: -29px;
}


.container_16 .grid_5 {
    width: 280px;
    margin-left: 30px;
    margin-right: 0;
    background: #fff;
}

footer {
display: none;
}


.results {
    padding: 0 20px;
}


.otvet {
    background-color: #2489c5;
    padding-bottom: 8px;
    padding-top: 8px;
    font-size: 25px;
    margin: 0 -20px;
    padding: 8px 20px;
}

.results label {
    line-height: 24px;
}
.otvet label{

    color: #fff;
}

}
