@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("howtogeek.com") {
  
  /* Backgounds */
  body, #content {
    background-color: #333030 !important;
  }
  
  #bd {
    background-color: #424242 !important;
  }
  
  blockquote, .articleExtras, .middlecol, .article-extras {
    background-color: #212121 !important;
  }
  
    span{
    background-color: #424242 !important;    
  }
  
  /* Search button 'GO' next to search box */
  #searchsubmit {
    background-color: #55A8DF !important;
    height: 44px !important;
  }
  
  /* Shadows */
  #hd, #bd, #ft, blockquote, .articleExtras, .middlecol {
    box-shadow: 3px 3px 8px #222 !important;    
  }
  
  /* Texts */
  .nc4, .nc4l, .nc4r, .icon-magnifying-glass {
    background: none !important;    
  }
  
  h1, h2, h3, h4 {
    color: #EEE !important;
  }
  
  a {
    color: #7E97AC !important;
  }
  a:hover {
    color: #FFF !important;
  }
  
  /* No boarders and white text */
  * {
    border: none !important;
    color: #DDD !important;
  }
}
