}
[is-watch-page] ytd-masthead::before {
  content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  background: var(--yt-swatch-primary);
  opacity: .65;
  z-index: -1;
}
/* Video Settings */
ytd-player, #container.ytd-player {
    display: block;
    width: 100%;
    height: 100%;
}

[is-watch-page] ytd-masthead,
[is-watch-page] #masthead-container.ytd-app::after {
  transform: translateY(-56px);
  transition: transform .1s .5s ease-out;
}
[is-watch-page] #masthead-container:hover ytd-masthead,
[is-watch-page] #masthead-container:hover.ytd-app::after {
  transform: translateY(0px);
}

[is-watch-page] [theater] #player.ytd-watch {
  height: 100vh;
  max-height: 100vh;
}

[is-watch-page] [theater] #player video {
  width: 100%!important;
  height: auto!important;
  max-height: 100vh;
  max-width: 100%;
  left: 0!important;
}

