css:
@-moz-document domain("tvline.com"), domain("polldaddy.com") {
html,
body,
tbody,
thead,
th,
tr,
td,
blockquote,
ul,
h3,
h4,
h5,
h6,
font,
form,
footer {
    color: darkgray !important;
    background: black;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
}
strong {
    color: darkred !important;
}
.site .site-content {
    background: #000000;
    overflow: hidden;
    position: relative;
}
.site .sidebar-wrapper {
    position: relative;
    float: left;
    width: 340px;
    background: #000000;
}
.widget {
    background: #000000;
    padding: 20px 10px;
    margin-bottom: 20px;
}
.widget a {
    color: #0C59BC;
    font-size: 12px;
    line-height: 16px;
}
.AR_2.ob-classic-layout .ob-rec-text {
    color: #A9A9A9;
    font-family: inherit;
    font-size: 14px;
    line-height: 1.25;
}
.widget .border {
    border-top: 1px solid #808285;
    border-bottom: 4px solid #250FA3;
    margin-bottom: 5px;
}
.single article .view-gallery .inline-gallery-nav {
    color: #FFFFFF;
    position: absolute;
    top: 86%;
    left: 72%;
    z-index: 9;
    width: 174px;
    height: 50px;
    background: rgba(0, 174, 239, 0.7);
}
.inline-gallery-image {
    background-color: transparent;
}
h2.post-title {
    color: #176AB4;
}
h1.post-title.entry-title {
    color: #176AB4;
}
h1.site-title, div.pds-box {
    background-color: #000 !important;
}
article footer a {
    color: #808285;
}
.widget ul li {
    overflow: hidden;
    display: block;
    padding: 10px 10px;
    border-top: 1px dotted #000;
}
.module-comments .comment-count {
    width: 100%;
    padding: 10px 0;
    background: #000;
    margin-top: 10px;
    position: relative;
}
#comment-form-guest.comment-form-service.selected {
    background-color: #000;
}
#respond .comment-form-fields div.comment-form-input input {
    background-color: #000 !important;
    color: #CA0505;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
    padding: 1px 2px;
    margin: 0px 0 8px 0;
}
#respond .comment-form-fields div.comment-form-input {
    padding: 1px 2px;
    margin: 0 0 8px 0;
    -moz-box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.1) inset;
    -webkit-box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.1) inset;
    box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.1) inset;
    background: #fefefe;
    background: rgba( 255, 255, 255, 0.9);
    border: 1px solid #cdcdcd;
    -moz-transition-duration: .3s;
    -webkit-transition-duration: .3s;
    width: auto !important;
}
#respond .comment-form-avatar img {
    padding: 1px;
    border: 1px solid #ccc;
    background: #000;
    width: 25px;
    vertical-align: middle;
    margin: 0;
}
.what-to-watch {
    width: 100%;
    background: #631818;
    color: #fff;
    padding: 0 20px;
    font-weight: 700;
}
* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background-color: black;
}
.main-navigation li.special-menu a.menu-link.main-menu-link {
    color: #E70F0F;
}
a:hover,
a:hover *:not(img) {
    color: #FFE900 !important;
}
a:hover,
a:hover *:not(img) {
    color: #FFE900 !important;
}
.widget .todays-hot li:hover div {
    background-color: #000000;
}
article .cat-title-shadow span {
    display: inline-block;
    padding: 8px 20px 4px 5px;
    margin-left: 5px;
    background-color: transparent;
    color: #D71A1A;
}
article .cat-title-shadow .cat-title-wrap:before {
    display: inline-block;
    border-left: 12px solid #000000;
    content: "";
    border-top: 30px solid #fd0;
}
.main-navigation .special-menu a:before {
    display: block;
    border-top: 30px solid #fd0;
    border-right: 10px solid #fd0;
    border-left: 12px solid #000000;
    content: "";
    float: left;
}
.widget .mostcommented li:hover div {
    background-color: #000000;
}
.widget .tvline-voices li:hover div {
    background-color: #880909;
}
.site .paging-navigation .nav-previous a span {
    padding: 0 15px;
    background-color: #00AEEF;
}
.site .paging-navigation .nav-next a span {
    padding: 0 15px;
    background-color: #00AEEF;
}
.widget .mostcommented .comment-count {
    font-size: 12px;
    background: #fd0;
    border-radius: 15px;
    line-height: 21px;
    padding: 1px 5px;
    display: inline-block;
    position: relative;
    color: #D01010 !important;
}
.fa-3x {
    font-size: 3em;
    background-color: rgba(0, 0, 0, 0);
}
.carousel .carousel-content .text {
    background: rgba(8, 8, 8, 0.7) !important;
}
* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background-color: transparent;
}
.site {
    font-family: "Antenna", Arial, Helvetica, sans-serif;
    font-weight: 400;
    width: 1000px;
    margin: 0 auto;
    background: #000000;
}
.single article .post-title:hover {
    color: #fd0;
}
p {
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
    color: #9EA9A9 !important;
}
article .cat-title-shadow span {
    display: inline-block;
    padding: 8px 20px 4px 5px;
    margin-left: 5px;
    background-color: transparent;
    color: #000000;
    font-weight: bold;
}
.ytp-title-link,
.ytp-title-channel-name {
    height: 2.2em;
    max-width: 100%;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    float: left;
    padding-left: 4px;
}
.widget .mostcommented li:hover .counter {
    color: #00AEEF;
}
article a {
    color: #0000EE;
}
.swiftype .search_form_widget.search_form input[type='text'] {
    width: 100%;
    padding: 5px 0;
    text-indent: 30px;
    background-position: 8px 8px;
    background-color: #494545;
}
.site .paging-navigation .nav-previous a:before {
    display: block;
    border-top: 30px solid #000000;
    border-right: 10px solid #00aeef;
    content: "";
    float: left;
}
.site .paging-navigation .nav-next a:after {
    display: block;
    border-top: 30px solid #000000;
    border-left: 10px solid #00aeef;
    content: "";
    float: right;
}
#ad-interruptus-header a {
    color: gold;
    text-decoration: none;
}
.single .share-comment {
    border-top: 1px solid #f3f4f4;
    border-bottom: 1px solid #f3f4f4;
    text-align: center;
    float: none;
    margin: auto !important;
}
p {
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
    color: #9EA9A9 !important;
}

#respond #comment-form-comment,
#comment-form-share-text-padder {
    background: rgba(29, 28, 28, 0.85);
}

#respond #comment-form-comment textarea, #comment-form-share-text-padder textarea {
    color: #777;
    text-shadow: none;
    background-color: #000000 !important;
    font-weight: bold !Important;
}

.post-type-archive-tvline_what_to_watch .shows>li:nth-child(even) .wrapper, .single-tvline_what_to_watch .shows>li:nth-child(even) .wrapper {
    background: #1e1f1f;
}

span {
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    vertical-align: baseline;
    color: white !important;
}

em {
    color: #D01010 !important;
    font-weight: bolder !important;
}

.share-comment span {
    width: 34px;
    height: 34px;
    -webkit-border-radius: 17px;
    -moz-border-radius: 17px;
    border-radius: 17px;
    background: #176ab4;
    line-height: 34px;
    text-align: center;
    margin-top: auto;
    margin-left: 5px;
    display: inline-block;
}

article .comment-add span  {
    background: #2500ff; 
}

article .comment-add .comment-bubble  {
    border-color: transparent transparent transparent rgba(255, 221, 0, 0);
}

#respond .comment-form-fields label {
    color: #fd0;
}

.share-comment .article-comment .comment-limit:hover, .share-comment .article-comment .comment-limit:hover em {
    color: #ec0423 !important;
}

.river-social-btns .share-container ul.share-buttons li a.btn-comment span {
    font-size: .75rem;
    color: #d01010 !important;
    background-color: #ffdd00;
}
}
