css:
@-moz-document domain("gossipcop.com") {
body, #content, .sidebar, .sidebar .sidebar-item, #posts {
    background-color: #000000;
}

#hot-topics, #content-wrapper, .sidebars-container {
    background: #000000;
}

#hot-topics .featured-post-title {
    background-color: #101010;
}

span.featured-post-title, div#posts div:nth-child(n) > div.blurb-text > div > h2 > a, .post h1, .page404 h1, div#posts p.alignleft > a, div#posts p.alignright > a {
    color: #961b1b !important;
}

.post {
    clear: both;
    font-size: 13px;
    margin: 5px 0 0;
    margin-right: 15px;
    padding: 0 30px 25px 30px;
    position: relative;
    background-color: #101010;
    border-style: outset;
    border-width: 15px;
    overflow: hidden;    
    border-color: #5a7694;
}

img {
    border-style: outset;
    border-width: 5px;
    border-color: #5a7694;
}



.featured-post-thumb {
    background: none;
}

#hot-topics .featured-post-title {
    background-color: #101010;
    border-style: outset;
    border-width: 5px;
    width: 239px;
    height: auto;
    padding-left: 10px;
    border-color: #5a7694;
}

.featured-post-thumb img {
    border-style: outset;
    border-width: 5px;
    width: 258px;
    height: auto;
    border-color: #5a7694;
}

.sharethefacts {
    background-color: #101010 !important;
}

.sharethefacts .sharethefacts-header {
    color: #2e87e7;
}

.sharethefacts .sharethefacts-rating img {
    width: 100%;
    padding-bottom: 24px;
    padding-top: 2px;
    padding-right: 0px;
    padding-left: 0px;
    background-color: #101010;
    border: 0px;
}

.sharethefacts .sharethefacts-speaker .sharethefacts-speaker-name {
    color: #2e9fff;
}

.sharethefacts .sharethefacts-speaker .sharethefacts-speaker-title {
    color: #ebeef2;
}

.sharethefacts .sharethefacts-statement {
    color: #2dc5ff;
}

.sharethefacts .sharethefacts-dateline {
    color: #7f919e;
}

div.social-b {
    display: inline-flex !important;
}

.post .blurb-image img {
    width: 433px;
    height: auto;
}

div#posts strong > em {
    color: darkred;
}

div.blurb-text div h2 a {
    background-color: #101010 !important;
}
}
