css:
@namespace "http://www.w3.org/1999/xhtml";
@-moz-document domain("tfile.cc"), domain("tfile.co"), domain("tfile-music.org"), domain("new-tfile.org"), domain("my-tfile.org") {
tr, td {
    background: #24303d none repeat scroll 0 0;
    border: 1px solid #364451;
    color: #a7afb8;
    font-family: inherit;
    font-size: 100%;
    font-style: inherit;
    font-weight: inherit;
    margin: 0;
    padding: 5px;
    vertical-align: baseline;
}
li {
    border-bottom: 1px solid #000;
    border-top: 1px solid #414141;
}
span {
    color: #ccc;
}
h1 {
    color: #fff;
    font-size: 20px;
    font-weight: normal;
    text-align: left;
}
#head {
    background: #000 none repeat scroll 0 0;
    height: 120px;
    min-width: 900px;
    position: relative;
    text-align: center;
}
#top_info {
    background: #818181 none repeat scroll 0 0;
    border: 1px solid #fff;
    font-size: 1.2em;
    height: 29px;
    margin: 0 auto;
    padding-top: 3px;
    text-align: left;
    width: 100%;
}
#top_info .l {
    color: #a5bbdc;
    float: left;
    margin-left: 14px;
}
a {
    color: #ccc;
    text-decoration: none;
}
a:active, a:visited {
    color: #14ad1b;
}
a:hover {
    color: yellow;
    text-decoration: underline;
}
.s_login {
    background: rgba(0, 0, 0, 0) url("http://iconbird.com/png/download.php?id=45684&s=22") no-repeat scroll 0 0;
    border: 0 none;
    cursor: pointer;
    display: block;
    float: right;
    height: 22px;
    margin: 0 0.2em;
    width: 32px;
}
.s_search {
    background: rgba(0, 0, 0, 0) url("http://iconbird.com/png/download.php?id=47413&s=24") no-repeat scroll 0 0;
    display: block;
    float: right;
    height: 23px;
    margin: 0 0.2em;
    width: 27px;
}
#table_reduser {
    background: #000 none repeat scroll 0 0;
    padding-bottom: 3em;
    padding-top: 15px;
}
#pcol {
    border-right: 1px solid #364451;
    width: 325px;
}
h3.opt {
    background: transparent url("http://findicons.com/icon/download/55432/play/128/png") no-repeat scroll 0 0 / contain !important;
    border-bottom: 1px dotted #d4d4d4;
    clear: both;
    color: #7c8c94;
    font-size: 1.2em;
    margin: 0.4em 0;
    padding: 0.4em 0 0.4em 1.5em;
}
a.icon {
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
    color: #fff;
    display: block;
    float: left;
    height: 100px;
    text-align: center;
    text-decoration: none;
    text-shadow: 0 0 0.2em #f87, 0 0 0.2em #f87, 0 0 0.2em #f87;
    text-transform: uppercase;
    width: 105px;
}
a.icon:hover {
    text-decoration: none;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
    text-transform: uppercase;
}
.top-icons-video, .top-icons-music, .top-icons-soft, .top-icons-games, .top-icons-books, .top-icons-other, .top-icons-talks, .top-icons-sport {
    display: block;
    height: 70px;
    margin: 0 auto;
    transform: scale(1.1, 1.1) !important;
    width: 64px;
}
.top-icons-video {
    background: transparent url("http://www.iconsearch.ru/ajax/download.php?icon_id=47370&size=6&format=png") no-repeat scroll 0 0 / contain !important;
}
.top-icons-music {
    background: transparent url("http://www.iconsearch.ru/ajax/download.php?icon_id=16860&size=6&format=png") no-repeat scroll 0 0 / contain !important;
}
.top-icons-soft {
    background: transparent url("http://www.iconsearch.ru/ajax/download.php?icon_id=16846&size=6&format=png") no-repeat scroll 0 0 / contain !important;
}
.top-icons-games {
    background: transparent url("http://www.iconsearch.ru/ajax/download.php?icon_id=28775&size=6&format=png") no-repeat scroll 0 0 / contain !important;
}
.top-icons-books {
    background: transparent url("http://www.iconsearch.ru/ajax/download.php?icon_id=35292&size=6&format=png") no-repeat scroll 0 0 / contain !important;
}
.top-icons-sport {
    background: transparent url("http://www.iconsearch.ru/ajax/download.php?icon_id=40342&size=6&format=png") no-repeat scroll 0 0 / contain !important;
}
.top-icons-other {
    background: transparent url("http://www.iconsearch.ru/ajax/download.php?icon_id=41961&size=6&format=png") no-repeat scroll 0 0 / contain !important;
}
#top-icons li:hover {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
}
ul.slist {
    background: #222933 none repeat scroll 0 0;
    border: 1px solid #364451;
    border-radius: 3px;
    line-height: 1.5em;
    list-style: outside none none;
    margin: 0 0 2em;
    padding: 10px;
}
ul.slist li a {
    color: #fff;
    font-weight: bold;
    padding: 0 8px;
}
ul.slist i {
    color: #7c8c94;
    font-size: 0.92em;
    font-style: normal;
    padding: 10px;
}
h3.opt a {
    color: #fff;
    font-size: 20px;
    padding: 15px;
    text-decoration: none;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
    text-transform: uppercase;
}
h3.opt a:hover {
    color: #fff;
    text-decoration: none;
    text-shadow: 0 0 0.2em #f87, 0 0 0.2em #f87, 0 0 0.2em #f87;
}
div.header span a {
    color: #fff;
    padding: 0 35px;
    text-decoration: none;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
    text-transform: uppercase;
}
#foot_links {
    background: #24303d none repeat scroll 0 0;
    border: 1px solid #313842;
    border-radius: 3px;
    color: #a7afb8;
    padding: 7px 6px 10px 8px;
}
div.header span {
    border: 1px solid #fff;
    box-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
    color: #fff;
    font-size: 22px;
    font-weight: bold;
    padding: 4px 30px;
}
div.header span.movies {
    background: transparent url("http://findicons.com/icon/download/35886/movies/128/png") no-repeat scroll 0 0 / contain !important;
    margin: 0.6em 0;
    padding: 0.7em 0 0.4em 0.9em;
}
div.header span.smovies {
    background: transparent url("http://findicons.com/icon/download/78424/little_tv/128/png") no-repeat scroll 0 0 / contain !important;
    margin: 0.6em 0;
    padding: 0.7em 0 0.4em 0.9em;
}
div.header span.newest {
    background: transparent url("http://findicons.com/icon/download/71463/u_torrent/128/png") no-repeat scroll 0 0 / contain !important;
    margin: 0.6em 0;
    padding: 0.7em 0 0.4em 0.9em;
}
div.header span.music {
    background: transparent url("http://iconbird.com/png/download.php?id=7788&s=512") no-repeat scroll 0 0 / contain !important;
    margin: 0.6em 0;
    padding: 0.7em 0 0.4em 0.9em;
}
div.header span.cartoons {
    background: transparent url("http://iconbird.com/png/download.php?id=44586&s=256") no-repeat scroll 0 0 / contain !important;
    margin: 0.6em 0;
    padding: 0.7em 0 0.4em 0.9em;
}
#cats {
    float: none;
    margin-right: -50px;
}
#cats a {
    color: #fff;
    float: left;
    font-size: 1.2em;
    height: 24px;
    text-align: center;
    text-decoration: none;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
    text-transform: uppercase;
    width: 100px;
}
#cats a:hover {
    color: #fff;
    text-shadow: 0 0 0.2em #f87, 0 0 0.2em #f87, 0 0 0.2em #f87;
}
#foot_links a {
    color: orange;
    text-decoration: none;
    text-transform: uppercase;
}
.narrowPage #foot_links .inner, .narrowPage #top_tools {
    background: #222933 none repeat scroll 0 0;
    border: 1px solid #414141;
    border-radius: 3px;
    text-align: center;
    width: 100%;
}
.narrowPage #top_info {
    width: 100%;
}
#home_c {
    box-shadow: 0 20px 20px rgba(0, 0, 0, 0.1);
    width: 100%;
}
#postList .name {
    color: yellow;
    font-weight: bold;
    text-decoration: underline;
}
#postList tr:nth-child(2n) td {
    background: #24303d none repeat scroll 0 0;
    color: #a5bbdc;
}
#postList td {
    background: #304156 none repeat scroll 0 0;
    margin: 1px;
}
#postList .pT {
    color: #a5bbdc;
    font-size: 14px;
    line-height: 18px;
    padding: 0.5em;
}
#topics tr:hover td {
    background-color: #000;
    transition: all 0.7s ease 0s;
}
#topics td {
    background: #3f546f none repeat scroll 0 0;
    font-size: 0.9em;
    text-align: center;
}
#topics td.t {
    background-color: #24303d;
    text-align: left;
}
#topics td.dl {
    background-color: #304156;
    white-space: nowrap;
    width: 48px;
}
#topics td.st {
    background-color: #24303d;
    color: #ccc;
    width: 64px;
}
#topics td.ms {
    background-color: #304156;
    color: #ccc;
    width: 132px;
}
#topics b.sd {
    color: #14ad1b;
}
#topics b.lc {
    color: #dc143c;
}
#topics td.t a {
    color: #a5bbdc;
    font-family: Raleway,DIN Pro,Verdana,Helvetica,Arial,sans-serif;
    font-size: 1.2em;
    font-weight: 100;
    text-decoration: none;
}
#topics tr:hover td.t a, #topics tr:hover td.dl a {
    color: #ccc;
}
#leftPane {
    background: #000 none repeat scroll 0 0;
    font-size: 0.8em;
    margin-bottom: 30px;
    padding: 9px 0 0;
    vertical-align: top;
    width: 240px;
}
#leftPane ul {
    background: #222933 none repeat scroll 0 0;
    margin: 0 0 1em;
    padding: 1em 0;
}
#leftPane li {
    line-height: 2em;
    list-style: outside none none;
    padding: 0 1em 1em;
}
#leftPane li:hover {
    box-shadow: 0 0 10px 0 rgba(4, 162, 220, 0.5);
    line-height: 2em;
    list-style: outside none none;
    padding: 0 1em 1em;
    transition: all 0.7s ease 0s;
}
#leftPane a {
    color: #ccc;
    font-size: 12px;
    text-decoration: none;
}
#leftPane li.selected a {
    border-bottom: 1px dotted #888;
    color: orange;
    font-size: 17px;
}
#contentPanel #newest i {
    clear: both;
    color: #f0f0f0;
    float: none;
    overflow: hidden;
    text-align: right;
    width: 130px;
}
h1, h2, h3, h4, h5, h6 {
    color: #02b0e4;
    font-weight: normal;
}
.forumIndex .forum-container .subforums a {
    color: #a5bbdc;
    font-size: 12px;
    font-weight: normal;
    margin-right: 20px;
    white-space: nowrap;
}
.forumIndex .forum-container {
    border-bottom: 1px solid #364451;
    margin-bottom: 5px;
    padding: 4px 5px;
}
.forumIndex .forum-container a {
    color: #fff;
    font-size: 14px;
    font-weight: bold;
}
.forumIndex .forum-container:hover {
    background: #304156 none repeat scroll 0 0;
    transition: all 1s ease 0s;
}
.forumIndex .forum-container:hover .subforums a {
    color: #ccc;
}
.fa.D, .rr.D {
    color: #dc143c;
    padding-right: 5px;
}
.fa.U, .rr.U {
    color: #14ad1b;
    padding-right: 5px;
}
#contentPane {
    background-color: #161d25;
    margin-left: 240px;
    padding: 0 0 0 5px;
    vertical-align: top;
}
.spoiler div {
    background: #222933 none repeat scroll 0 0;
    border: 1px solid #364451;
    color: #999;
    display: none;
    padding: 4px;
}
.spoiler {
    background: #ebeef7 none repeat scroll 0 0;
    border: 1px solid #000;
    clear: both;
    margin: 5px;
    max-width: 100%;
    min-height: 24px;
    overflow: hidden;
}
table {
    border-collapse: collapse;
    border-spacing: 0;
}
#leftPane h2 {
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    -moz-border-right-colors: none;
    -moz-border-top-colors: none;
    background: #9b59b6 none repeat scroll 0 0;
    border-color: #37829c #37829c #37829c -moz-use-text-color;
    border-image: none;
    border-style: solid solid solid none;
    border-width: 1px 1px 1px medium;
    font-size: 18px;
    font-weight: 300;
    padding: 6px 0 6px 16px;
}
ul li {
    border-bottom: 1px solid #000;
    border-top: 1px solid #333;
}
ul li:first-child {
    border-top: medium none;
}
ul li:last-child {
    border-bottom: medium none;
}
ul li a {
    color: #fff;
    display: block;
    padding: 5px;
    text-decoration: none;
}
ul li:hover {
    background: #304156 none repeat scroll 0 0;
    transition: all 0.5s ease 0s;
}
.tab-content.torrent {
    background: #000 none repeat scroll 0 0;
    margin-bottom: 1px;
    padding: 15px;
}
.info-torrent {
    background: #24303d none repeat scroll 0 0;
    float: right;
    padding: 10px;
}
#topicStats .seeders {
    color: #14ad1b;
    font-weight: bold;
}
#topicStats .leechers {
    color: #dc143c;
    font-weight: bold;
}
#topicStats {
    background: #304156 none repeat scroll 0 0;
    border: 1px solid #89bcf3;
    margin: 0.5em 0;
    padding: 1px;
    text-align: center;
}
.gen, .genmed, .gensmall {
    color: #ccc;
}
a.gen, a.genmed {
    color: #888;
    text-decoration: underline;
}
.seed, .seedmed, .seedsmall {
    color: #14ad1b;
}
.leech, .leechmed, .leechsmall {
    color: #dc143c;
}
td.row1 {
    background-color: #304156;
}
td.row2 {
    background-color: #304156;
}
.bodyline {
    background-color: #24303d;
    border: 1px solid #ccc;
}
td.cat, td.catHead, td.catSides, td.catLeft, td.catRight, td.catBottom {
    background-color: #24303d;
    background-image: none;
    border: medium solid #fff;
    height: 26px;
    padding-left: 12px;
}
.relatedTorrents li {
    background: #1f2831 none repeat scroll 0 0;
    border-bottom: 1px solid #e0e0e0;
    color: #404040;
    padding: 0.5em;
}
#topics td a {
    color: #fff;
    text-decoration: underline;
}
#topics tr:hover td a {
    color: #ccc;
}
#topics.search th {
    background: #7551b1 none repeat scroll 0 0;
    color: #ccc;
}
.advancedSearch .hint {
    background: #3f546f none repeat scroll 0 0;
}
.searchTop {
    background: #3f546f none repeat scroll 0 0;
    border-bottom: 1px solid #999;
}
#topics td.ps {
    background-color: #3c444c;
    width: 132px;
}
#topics td.topicSeparator {
    background: #7551b1 none repeat scroll 0 0;
    color: #ccc;
}
a, #postList .tab-content .btn, a img, #top-icons li, ul li, div.header, h3.opt, a.icon, a.icon i {
    transform: scale(1);
    transition: all 0.3s ease 0s;
}
#postList .tab-content .btn:hover, a img:hover, #top-icons li:hover, h3.opt:hover, a.icon:hover, i:hover {
    transform: scale(1.13);
}
div.header:hover {
    transform: scale(1.08);
}
a:hover {
    transform: scale(1.02);
}
.forumIndex .sm-cat {
    background: #9b59b6 none repeat scroll 0 0;
    border: 1px solid #999;
}
}
