css:
@-moz-document url-prefix("http://stackoverflow.com"), url-prefix("https://stackoverflow.com"), url-prefix("http://superuser.com") {

    /**
     * General.
     */
    body {
        background-color: #888888 !important;
    }

    /* Tags. */
    .post-tag {
        border-bottom-color : rgb(179, 206, 225) !important;
        border-right-color  : rgb(179, 206, 225) !important;
    }
    
    /* Buttons. */
    input[type=submit], input[type=button], .button, a.button:link, a.button:visited, .btn {
        background-color : #444444 !important;
        border-color     : #222222 !important;
    }

    input[type=text], textarea {
        background-color : #111 !important;
        color     : #bbb !important;
    }
    
    #tabs a {
        background: grey !important;
        color: #AAA !important;
    }
    
    .owner {
        background-color: #B0BAC1;
    }

    
    /**
     * Question list.
     */

    /* Title links. */
    h3 > a {
        font-family : 'Trebuchet MS', 'Liberation Sans', 'DejaVu Sans', sans-serif !important;
        font-size   : 17px !important;
        font-weight : bold !important;
    }
    
    a {
        color: #086CBC !important;
    }
    
    .favicon {
        opacity: 0.7 !important;
    }
    
    #content {
        background-color: #928E8A !important;
    }
    
    #sidebar .module.community-bulletin {
        background-color: #A09480 !important;
    }
    div#hireme > ul {
        background-color: #3A3B3C !important;
    }

    /* Votes, answers, views. */
    div.cp {
        font-family : Arial, 'Liberation Sans', 'DejaVu Sans', sans-serif !important;
    }

    .votes, .status, .views {
        font-size   : 13px !important;
        font-weight : normal !important;
    }
    .answer-votes {
        background: #BBB !important;
    }
    .answered {
        background-color: #B39970 !important;
    }
    .answer-votes.answered-accepted {
        color: #10B040 !important;
    }
    
    .post-tag {
        background: #789 !important;
        color: #567 !important;
        border-color: #678 !important;
    }
    .post-menu > a {
        color: #707070;
    }
    .started, .comment-date > a {
        color: #707070;
    }

    div.cp > .status {
        width : 48px !important;
    }

    div.cp .mini-counts {
        font-size   : 20px !important;
        font-weight : normal !important;
    }
    
    .cool, .mini-counts.cool {
        color: #AAA !important;
    }
    .warm, .mini-counts.warm {
	      color: #Ab7048 !important;
    }
    .hot, .mini-counts.hot {
	      color: #ffc761 !important;
    }
    .supernova, .mini-counts.supernova {
        font-weight: bold !important;
	      color: #eff4ff !important;
    }
    .page-numbers {
        color: #777 !important;
    }
    .started-link {
        color: #a8a8a8 !important;
    }


    /* Questions with favorite tags. */
    .tagged-interesting {
        background-color: #E0B040 !important;
    }
    
    .bulletin-title {
        color: #60708B !important;
    }

    /**
     * Question page.
     */

    /* Share, edit, close, flag link separator. */
    .lsep {
        color      : #cccccc !important;
        font-size  : 110% !important;
        visibility : visible !important;
    }

    /* Question header. Duh. */
    #question-header {
        padding : 0 !important;
    }

    #question-header h1 {
        margin-bottom : 7px !important;
    }

    /* Code.
    .post-text code {
        font-size : 14px !important;
    } */
    pre, code {
        background: #BBB !important;
    }
    
    blockquote {
        background-color: #F5E3A9 !important;
    }
    
    kbd {
        background-color: #333 !important;
        box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.2), 0px 0px 0px 2px #555 inset !important;
        color: #999 !important;
    }
}
