css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("askubuntu.com") {

    body {
        background-color: #3D182F !important;
        background-image: none !important;
    }

    .post-tag {
        border-bottom-color : #000 !important;
        border-right-color  : #000 !important;
    }
    
    .wmd-button {
        background-color: #212 !important;
        opacity: 0.2 !important;
    }
    .wmd-preview {
        border: 2px dotted #620 !important;
    }
    input[type=submit], input[type=button], .button, a.button:link, a.button:visited, .btn {
        background-color : #444444 !important;
        border-color     : #222222 !important;
    }
    input[type=checkbox] {
        border: none !important;
    }
    .vote {
        opacity: 0.3 !important;
    }
    .vote span {
        color: #fff;
    }

    input[type=text], textarea {
        background-color : #080003 !important;
        color     : #bbb !important;
    }
    
    #tabs a {
        background: #432 !important;
        color: #AAA !important;
    }

    
    h3 > a {
        font-family : 'Trebuchet MS', 'Liberation Sans', 'DejaVu Sans', sans-serif !important;
        font-size   : 17px !important;
        font-weight : bold !important;
    }
    
    a {
        color: #E51 !important;
    }
    
    .favicon, .user-gravatar32 {
        opacity: 0.7 !important;
    }
    
    #content {
        background-color: #361122 !important;
        color: #C8C0B0 !important;
    }
    .comments {
        color: #A98 !important;
    }
    
    .module, .owner, pre, code {
        background-color: #410 !important;
    }
    div#hireme > ul {
        background-color: #3A3B3C !important;
    }

    div.cp {
        font-family : Arial, 'Liberation Sans', 'DejaVu Sans', sans-serif !important;
    }

    .votes, .status, .views {
        font-size   : 13px !important;
        font-weight : normal !important;
    }
    .answer-votes, .answered strong {
        background: #906863 !important;
        color: #005 !important;
    }
    .answered-accepted strong {
        background: #406833 !important;
        color: #005 !important;
    }
    .unanswered strong {
        background: #CC4410 !important;
        color: #FFF !important;
    }
    
    .post-tag {
        background: #A30 !important;
        color: #000 !important;
        border-color: #678 !important;
    }
    .post-menu > a {
        color: #707070;
    }
    .started, .comment-date > a {
        color: #707070;
    }

    div.cp > .status {
        width : 48px !important;
    }

    div.cp .mini-counts {
        font-size   : 20px !important;
        font-weight : normal !important;
    }
    
    .cool, .mini-counts.cool {
        color: #345 !important;
    }
    .warm, .mini-counts.warm {
	      color: #720 !important;
    }
    .hot, .mini-counts.hot {
	      color: #ff7061 !important;
    }
    .supernova, .mini-counts.supernova {
        font-weight: bold !important;
	      color: #eff4ff !important;
    }
    .badge3 {
        opacity: 0.6 !important;
    }
    .relativetime, .question-hyperlink {
        text-shadow: 0px 1px 0px #000 !important;
    }
    .page-numbers {
        color: #777 !important;
    }
    .started-link {
        color: #a8a8a8 !important;
    }


    .tagged-interesting {
        background-color: #E0B040 !important;
    }
    
    .new-post-activity {
        background-color: #432 !important;
    }

    .lsep {
        color      : #cccccc !important;
        font-size  : 110% !important;
        visibility : visible !important;
    }
    
    textarea {
        border: 1px solid #620 !important;
    }
    .grippie {
        background-color: #321 !important;
        border-color: #620 !important;
    }
    
    .container {
        background-color: #000 !important;
    }

    #question-header {
        padding : 0 !important;
    }

    #question-header h1 {
        margin-bottom : 7px !important;
    }
    .question-status {
        background-color: #000 !important;
    }
    .user-show-new .site-hyperlink, .user-show-new .question-hyperlink, .user-show-new .answer-hyperlink {
        color: #987 !important;
    }
    
    .subheader {
        background-color: #280E18 !important;
    }

    pre, code {
        color: #BA9 !important;
        background-color: #481211 !important;
    }
    .pln { color: #C8C0B0 !important; }
    .str { color: #A00 !important; }
    .lit { color: #4C0 !important; }
    .kwd { color: #0000FF !important; }
    .pun { color: #999999 !important; }
    
    blockquote {
        background-color: #4A5538 !important;
    }
    
    kbd {
        background-color: #333 !important;
        box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.2), 0px 0px 0px 2px #555 inset !important;
        color: #999 !important;
    }
}
