/* Icon Locked */
#vehicle-list > div.box.vehicle-stats-box > div.box-content > ul.vehicle-stats-list.stat-list-col.col6 > li.locked:after,
#weapon-list > div.box.weapon-stats-box > div.box-content > ul.weapon-stats-list > li.locked:after,
#stats-kits > div.row.profile-statistics-container > div.span8 > div.box > div.box-content > ul.kits-list > li.kit-item.locked:after {
    content: '';
    background: url('http://image.noelshack.com/fichiers/2016/26/1467049566-lock.png') center center no-repeat!important;
    background-size: 14px!important;
    position: absolute!important;
    display: block!important;
    height: 48px!important;
    top: calc(0% - 10px)!important;
    right: calc(0% - 0px)!important;
    width: 28px!important;
    -webkit-filter: invert(35%);
    filter: invert(35%);
}

/* Battle report */
table.scoreboard tbody > tr.active-player:not(.active) td:not(:first-child) {
    color: #000;
    background-color: rgba(224, 154, 0, 0.8)!important;
}
table.scoreboard tbody > tr.active-squad:not(.active) td:not(:first-child) {
    color: #fff;
    background-color: rgba(203, 255, 91, .2)!important;
}
table.scoreboard tbody > tr.active-squad:not(.active) td:first-child {
    color: #fff;
    background-color: rgba(203, 255, 91, .4)!important;
}

/* Award */
li.active > div.award-ribbon > span.times-taken,
li.active > div.award-medal > span.times-taken {
    color: #000;
}

/* Avatar */
.avatar.xlarge.online {
    box-shadow: 6px 0 #78c753 inset!important;
}
.avatar.large.online,
.avatar.online {
    box-shadow: 3px 0 #78c753 inset!important;
}
.avatar.xlarge.playing {
    box-shadow: 6px 0 #60c0f6 inset!important;
}
.avatar.large.playing,
.avatar.playing {
    box-shadow: 3px 0 #60c0f6 inset!important;
}
}
