css:
@-moz-document domain("www.gw2wvw.net") {

/**
 * Desktop layout; two-column front page
 */

body.html {
  background: #080808 !important;
  margin: 0 !important; 
  padding: 0 !important;
}

#main, body.overlay, #page, #header, #page-wrapper, #content, #footer-wrapper, #page-title,
#menubar, #menubar .sf-menu > li > a, #navigation, #preview #preview-navigation, .column-block,
.profile .user-picture, table.views-view-grid tr,
h1, h2, h3, h4, h5, h6, h7, h8, h9 {
  background: transparent !important;
  background-color: transparent !important;
  background-image: none !important;
}

h1, h2, h3, h4, h5, h6, h7, h8, h9 {
  margin-left: 0 !important;
  margin-right: 0 !important;
  padding-left: 0 !important;
  padding-right: 0 !important;
}

#logo, #header-searchbox, #menubar {
  margin: 0 !important; 
  padding: 0 !important; 
}
#header-searchbox .form-item-search-block-form input
{
  width: auto !important;
}

#header, #menubar {
  width: auto !important;
}

#page {
  width: auto !important;
  margin: 0 !important;
  padding: 0 !important;
}

#page-wrapper {
  width: auto !important;
  margin: 0 !important;
  padding: 20px !important;
}

#main-wrapper {
  width: auto !important;
  margin: 0 !important;
  padding: 0 !important;
}

#main {
  margin: 10px 0 0 0 !important;
  padding: 0 !important;
}

.center-wrapper > .panel-panel {
  width: 48% !important;
}

.center-wrapper > .panel-panel.panel-col-last {
  float: right !important;
}

.node h2 {
  border: none !important;
  margin: 0 0 1em 0 !important;
  padding: 0 !important;
}

.node,
.panel-pane,
.pane-content {
  background: none !important;
  border: none !important;
}

.messages.status {
  background: #511 !important;
  border-color: #922 !important;
  color: #ccc !important;
}

.pane-front-page .panel-separator {
  margin: 0 0 2em 0 !important;
}

.pane-front-page .view-header {
  margin: 1em 0 !important;
}

.pane-front-page .view-content .node {
  margin: 1em 0 3em 0 !important;
}

.node ul.rate-fivestar-processed {
  padding-left: 0 !important;
}

.node .field-type-taxonomy-term-reference .field-items .field-item:first-child {
  margin-left: 0 !important;
}

.view-header a:not(:hover),
.tabs li a:not(:hover),
.af-button-large:not(:hover),
ul.links a:not(:hover),
ul.pager a:not(:hover) {
  background: none !important;
}

.action-links
{
  margin-left: 0 !important;
  list-style: none !important;
}
.action-links > li
{
  display: block !important;
}

.tabs li a:hover {
  background: #555 !important;
  color: #ddd !important;
}

.tabs li.active a:hover {
  background: #333 !important;
  color: #bbb !important;
}

table.sticky-header {
  background: #222 !important;
}

.view-content .views-row,
.view-content .views-table,
.view-content .views-table td,
.view-content .views-table th,
.view-content .views-table tr {
  background: none !important;
}

.view-content .views-row .content {
  clear: both !important;
}

.view-content .views-table{
  border-spacing: 0 !important;
}

.view-content .views-table th,
.view-content .views-table tr:not(.views-row-last):not(:last-child) {
  border-bottom: 1px solid #555 !important;
}

.column-block table tr th, .sidebar .block table tr th,
.column-block table tr td, .sidebar .block table tr td
{
  border-bottom: 1px solid #555 !important;
  border-color: #555 !important;
}

.column-block table tr:last-child td, .sidebar .block table tr:last-child td
{
  border-bottom: none !important;
}

.view-userposts li.views-row {
  background: none !important;
  list-style: none !important;
  margin: 1em 0 2em 0 !important;
}

.profile h3
{
  border: none !important;
}



/**
 * Superfish Menubar
 */

#menubar * {
  border: none !important;
}

#menubar .sf-menu li {
  margin: 0 !important;
}

#menubar .sf-menu li > a {
  text-indent: -1em !important;
  padding-left: 2em !important;
}

#menubar .sf-menu li ol  {
  padding-left: .5em !important;
}

#menubar .sf-menu > li {
  margin-bottom: 5px !important;
}

#menubar .sf-menu > li > a.sf-with-ul {
  padding-right: 25px !important;
}


/**
 * Forms
 */

form fieldset {
  border: none !important;
}

input, select {
  -webkit-filter: invert(100%) grayscale(100%) !important;
  filter: invert(100%) grayscale(100%) !important;
}

.filter-wrapper {
  background-color: transparent !important;
}

label {
  display: inline-block !important;
}

label[for*='author']:after {
  content: ": " !important;
}

.form-item-search-block-form .form-text {
  background: transparent url(http://www.gw2wvw.net/sites/all/themes/mayo/images/xsearch.png.pagespeed.ic.SZj-eBzZGe.png) no-repeat 2px center !important;
  padding-left: 30px !important;
}


/**
 * Avatars
 */

.user-picture > a > img,
.author-pane-general > .picture > img {
  background: #000 !important;
  border: 2px solid #000 !important; 
  border-radius: 10px !important; 
  padding: 0 !important; 
  margin: 2px 10px !important; 
}


/**
 * Usernames
 */

.author-name {
  width: 130px !important;
}
.username {
  line-height: 125% !important;
  word-break: break-all !important;
  word-break: break-word !important;
  word-wrap: break-word !important;
  -moz-hyphens: auto !important;
  -webkit-hyphens: auto !important;
  hyphens: auto !important;
}


/**
 * Front page
 */

body.front .node,
body.front .panel-pane,
body.front .pane-content,
body.front .views-row,
body.front table,
body.front table td {
  background: none !important;
  border-color: transparent !important;
}

/**
 * Front page videos
 */

body.front table.cols-2 {
  border-collapse: separate !important;
  border-spacing: 10px !important;
}

body.front table.cols-2 td {
  position: relative !important;
  background: #000 !important;
  -webkit-border-radius: 15px !important;
  -moz-border-radius: 15px !important;
  border-radius: 15px !important;
  overflow-y: hidden !important;
  width: 50% !important;
  vertical-align: bottom !important;
}

body.front table.cols-2 td .views-field-title {
  position: absolute !important;
  z-index: 2 !important;
  top: 0 !important;
  left: 0 !important;
  right: 0 !important;
  background: linear-gradient(to bottom, rgba(0,0,0,1) 0%, rgba(0,0,0,0.9) 30%, rgba(0,0,0,0.7) 60%, rgba(0,0,0,0) 100%) !important;
  height: 100% !important;
  padding: 0 !important;
}

body.front table.cols-2 td:not(:hover) .views-field-title {
  visibility: hidden !important;
}

body.front table.cols-2 td .views-field-title a {
  display: block !important;
  height: 100% !important;
  padding: .5em .5em 1.5em .5em !important;
  margin: 0 !important;
  text-decoration: none !important;
}

body.front table.cols-2 td .views-field-name,
body.front table.cols-2 td .views-field-term-node-tid {
  position: relative !important;
  z-index: 3 !important;
}

body.front .views-field-field-youtube-video a {
  /**
   * force aspect ratio on youtube thumbnails
   */
  display: block !important;
  width: 100% !important;
  position: relative !important;
  height: 0 !important;
  padding: 56.25% 0 0 !important;
  margin: 0 0 1em 0 !important;
  overflow: hidden !important;
}

body.front .views-field-field-youtube-video img {
  position: absolute !important;
  display: block !important;
  max-width: 100% !important;
  max-height: 100% !important;
  left: 0 !important;
  right: 0 !important;
  top: 0 !important;
  bottom: 0 !important;
  margin: auto !important;
}

body.front table.cols-2 td {
  vertical-align: top !important;
}

body.front table.cols-2 td .views-field-term-node-tid
{
  display: none !important;
}


/**
 * Forum tables
 */

.forum-table-wrap,
.forum-table tr,
.forum-table td,
.privatemsg-list,
.privatemsg-list tr,
.privatemsg-list td
{
  background: none !important;
}

.forum-table tr:not(.views-row-last):not(:last-child),
.privatemsg-list tr:not(.views-row-last):not(:last-child)
{
  border-bottom: 1px solid #222 !important;
}

.forum-table tr:hover,
.privatemsg-list tr:hover
{
  background: #222 !important;
}

.forum-table-topics td.views-field-last-updated {
  width: auto !important;
}

.privatemsg-list td,
.views-table td,
.forum-table td
{
  vertical-align: top !important;
}

/**
 * Forum posts
 */

.quote-msg, .quote-msg blockquote {
  background: none !important;
  border: 0 !important;
  border-left: 1px solid #555 !important;
  color: rgb(102, 102, 102) !important;
  font-size: 11pt !important;
  padding: 5px 0 5px 20px !important;
  margin: 1.5em 0 !important;
}

.quote-author {
  display: block !important;
  margin: 0 !important;
  padding: 0 !important;
}

.author-pane-line {
  font-size: .9em !important;
}

.author-pane-line.author-name {
  font-size: 1.5em !important;
}

.author-signature, .post-edited {
  border: none !important; 
  color: #666 !important;
  font-size: .85em !important; 
  margin: 0 0 0 30px !important; 
  padding: 0 !important;
}

.author-signature > blockquote {
  border: none !important; 
  margin: 0 !important; 
  padding: 10px 0 !important;
}

.forum-post {
  background: none !important;
  margin: 0 0 30px 0 !important;
  padding: 10px 20px !important;
}

.forum-post-panel-main {
  border: 0 !important;
}

.forum-post-content {
  padding: .2em 0 1em 2em !important;
}

.forum-post-content,
.author-signature,
.privatemsg-list-subject
{
  /**
   * Force line wrap
   */
  word-break: break-all !important;
  word-break: break-word !important;
  -moz-hyphens: auto !important;
  -webkit-hyphens: auto !important;
  hyphens: auto !important;
}

.forum-post-content p {
  margin: .75em 0 !important;
}

.forum-post-content > .field-type-text-with-summary {
  clear: none !important;
}

.forum-post-footer {
  border: 0 !important;
  background: none !important;
}

.forum-post-info {
  padding: .3em 0 !important;
}

.forum-post-title {
  font-style: italic !important;
  font-size: .9em !important;
  padding: 0 2em !important;
  color: #777 !important;
}

.forum-post-title:before {
  content: "Opening Post:" !important;
  font-style: italic !important;
  font-size: .9em !important;
  padding-right: 1em !important;
}

.forum-jump-links {
  display: none !important;
}

.forum-post-footer ul.links,
.forum-post-footer ul.links > li {
  padding-top: 0 !important;
  padding-right: 0 !important;
  padding-bottom: 0 !important;
  padding-left: 0 !important;
  margin-top: 0 !important;
  margin-right: 0 !important;
  margin-bottom: 0 !important;
  margin-left: 0 !important;
}

.forum-post-footer ul.links > li > a {
  padding-top: .3em !important;
  padding-right: .3em !important;
  padding-bottom: .3em !important;
  padding-left: .3em !important;
  margin-top: 0 !important;
  margin-right: 0 !important;
  margin-bottom: 0 !important;
  margin-left: .3em !important;
}

.forum-post-footer ul.links > li.last > a {
  margin-right: -.3em !important;
}

.forum-post-number {
  margin-right: 0 !important;
}

.privatemsg-message-participants
{
  border: none !important;
  margin-top: 1em !important;
}
.privatemsg-message-information
{
  border-bottom: 1px solid #555 !important;
}


/**
 * Polls
 */

.poll > .text, .poll > .total {
  clear: both !important;
  margin: 1em 0 !important;
}

.poll > .bar {
  background: none !important;
  margin: 0 !important;
}
.poll > .bar > .foreground {
  background: #922 !important;
}

.poll > .percent {
  float: right !important;
  margin-top: -1.75em !important;
}

.poll .vote-form,
.poll .vote-form .choices {
  margin: 0 !important;
  text-align: left !important;
}


/**
 * Lightbox
 */

#outerImageContainer, #imageDataContainer, #bottomNavClose {
  background-color: #000 !important;
}

/**
 * ckEditor
 */

div.textarea-processed {
  -webkit-filter: invert(90%) brightness(0.8) grayscale(100%) !important;
  filter: invert(90%) brightness(0.8) grayscale(100%) !important;
}


/**
 * Tablet; medium resolution layout
 */

@media only screen and (max-width: 900px) {
    
    div.panel-2col-stacked div.panel-panel,
    .center-wrapper > div.panel-panel.panel-col-first,
    .center-wrapper > div.panel-panel.panel-col-last {
      clear: both !important;
      float: none !important;
      width: auto !important;
    }

    #page-wrapper {
      max-width: none !important;
      width: auto !important;
      margin: 0 !important;
      padding: 2% 0 !important;
      position: absolute !important;
      left: 2% !important;
      right: 5% !important;
    }

    
    
}


/**
 * Mobile; small resolution layout
 */

@media only screen and (max-width: 500px) {
    
    /**
     * Screen too narrow; hide these
     */
    th.forum-icon,
    th.privatemsg-header-lastupdated,
    th.views-field-topic-icon,
    th.views-field-comment-count,
    th.views-field-totalcount,
    th.views-field-name,
    th.views-field-term-node-tid,
    th.forum-topics,
    th.forum-posts,
    td.forum-list-icon,
    td.privatemsg-list-date,
    td.views-field-topic-icon,
    td.views-field-comment-count,
    td.views-field-totalcount,
    td.views-field-name,
    td.views-field-term-node-tid,
    td.forum-number-topics,
    td.forum-number-posts,
    div.forum-list-icon-legend,
    div.forum-topic-legend
    {
      display: none !important;
    }


    #header
    {
      height: 120px !important;
    }
    #header-logo
    {
      clear: both !important;
    }
    #logo img[alt="Home"]
    {
      width: 100% !important;
    }
    #header-searchbox
    {
      float: left !important;
    }

    body.front table.cols-2 td {
      display: block !important;
      clear: none !important;
      float: none !important;
      text-align: center !important;
      width: auto !important;
      margin: 0.5em auto !important;
      background: transparent !important;
    }
    
    .forum-table tr th.forum-name,
    .forum-table tr th.views-field-title,
    .forum-table tr td.forum-details,
    .forum-table tr td.views-field-title
    {
      width: 60% !important;
    }
    .forum-table tr th.forum-last-post,
    .forum-table tr th.views-field-last-updated,
    .forum-table tr td.forum-last-reply,
    .forum-table tr td.views-field-last-updated
    {
      width: 40% !important;
    }

    .sticky-header,
    .privatemsg-list
    {
      width: 100% !important;
    }
    .privatemsg-list tr th.privatemsg-header-subject,
    .privatemsg-list tr td.privatemsg-list-subject
    {
      max-width: 160px !important;
      min-width: 0 !important;
    }
    .privatemsg-list tr th.privatemsg-header-participants,
    .privatemsg-list tr td.privatemsg-list-participants
    {
      max-width: 100px !important;
      min-width: 0 !important;
    }
    .privatemsg-message-column
    {
      width: 70% !important;
    }


    .forum-table-wrap,
    .forum-post
    {
      margin-left: 0 !important;
      margin-right: 0 !important;
      padding-left: 0 !important;
      padding-right: 0 !important;
    }
    .forum-post-panel-sub .author-pane,
    .forum-post-content
    {
      margin-left: 0 !important;
      padding-left: 1em !important;
    }

    /**
     * Quote-wall fix:
     * Scrollable quotes
     */
    .forum-post-content .field-item > blockquote
    {
      max-height: 15em !important;
      overflow-y: auto !important;
    }
    .quote-msg,
    .quote-msg blockquote
    {
      padding: 0 !important;
      margin: 0 !important;
    }
    .quote-msg
    {
      border-left: 1px solid #555 !important;
      margin-left: 0 !important;
      padding-left: 1em !important;
    }
    .quote-msg blockquote
    {
      border-left: none !important;
      border-bottom: 1px solid #555 !important;
      margin-left: 0 !important;
      padding-left: 0 !important;
    }
    .quote-msg p
    {
      padding: 0 !important;
      margin: 0 !important;
    }

    
    .forum-tools
    {
      float: none !important;
    }

}

}
