css:
@-moz-document url-prefix("http://hexui.com/") {
/* Replaces background with my dark mode Hexui background */

body {
    background-image: url('http://i.imgur.com/TYvPTDv.png') !important;
    background-color: #0C0C0C;
    color: #121212 !important;
}
body.custom-background {
    background-image: url('http://i.imgur.com/TYvPTDv.png') !important;
    background-color: #0C0C0C;
    color: #121212 !important;
}
/* Replaced forum/support/home/download buttons below the logo AND the logo itself with my revised dark mode icons */

img[src*="http://hexui.com/wp-content/uploads/2014/05/logo65.png"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 356px !important;
    padding-top: 100px !important;
    background: url(http://i.imgur.com/WOfwOVX.png) no-repeat !important;
}
div[style*="background-image: url('http://hexui.com/myicons/home4.png'); background-repeat: no-repeat; display: inline; margin: 0; padding: 0 0 0 20px;"] {
    height: 0 !important;
    width: 0 !important;
    padding: 0 0 0 20px;
    background: url(http://i.imgur.com/8Yr8tLB.png) no-repeat !important;
}
div[style*="background-image: url('http://hexui.com/myicons/download4.png'); background-repeat: no-repeat; display: inline; margin: 0; padding: 0 0 0 20px;"] {
    height: 0 !important;
    width: 0 !important;
    padding: 0 0 0 20px;
    background: url(http://i.imgur.com/HiIK38V.png) no-repeat !important;
}
div[style*="background-image: url('http://hexui.com/myicons/forum4.png'); background-repeat: no-repeat; display: inline; margin: 0; padding: 0 0 0 20px;"] {
    height: 0 !important;
    width: 0 !important;
    padding: 0 0 0 20px;
    background: url(http://i.imgur.com/sHaH3JE.png) no-repeat !important;
}
div[style*="background-image: url('http://hexui.com/myicons/support4.png'); background-repeat: no-repeat; display: inline; margin: 0; padding: 0 0 0 20px;"] {
    height: 0 !important;
    width: 0 !important;
    padding: 0 0 0 20px;
    background: url(http://i.imgur.com/9Sw4vaa.png) no-repeat !important;
}
/* Reworking review stars for dark mode */

.inline_rating {
    height: 18px !important;
    background-color: #f2f2f2;
}
strong[style*="float: left; padding-right: 10px;"] {
    height: 18px !important;
    color: #0d0d0d;
    background-color: #f2f2f2;
}
img[src*="images/star.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 16px !important;
    padding-top: 16px !important;
    background: url(http://www.cloudynights.com/public/style_images/master/star_off.png) no-repeat !important;
}
/* Reworking new/not new post icons for dark mode */

img[src*="images/hexuiimg/off.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 30px !important;
    padding-top: 30px !important;
    background: url(http://i.imgur.com/7HXTXnl.png) no-repeat !important;
}
img[src*="images/hexuiimg/on.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 30px !important;
    padding-top: 30px !important;
    background: url(http://i.imgur.com/kgLagdV.png) no-repeat !important;
}
/* Reworks the product page */

#wrapper {
    background-color: #1a1a1a;
}
center {
    background-color: #333333;
    color: #f2f2f2;
}

/* Shoutbox mods */
#shout_data {
    background-color: #333333;
    color: #f2f2f2;
}


/* Nicer scrollbar : Credit to some other guy, Freddie Chessell , not mine... */
::-webkit-scrollbar-track-piece {
    background-color: #000;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0);
    border-radius: 0px;
    border: none;
}
::-webkit-scrollbar {
    width: 5px;
    height: 3px;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0);
    border-radius: 0px;
    border: none;
}
::-webkit-scrollbar-thumb {
    background-color: #fff;
    border-radius: 0px;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0);
    border: none;
}


button,
input,
select,
textarea {
    background-color: #333333;
    color: #f2f2f2;
    border-color: #0C0C0C;
    
}
.tborder {
    background: #585858;
}
textarea {
    background: #525252;
    border: 1px solid #4E4E4E;
}
.trow_sep {
    background: #4D4B4B !important;
    color: #FFF !important;
}
a {
    color: #D8D5D5;
}
.menu li li {
    background: #121212 !important;
    background-image: none !important;
    border: 1px solid #636363 !important;
    border-top: 0 !important;
    color: #FFF !important;
}
.menu li li a {
    background: #121212
}
.thead {
    border: none;
}
.myboardwarning {
    color: #121212 !important;
    border: none !important;
}
.trow1 {
    background: #121212 !important;
    border: 1px solid #4D4B4B !important;
}
.trow2 {
    background: #121212 !important;
    border: 1px solid #4D4B4B !important;
}
.codeblock {
    background: #121212 !important;
}
.pm_alert {
    background: #C3BA0A !important;
    border: 1px solid #FFF !important;
    color: #121212 !important;
}
blockquote {
    background: #121212 !important;
    border: 2px solid #FFF !important;
}
.smalltext div {
    color: #383838 !important;
}
.headersmallbar {
    background: #121212 !important;
    border-bottom: 4px solid #121212 !important;
}
.menu {
    background-color: #585858;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#121212), to(#121212)) !important;
}
a:link {
    color: #FFFFFF !important;
}
a:visited {
    color: #FFF !important;
}
#panel {
    background: #121212 !important;
    color: #FFFFFF !important;
    border: none !important;
    font-size: 14px !important;
}
.myboardmessage {
    background: #277623 !important;
    border: none !important;
    color: #FFF !important
}
#container {
    color: #FFFFFF !important;
}
.navigation .active {
    color: #FFFFFF !important;
}
table {
    color: #FFFFFF !important;
    border: none !important;
}
.tcat {
    background: #121212 !important;
    color: #FFFFFF !important;
    border: 1px solid #4D4B4B !important;
}
.bottommenu {
    background: #131212 !important;
    color: #FFF !important;
    border: 2px solid #4D4B4B !important;
}
.tfoot {
    background-color: #121212 !important;
    border: 2px solid #4D4B4B !important;
}
.messageEditor .editor_dropdown_menu li {
    color: #121212;
}
.messageEditor .toolbar_dropdown .editor_dropdown_label {
    color: #121212;
}
div.error {
    /* border-top: 2px solid #FFFFFF; */
    /* border-bottom: 2px solid #FFFFFF; */
    
    background: #252525 !important;
    border: 2px solid !important;
}
div.error p em {
    color: #FFF;
}
.pagination_breadcrumb {
    background-color: #121212;
    border: none;
    outline: none;
    padding: 5px;
    margin-top: 5px;
    font-weight: normal;
}
.pagination .pagination_current {
    background: #121212;
    color: #FFF;
}
.popup_menu .popup_item {
    background: #121212;
    color: #000;
    border: none;
}
.popup_menu {
    background: #4F4D4D;
    border: none;
}
}
