css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("toothl.es") {
  
  :-moz-placeholder { /* Firefox 18- */
   color: #eeeeee;  
  }
  ::-moz-placeholder {  /* Firefox 19+ */
   color: #eeeeee;  
  }
  ::-webkit-input-placeholder {
   color: #eeeeee;
  }
  :-ms-input-placeholder {  
   color: #eeeeee;  
  }
  
  body {
    color: #e7e8e8;
  }
  
  #chat {
    background: #3d4650;
  }
  
  #chat .from {
    border-right-color: #2d3541;
  }
  
  #chat .toggle-content {
    background: #4e545c;
    color: #fff;
  }
  #chat .notice .time, 
  #chat .notice .text, 
  #chat .chan .notice .user {
    color: #9ca8cb !important;;
  }
  #chat .messages .message {
    color: #fff;
  }
  #chat .messages .action {
    color: #a7b5c6;
  }
  #chat .messages .self {
    color: #848688;
  }
  
  #chat .sidebar{
    background: #3d4650;
    border-left-color: #4d5865;
  }
  #chat .count {
    background: #2e353c;
    color: #eeeeee;
  }
  #chat .search {
    color: #eeeeee;
  }
  
  #form {
    background: #30363e;
    border-top-color: #353b44;
  }
  #form #input {
    border-color: #000;
    background: #1E252B;
    color: #fff;
  }
  #form #nick {
    background: #363c40;
    color: #cfcfcf;
  }
  
  #windows .border {
    border-bottom-color: #4d5865;
  }
  
  #windows .window:before,
  #windows .chan:before {
    background: #30363E;
    border-bottom-color: #4d5865;
  }
  
  #windows .window {
    background: #3d4650;
  }
  
  #windows .chan {
    background: #3d4650;
  }
  
  #windows .header {
    border-bottom-color: #4d5865;
  }
  
  #windows .header .title {
    color: #f7f7f7;
  }
  
  #windows .header .topic {
    color: #cacaca;
  }
}
