css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk") {
html, body
{
  font-family: arial, sans-serif;
  font-size: 10pt;
  background-color: #eee;
  color: #404040;
}

.omittedposts
{
  font-size: 15px;
  font-family: arial, sans-serif;
  color: #404040;
}
  
.reply blockquote, blockquote :last-child
{
  font-family: arial, sans-serif;
  font-size: 10pt;
  color: #404040;
}

.header
{
  background-color: #ddd;
  background-image: none;
  background-repeat: repeat;
  background-attachment: scroll;
  background-position: 0% 0%;
  background-clip: border-box;
  background-origin: padding-box;
  background-size: auto auto;
  color: #404040;
  padding-top: 0px;
  padding-right: 3px;
  padding-bottom: 0px;
  padding-left: 3px;
  font-weight: bold;
}

.list
{
  border: none;
}

.list div a
{
  display: block;
  background-color: #eee;
  text-decoration-line: none;
  text-decoration-style: solid;
  color: #404040;
  padding-top: 0px;
  padding-right: 3px;
  padding-bottom: 0px;
  padding-left: 3px;
}

.list div a:hover
{
  background-color: #fefefe;
  background-image: none;
  background-repeat: repeat;
  background-attachment: scroll;
  background-position: 0% 0%;
  background-clip: border-box;
  background-origin: padding-box;
  background-size: auto auto;
}

.logo
{
  clear: both;
  text-align: center;
  font-size: 2em;
  color: #404040;
  width: 100%;
}

.reply
{
  background-color: #ddd;
  color: #404040;
}

.doubledash
{
  display: none;
}

.reflink a
{
  color: #999;
}

.filesize
{
  color: #999;
}

a
{
  color: #5555ff;
  text-decoration: none;
}

a:hover
{
  color: #9999ff;
  text-decoration: none;
}

.postername
{
  color: #99bb99;
  font-weight: bold;
}

.commentpostername
{
  color: #779977;
}

.thumbnailmsg
{
  color: #999;
}

.adminbar
{
  text-align: center;
  clear: both;
  float: none;
}

.spoiler
{
  background-color: #999;
  color: #999;
}

.spoiler:hover
{
  background-color: #ccc;
  color: #404040;
}

.postblock
{
  background-color: #ddd;
  color: #404040;
}

.logo a, .sublogo a
{
  clear: both;
  text-align: center;
  font-size: 1em;
  color: #404040;
  width: 100%;
}

.highlight
{
  background-color: #ddd;
  background-image: none;
  background-repeat: repeat;
  background-attachment: scroll;
  background-position: 0% 0%;
  background-clip: border-box;
  background-origin: padding-box;
  background-size: auto auto;
  color: #404040;
  border-top-width: 1px;
  border-right-width: 1px;
  border-bottom-width: 1px;
  border-left-width: 1px;
  border-top-style: dashed;
  border-right-style: dashed;
  border-bottom-style: dashed;
  border-left-style: dashed;
  border-top-color: #707070;
  border-right-color: #707070;
  border-bottom-color: #707070;
  border-left-color: #707070;
  border-image-source: none;
  border-image-slice: 100% 100% 100% 100%;
  border-image-width: 1 1 1 1;
  border-image-outset: 0 0 0 0;
  border-image-repeat: stretch stretch;
}

.theader
{
  background-color: #ddd;
  background-image: none;
  background-repeat: repeat;
  background-attachment: scroll;
  background-position: 0% 0%;
  background-clip: border-box;
  background-origin: padding-box;
  background-size: auto auto;
  text-align: center;
  padding-top: 2px;
  padding-right: 2px;
  padding-bottom: 2px;
  padding-left: 2px;
  color: #404040;
}
  
.news
{
  background-color: #ddd;
  color: #404040;
}
  
.greeting
{
  color: #404040;
}
  
.adminname
{
  color: #aa4040;
}
  
.unkfunc
{
  color: #40aa40;
}
}
