css:
/*PET*/
@-moz-document domain("eldarya.fr") {

#pet-headerPortrait #pet-headerPortrait-base {
	background: url("http://i64.tinypic.com/2z7rbsw.jpg") no-repeat !important;
	-webkit-filter: hue-rotate(0deg);
	filter: hue-rotate(0deg);
}
#pet-portrait {
	background: url("http://i65.tinypic.com/ayauev.jpg") center no-repeat !important;
	background-size: auto 450px !important;
	height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
	display: none !important;
}
#player-display-pet {
	background: url("http://i.imgur.com/NbHykWJ.png") center no-repeat !important;
	background-size: 700px !important;
	height: 310px !important;
	width: 225px !important;
}
#player-display-pet > img[src*="/static/img/pet/mood/profile/"] {
	opacity: 0 !important;
}
#player-display-pet {
	background: url("http://i.imgur.com/NbHykWJ.png") center no-repeat !important;
}
/*PET-Infos*/

#pet-info-name:before {
	content: "Miku Hatsune/" !important;
    font-size : 16px !important ;
    line-height : 20px !important ;
}
  #pet-info-racegender:before {
    content : "Vocaloid" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
img[src*="/static/img/f.png"] {
	background: url("http://www.eldarya.fr/static/img/m.png") !important;
	padding-left: 14px !important;
	padding-top: 15px !important;
	width: 0 !important;
	height: 0 !important;
	margin-bottom: 5px !important;
}
/*PET-Infos-food*/

#pet-info-food:before {
	line-height: 15px;
	content: "fraises" !important;
	font-size: 30px !important;
	margin: 200px auto auto 34px;
}
#pet-info-food > div {
	margin-top: 2px !important;
	line-height: 15px;
	font-size: 12px;
	margin: 13px 0 0 100px;
}
#pet-info-food > img {
	background: url("http://1.bp.blogspot.com/-gur7kwb83VE/T5uC7pL9MXI/AAAAAAAAPbQ/IoW4H6Y71y0/s1600/erdbeere-256.png") no-repeat !important;
	background-size: 51px 56px !important;
	margin-top: -5px !important;
	padding-left: 60px !important;
	padding-top: 60px !important;
	width: 0 !important;
	height: 0 !important;
} 
  
  #pet-info-food {font-size : 0 !important ;}
    
  #pet-info-food > span {font-size : 16px !important ;}
}
