css:
@-moz-document domain("manage.betterttv.net") {
body{
   background: #282828;
   color: #FFFFFF;
   }
  .form-control {
    background-color: #282828;
    color: #ffffff;
  }
  .table-striped > tbody > tr:nth-child(2n+1){
    background-color: #1E1E1E;
    border-block-start-color: #282828;
  }
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    color: #ffffff;
  }
  hr{
    border-top: 1px solid #4d4d4d;
  }
  .navbar-default{
    background-color: #282827;
    border-color: #282827;
  }
  
  /* Makes the navbar text white */
  .navbar-default .navbar-nav > li > a {
    color: #ffffff;
  }
  
  /* Makes the "user" information white */
  .navbar-default .navbar-text {
    color: #ffffff;
  }
  
  /*Makes the mouseover a slightly grayer color, more inline with bttv on twitch*/
  .navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus {
    color: #cccccc;
  }
  
  /*Table above the emote preview*/
  table {
    color: #282828
  }
  
  /*Table on the first page*/
  .list-group-item{
    background-color: #282828;
    color: #fff;
  }
  
  /*Modify the fancy Jumbotron on the homepage*/
  .fancy-jumbo {
    background: #282828;
  }
  
  .fancy-jumbo .fancy-jumbo-overlay {
    background-color: rgba(40, 40, 40, 0.8);
    box-shadow: inset 0px 50px 50px -50px rgba(77, 77, 77, 0.4);
  }
  
  /*Modify the table properly*/
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 1px solid #282828;
  }
  
  /*Modify the other table elements*/
  .table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #1E1E1E;
  }
  
  /*Buttons!*/
  .btn-default {
    background-color: #232323;
    color: #fff;
  }
  
  /*Emote previews*/
  .app-grid .grid-item {
    background: #1E1E1E;
    color: #ffffff;
  }
  
  .emote-preview-contain {
    color: #1E1E1E;
  }
  
  /*Justification box*/
  .form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #232323;
  }
  
  .modal-body {
    color: black;
  }
}
