@-moz-document domain("www.koreus.com") {
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #efefef;
    background-color: #353535 !important;
}
	
	.navbar-default {
    background-color: #2C2C2C !important;
    border-color: #000;
}
	
	.navbar-default .navbar-nav > li > a:focus, .navbar-default .navbar-nav > li > a:hover {
    color: #fff;
    background-color: transparent;
}

	.navbar-default .navbar-nav > li > a, .navbar-default .navbar-text {
    color: #fff;
}
	
	.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    list-style: none;
    font-size: 14px;
    text-align: left;
    background-color: #2C2C2C;
    border: 1px solid #ccc;
    border: 1px solid rgba(0,0,0,.15);
    border-radius: 4px;
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,.175);
    box-shadow: 0 6px 12px rgba(0,0,0,.175);
    background-clip: padding-box;
}
	
.dropdown-menu > li > a {
    clear: both;
    font-weight: 400;
    color: #fff;
}
.dropdown-menu > li > a:hover {
background-color: #acaaba;
color: #555;
}
	
	
	
	.dropdown-menu > .active > a, .dropdown-menu > .active > a:hover, .dropdown-menu > .active > a:focus {
    color: #fff;
    text-decoration: none;
    outline: 0;
    background-color: #fecc53;
		color : #000;
}
	
.container {

    background: transparent !important;
   border : 0px !important;
}
	

	
li {
    margin-left: 2px;
    list-style-type:none !important;
    color: #FFF;
}	
	
	
	
	.thumbnail {
    display: block;
    padding: 4px;
    margin-bottom: 20px;
    line-height: 1.42857143;
    background-color: #252525;
    border: 1px solid #000000;
    border-radius: 4px;
    -webkit-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
}
	
	.panel {
    margin-bottom: 20px;
    background-color: transparent;
    border: 1px solid transparent;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0,0,0,0.05);
    box-shadow: 0 1px 1px rgba(0,0,0,0.05);
}
	
	.btn-default {
    color: #000;
    background-color: #fecc53 !important;
    border-color: #121212;
}

	.btn-default:hover {
    color: #fff;
    background-color: #9A9ACE !important;
    border-color: #121212;
}
	

.pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 12px 12px;
    line-height: 1.42857143;
    text-decoration: none;
    color: #fff;
    background-color: #252525;
    border: 1px solid #121212;
    margin-left: -1px;
}
.pagination > li > a:hover{
    position: relative;
    float: left;
    padding: 12px 12px;
    line-height: 1.42857143;
    text-decoration: none;
    color: #000;
    background-color: #fecc53;
    border: 1px solid #121212;
    margin-left: -1px;

}
	
.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
    z-index: 2;
    color: #fff;
    background-color: #9A9ACE;
    border-color: #306;
    cursor: default;
}
	
	
/**************************************/
/* MODIFICATION SUR LE MENU DE GAUCHE */
/**************************************/
	table {
    width: 100%;
    font-size: 12px;
    background-color: #303030 !important;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    color: #ccc;
}
	
	td#centercolumn {
    font-size: 12px;
		background-color: #303030 !important;
}
	

	
	/* Vignette menu jaune/orange */
	td#leftcolumn div.blockTitle{
	  color: #000;
  	background: #fecc53;
		padding: 4px 3px 4px 3px;
		text-transform: uppercase;
  }
	
	td#leftcolumn {
    width: 170px;
    border-right: 0px solid #fecc53;
    font-size: 12px;
    color: #330066;
    background-color: #303030;
}
	
	/* Suppression du décalage des TD dans Menu Utilisateur */
	td#usermenu{
		padding: 0px;
		background: #2C2C2C;
		color: #B8B8B8;
	}
	/* Suppression des marges des blocs des menus */
	td#leftcolumn div.blockContent{
		background: #2C2C2C;
		color: #B8B8B8;
		box-shadow: inset 0px 0px 5px 0px  #000;
		padding : 2px;
	}
	
	a{
color: #ddd;
	}
	
	 a:hover{
color: #fecc53;
	}
	

	
	/* Changement sur les cases|liens du Menu Utilisateur */ 
	td#usermenu a{
    transition: background 0.3s;
		padding: 2px 0px 2px 6px;
				border: 0px;
		border-color: #000; 
	}
	

	td#mainmenu {
	  padding: 0px !important;

	}
	td#mainmenu a{
    transition: background 0.3s;
		padding: 2px 0px 2px 6px;
		border: 0px;
		margin : 2px;
		box-shadow: inset 0px 0px 5px 0px  #000;
		text-transform: uppercase;
	}
		td#mainmenu a:hover{
    transition: background 0.3s;
		box-shadow: inset 0px 0px 20px 0px  #000;
			background-color: rgba(255,255,255,0.1);
	}
	td#mainmenu a.menuMain{
		padding : 5px;
	  border: 0px;
	}
	td#mainmenu a.menuTop{
		background-color: rgba(0,0,0,0.1);
		padding : 5px;
		border-right: 0px;
	  border: 0px;
		padding-left: 6px;

	}
	td#mainmenu a.menuSub{
		border: 0px;
		background-color: rgba(0,0,0,0.3);
		border-left: 10px solid #fecc53;
		padding : 5px;
		margin : 5px;
		box-shadow: inset 0px 0px 5px 0px  #000;
	}
	
	td#leftcolumn div.blockContent{
		border: 2px solid #fecc53;
	}

	td#leftcolumn div.blockContent ul{
	  padding: 0px;
		margin: 0px;

	}
	td#leftcolumn div.blockContent li{
    transition: background 0.3s;
		padding: 5px;
		border: 0px;
		margin : 2px;
		box-shadow: inset 0px 0px 5px 0px  #000;
	}
	td#leftcolumn div.blockContent li:hover{

    transition: background 0.3s;
		box-shadow: inset 0px 0px 50px 0px  #000;
			background-color: rgba(255,255,255,0.1);
	}
	
	
