css:
@-moz-document domain("discordapp.com") {

.body {
}

  /*************************/
 /*	TITLE 		          */
/*************************/

/* SERVER TITLE */

    .guild-header header {
        padding-left:15px;
        padding-right:10px;
        padding-top:20px;
        padding-bottom:20px;
        height:0px;
		box-shadow: 0 1px 0 #24272b,inset 0 -1px 0 rgba(0,0,0,.1);
    }

    .guild-header header span {
        line-height:40px;
        font-size:15px;
        padding-top:2px;
    }

/* MENU */

	.popout-menu {
        width: 200px;
        top:30px !important;
	}

/* CHAT TITLE */

    .chat .title-wrap {
        padding-left:2px;
        padding-right:10px;
        padding-top:0px;
        padding-bottom:1px;
        height:41px;
        min-height:40px;
    }

	.chat>.title-wrap>.title .channel-name {
        margin-left:12px;
	}

    .chat .title-wrap .title {
        font-size:16px;
    }

/* BUTTONS */

    #bd-customcss-detach-container {
        padding-top:40px;
    }

	.header-toolbar button+button {
		margin-left:4px;
	}
	
	.win-buttons {
        height:40px;
    }
		.win-minimize:hover {
            background-color:#f5f8fa;
            border-radius:5px;
		}
		.win-maximize:hover {
            background-color:#f5f8fa;
            border-radius:5px;
		}
		.win-close:hover {
            background-color:#faeded;
            border-radius:5px;
		}
		.win-buttons-light .win-minimize:hover {
            background-color:#2e3136;
            border-radius:5px;
		}
		.win-buttons-light .win-maximize:hover {
            background-color:#2e3136;
            border-radius:5px;
		}
		.win-buttons-light .win-close:hover {
            background-color:#361b1b;
            border-radius:5px;
		}
	
	.header-toolbar {
		margin-left:8px;
		} .theme-dark .header-toolbar {
	}
		.header-toolbar:before {
			content:"";
			position:relative;
			left:-6px;
			width:1px;
			height:22px;
			background-color: rgba(216,216,216,.1);
			} .theme-dark .header-toolbar:before {
			background-color: rgba(216,216,216,.1);
		}
		
	.header-toolbar button:hover {
		background-color:#ebeef0;
		border-radius:5px;
		} .theme-dark .header-toolbar button:hover {
		background-color:#2e3136;
		border-radius:5px;
	}

	.header-toolbar button.active {
		} .theme-dark .header-toolbar button.active {
		background-color:#2e3136;
		border-radius:5px;
	}

	.header-toolbar .separator.separator-win {
        margin-right:108px;
	}

	.btn-hamburger:hover {
		background-color:#4f5359;
	}
	
/* SEPARATORS */
	
	.header-toolbar .separator {
		margin: 0px 4px 0px 4px
	}
	
	.header-toolbar .separator.separator-win {
		margin-left:2px;
	}
	
/* SEARCH */
	
	.search .search-bar {
		width:88px;
		margin-right:4px;
		} .theme-dark .search .search-bar {
		background-color:#2e3136;
		border: 2px solid #2e3136;
	}
		.search .search-bar:hover {
			} .theme-dark .search .search-bar:hover {
			background-color:#43464D;
		}
		
	.search .DraftEditor-root .public-DraftEditor-content, .search .DraftEditor-root .public-DraftEditorPlaceholder-root {
		padding-left:10px;
		padding-right:4px;
	}
	
	.search .search-bar-icon {
		bottom:2px;
		margin-right:2px;
	}
	
	.search .DraftEditor-root .public-DraftEditor-content, .search .DraftEditor-root .public-DraftEditorPlaceholder-root {
		padding-top:1px;
	}
		
  /*************************/
 /*	GUILDS	              */
/*************************/

/* FRAME */

    .guilds-wrapper {
        width:70px;
    }

	/* center-justify */
    .guilds-wrapper .guilds {
        padding-left:10px;
        padding-right:10px;
    }

/* GUILDS */

    .guild {
        padding-left:10px;
        padding-right:10px;
        left:-10px;
        transition:background-color 0.5s;
    }
        .guild:hover {
            background-color: #2e3136;
        }
        .guild.selected {
            background-color: #2e3136;
        }
        .guilds-wrapper .guilds .guild.selected:before {
            left:-7px;
        }

    /* LEADING */

        /* first */
        .guilds-wrapper .guilds .guild { 
            margin-top:px;
            padding-top:3px;
            padding-bottom:3px;
        }

        /* subsequent */
        .guilds-wrapper .guilds .guild+.guild { 
            margin-top:0px;
            padding-top:3px;
            padding-bottom:3px;
        }

        /* friends online */
        .guilds-wrapper .guilds .friends-online {
            margin-top:5px;
            margin-bottom:5px;
        }

    /* ADD NEW */

        .guilds .guilds-add {
            margin-left:10px;
            margin-top:10px;
            padding-top:10px;
        }

/* BADGES */

    .guilds-wrapper .guilds .guild.unread:before {
        left: -7px;
        border-radius:10px;
    }

    .guilds-wrapper .guilds .guild .badge {
        bottom: 0px;
        right: 8px;
    }
	
	.guilds-wrapper .guilds .guild.audio .guild-inner:after {
		top: -2px;
		right:1px;
		width:20px;
		height:20px;
		background-size:14px;
	}

	
  /*************************/
 /*	CHANNELS	          */
/*************************/

/* FRAME */

    .channels-wrap {
        width:220px;
    }

    .guild-channels {
        width:220px;
    }

/* SECTIONS */

    .guild-channels header {
        margin-top:10px;
		padding-bottom:5px;
        padding-right:0px;
        padding-left:15px;
        height:25px;
        font-weight:900;
        transition:background 0.5s;
    }
    .guild-channels header:hover {
        background:linear-gradient(90deg,#2e3136, #2b2e33 8%, #2b2e33 90%,#2e3136);
    }

	/* TEXT CHANNELS */

        .guild-channels .channel-text a {
            padding-top:4px;
            padding-bottom:5px;
            padding-left:15px;
            font-size: 14px;
            
        }
		.guild-channels .channel-text:hover {
            transition:background 0.5s;
            background:linear-gradient(90deg,#2e3136, #2b2e33 8%, #2b2e33 90%,#2e3136);
		}
		.guild-channels .channel-text:hover.channel-muted:before,.guild-channels .channel-text:hover:not(.unread):before {
            border-left:0px solid;
            transition:background 0.5s;
		}

        .guild-channels .icon+.icon {
            margin-left:3px;
            margin-right:-6px;
        }

		.guild-channels .channel-text.unread:not(.selected):not(.channel-muted):before {
            width:10px;
            height:10px;
            top:8px;
		}


		.guild-channels .channel-text.selected {
            background:linear-gradient(90deg,#2e3136, #282b30 8%, #282b30 90%,#2e3136);
		}
		.guild-channels .channel-text.selected:before {
            border-left:0px;
		}
	
		.guild-channels .toggle-muted-text-channels {
			padding-top:4px;
			padding-left:20px;
		}
		
		.guild-channels .toggle-muted-text-channels a{
			font-size:10px;
		}
		
		.guild-channels .toggle-muted-text-channels a:before {
			width: 12px;
			height: 12px;
			background-size:12px 12px;
			margin-bottom:2px;
		}
		
        /* Channel name width. Doesn't seem to be working. */        
        .guild-channels .channel-name {
            width:180px !important;
        } 

	/* VOICE CHANNELS */

        .guild-channels .channel-voice {
            padding-top:4px;
            padding-bottom:5px;
			padding-left:15px;
            font-size: 14px;
        }	
			.guild-channels .channel-voice:hover {
                background:linear-gradient(90deg,#2e3136, #282b30 8%, #282b30 90%,#2e3136);
            }
            .guild-channels .channel-voice-states {
                margin-top:5px;
            }

		.guild-channels .channel-voice .channel-arrow {
            margin-right:6px;
		}

	/* ACCOUNT */

        .account {
            height:55px;
            padding-bottom:20px;
        }

        .account:after {
            content:'Composy v1.41 - 2016 Nov 15 - Swan Song';
            position:absolute;
            height:15px;
            width:220px;
            bottom:0px;
            left:70px;
            border-top:1px solid #1c1e22;
            font-size:10px;
            text-align:center;
            padding-top:5px;
            color:#6c6e71;
			line-height:10px;
        }

		.account .account-details .username {
            line-height:16px;
		}

		.account .btn:hover {
            background-color:#383c42;
		}

		#voice-connection .btn:after {
            background-color:#383c42;
		}

  /*************************/
 /* MESSAGES	          */
/*************************/

.messages-wrapper:after {
    content:' ';
    height:20px;
    width:100%;
    position:absolute;
    float:top;
    top:-1px;
    background: linear-gradient(to bottom, rgba(16,6,14,1)5%,rgba(16,6,14,0) 100%);
	} .theme-dark .messages-wrapper:after {
    background: linear-gradient(to bottom, rgba(54,57,62,1)5%, rgba(54,57,62,0) 100%);
}

/* MESSAGE GROUP (COZY) */

    .message-group {
        padding-top:0px;
        padding-bottom:8px;
        padding-left:6px;
        margin-left:6px;
        border-bottom:0px;
    }
        .message-group:hover {
            background-color:#fafafa;
            border-radius:5px;
			} .theme-dark .message-group:hover {
            background-color: #3a3d42;
            border-radius:5px;
        }

	/* USER */

		.message-group h2 {
            padding-top:6px;
            border-top: 1px solid rgba(0,0,0,0.04);
		} .theme-dark .message-group h2 {
            border-top:1px solid rgba(255,255,255,.04);
		}

		/* name */
        .message-group h2 strong {
            font-size:11px;
            font-weight:900;
        }

		/* avatar */
        .message-group .avatar-large {
            margin-right:12px;
            width:30px;
            height:30px;
            margin-top:10px;
            background-size:30px 30px;
            border-radius:15px;
        }

		/* timestamp */
        .message-group h2 .timestamp {
            font-size:10px;
            font-style:italic;
        }

	/* TEXT */
        .message-group .comment .markup {
            padding-top:3px;
            padding-bottom:2px;
            margin-top:0px;
            font-size:14px;
        }
		
		.message-group .comment .attachment {
			margin-top:5px;
		}

		.message-group .comment .edited {
            font-style:italic;
            padding-left:2px;
		}

/* MESSAGE (COMPACT) */

    .message-group.compact.message-group {
        padding-top:1px;
        padding-bottom:1px;
        border-top: 1px solid rgba(0,0,0,0.04);
    } .theme-dark .message-group.compact.message-group {
        border-top:1px solid rgba(255,255,255,.04);
	}

    .message-group.compact .message .markup .user-name {
        font-size:12px !important;
        font-weight:700 !important;
        padding-right:2px;
    }

    .message-group.compact .message .markup {
        margin-top:0px;
        padding-top:0px;
        padding-bottom:0px;
        text-indent:-70px;
        padding-left:70px;
    }

    .message-group.compact .timestamp {
        width:50px;
        font-size:10px;
    }

/* MISC */

	/* EDITING */
		.message-group .edit-message .edit-container-outer {
            margin-left:38px;
		}

		.message-group .edit-message .channel-textarea {
            margin-top:3px;
            padding-top:0px;
            padding-bottom:0px;
		}

		.message-group .edit-message .channel-textarea-inner {
            margin-bottom:0px;
		}

	/* EMBEDS */
		.embed {
            margin-left:2px;
            margin-right:2px;
            padding-top:2px;
            padding-bottom:2px;
            padding-left:8px;
            padding-right:8px;
            margin-top:2px;
            font-size:14px;
            border-right:5px solid #f3f3f3;
			} .theme-dark .message-group .embed {
            border-right-color:hsla(0,0%,95%,.1);
		}
		.message-group .embed .embed-description {
            font-size:14px;
		}
		.embed.embed-image {
            margin-left:8px;
		}
		.message-group .comment .attachment-image {
            margin-top:0px;
            margin-left:8px;
		}

	/* CODE */

		.message-group .comment .markup pre code {
            font-size:12px;
		}

    .message-group .comment {
        margin-right:5px;
    }
	
	.chat .jump-to-present-bar {
		left:10px;
		bottom:-1px;
	}
	
	.chat .new-messages-bar {
		left:10px;
		top:-1px;
	}

	/* DIVIDERS */
	
	.chat .divider {
		margin-top:12px;
	}
		.chat .divider>div {
			height:2px;
			border-radius:1px;
		}
		.chat .divider>span {
			font-size:12px;
			font-weight:700;
			letter-spacing:1px;
		}
		.theme-dark .friends-table .messages .divider:not(.red) span, .theme-dark .messages-wrapper .messages .divider:not(.red) span {
			color: rgba(114,137,218,.9);
		}
		.theme-dark .friends-table .messages .divider:not(.red) div, .theme-dark .messages-wrapper .messages .divider:not(.red) div {
			background-color: rgba(114,137,218,.9);
		}
		
	.messages-wrapper .messages .divider span {
        border-radius:8px;
		} .theme-dark .messages-wrapper .messages .divider span {
        border-radius:8px;
	}


/* TEXT INPUT BOX */

    .channel-textarea {
        margin-top:2px;
        padding-top:6px;
        padding-bottom:6px;
        margin-bottom:20px;
    }

    .channel-textarea-inner {
        padding-top:7px;
        padding-bottom:6px;
        margin-bottom:6px;
        transition:background-color 0.2s, border-color 0.2s;
    }
        .channel-textarea-inner:hover {
            border-color: rgba(0,0,0,.3);
        } .theme-dark .channel-textarea-inner:hover {
            background-color: rgba(255,255,255,.1);
            border-color: rgba(255,255,255,.3);
        }


    .channel-textarea-upload {
        margin-top:-6px;
        margin-bottom:-6px;
        padding-right:1px;
        margin-right:8px;
        width:38px;
        background-size:22px 22px !important;
    }

	/* FORM */

        .chat form {
            margin-left:10px;
            margin-right:10px;
            margin-top:10px;
        }

	/* TYPING */

        .typing {
            height:25px;
            padding-bottom:2px;
        }


  /*************************/
 /*	USERS			      */
/*************************/

/* FRAME */

    .channel-members {
        padding-top:5px;
        
    }

/* SECTIONS */

    .channel-members h2 {
        padding-left:15px;
        margin-top:15px;
        margin-bottom:5px;
		font-size:11px;
	}

    .channel-members .member+h2 {
        padding-left:15px;
        margin-top:15px;
        margin-bottom:5px;
		font-size:11px;
    }

/* USERS */

    .channel-members .member {
        padding-left:15px;
        padding-right:0px;
    }

    .avatar-small {
        height:20px;
        width:20px;
        background-size:20px 20px;
        border-radius:10px;
    }

    .avatar-small .status {
        width:7px;
        height:7px;
    }

    .status.status-typing {
        width: 20px !important;
        height: 7px !important;
    }

    .channel-members .member-username {
        width:200px;
    }

    .channel-members .member-username-inner {
        font-size:14px;
    }

	.channel-members .member .member-activity:before {
        content:"?";
        padding-left:1px;
        padding-right:2px;
        font-size:10px;
        font-style:normal;
		color:#ffffff;
	}

	.channel-members .member .member-activity {
        font-size:0px;
		font-style:italic;
        line-height:10px;
	}

	.channel-members .member .member-activity strong {
        font-size:10px;
		font-style:normal;
        font-weight:600;
	}

  /*************************/
 /*	USERS (PRIVATE)       */
/*************************/

    .private-channels .scroller {
        padding-top:10px;
        padding-bottom:10px;
    }

    .private-channels header {
        padding-top:5px;
        padding-bottom:5px;
        padding-left:10px;
    }

    .private-channels .channel {
        height:30px !important;
    }

    .private-channels .channel a {
        padding-left:12px;
        height:30px !important;
    }
	.private-channels .channel {
        transition:background 0.5s;
	}
		.private-channels .channel:hover {
            background:linear-gradient(90deg,#2e3136, #2b2e33 8%, #2b2e33 90%,#2e3136);
		}
		.private-channels .channel.selected {
            background:linear-gradient(90deg,#2e3136, #282b30 8%, #282b30 90%,#2e3136);
		}
		.private-channels .channel:hover:before,
		.private-channels .channel.selected:before {
        	border-left:0px;
		}



  /*************************/
 /*	FRIENDS			      */
/*************************/

	#friends .friends-table .friends-table-header {
		padding-top:4px;
        padding-bottom:4px;
	}

	#friends .friends-table .friends-row {
		height:45px !important;
	}

	.search-bar {
		background-color:#2e3136;
	}
	
	.search-bar input {
		border-radius: 0px 2px 2px 0px;
	}
	
		.search-bar input:hover {
			background-color:#43464D;
		}
	
	.search-bar-icon {
		width:28px;
	}


  /*************************/
 /*	SETTINGS		      */
/*************************/


    .tab-bar.SIDE .tab-bar-item {
        padding:6px 6px 6px 20px;
    }

    .tab-bar.SIDE .tab-bar-item:hover {
        background: linear-gradient(90deg,#2b2e33 90%,#2e3136);
    }

    .tab-bar.SIDE .tab-bar-item.selected {
        background: linear-gradient(90deg,#282b30 90%,#2e3136);
    }

    .tab-bar.SIDE .tab-bar-item:before {
        border-left:0px;
    }

	#settings-roles .roles li.draggable {
        padding-left:24px;
        line-height:18px;
        min-height:30px;
        height:18px;
}

	.form .checkbox-group .checkbox {
        height:24px;
}
	.form .control-group+.control-group, 
	.form .control-group+.control-groups {
    margin-bottom:24px !important;
        }


  /*************************/
 /*	BPM				      */
/*************************/

/* button */

	.bpm-emote-search-button {
		margin-top:12px;
		margin-bottom:30px !important;
		margin-left:6px !important;
        margin-right:20px;
		padding-top:6px;
		padding-bottom:2px;
		padding-left:5px;
		border:1px solid #15161a !important;
        border-radius:4px;
        text-align:left !important;
        background-color: #2e3136;
        box-shadow: inset 1px 0 0 #373b42;
        background-size:20px;
        transition:background-color 0.2s, color 0.2s;
        color:#ccc;
	}

	.bpm-emote-search-button:hover {
		background-color:#383c42 !important;
        cursor:pointer;
		color:white;
	}
	.bpm-emote-search-button:before {
		content:"";
		margin-left:30px;
		font-size:12px;
        position:absolute;
        width:70px;
        height:54px;
        color:white;
        bottom:20px;
        left:-30px;
        border-top:1px solid #15161a;
        border-bottom:1px solid #15161a;
	}
	.bpm-emote-search-button:after {
		content:"BPM";
		margin-left:30px;
		font-size:11px;
        font-weight:600;
	}
	
/* box */
 
    #bpm-sb-container {
        border-radius:10px !important;
        font-family:Whitney,"Helvetica Neue",Helvetica,Arial,"Lucida Grande",sans-serif;
        background-color:#2e3136;
        padding:4px 0px 24px 0px;
        border:4px solid #2e3136 !important;
        box-shadow: 0px 0px 100px 50px rgba(0,0,0,0.75);
    }
 
    #bpm-sb-toprow {
        padding-left:12px !important;
        width:97% !important;
        padding-right:12px !important;
        padding-bottom:4px !important;
        }
 
        #bpm-sb-dragbox {
            -webkit-filter: invert(80%);
            filter: invert(80%);
            border-radius:5px;
            background-position:center;
            background-size:36px 36px;
            width:30px;
            height:30px;
            cursor:pointer;
            transition:background-color 0.3s;
        }
        #bpm-sb-dragbox:hover {
            -webkit-filter: invert(100%);
            filter: invert(100%);
            background-color:#d9d9d9;
        }
 
        #bpm-sb-close {
            -webkit-filter: invert(80%);
            filter: invert(80%);
            border-radius:5px;
            background-position:center;
            background-size:36px 36px;
            width:30px;
            height:30px;
            cursor:pointer;
            transition:background-color 0.3s;
        }
        #bpm-sb-close:hover {
            -webkit-filter: invert(100%);
            filter: invert(100%);
            background-color:#d9d9d9;
        }
 
        #bpm-sb-tagdropdown {
            font-family:Whitney,"Helvetica Neue",Helvetica,Arial,"Lucida Grande",sans-serif;
            border-radius:2px;
            border:3px solid #232529 !important;
            background-color:#24272b;
            color:white;
            transition:background-color 0.3s;
        }
        #bpm-sb-tagdropdown:hover {
            background-color:#43464d;
        }
 
        #bpm-sb-input {
            border:3px solid #232529 !important;
            background-color:#24272b;
            border-radius:4px;
            color:white;
            transition:background-color 0.3s;
        }
        #bpm-sb-input:hover {
            border:3px solid #232529 !important;
            background-color:#43464d;
        }
 
        #bpm-sb-bottomrow {
            height:40px;
            padding-left:12px !important;
            width:97% !important;
            padding-right:12px !important;
            padding-bottom:8px !important;
            padding-top:8px !important;
        }
 
        #bpm-sb-resize {
            -webkit-filter: invert(80%);
            filter: invert(80%);
            border-radius:5px;
            background-position:center;
            background-size:38px 38px;
            width:30px;
            height:30px;
            cursor:nwse-resize;
            transition:background-color 0.3s;
        }
        #bpm-sb-resize:hover {
            -webkit-filter: invert(100%);
            filter: invert(100%);
            background-color:#d9d9d9;
        }
 
        .bpm-search-result {
            border:4px solid #eee;
            border-radius:4px;
            background-color:#eee;
            margin:4px;
            padding:0px;
            cursor:pointer;
            transition:background-color 0.2s, border 0.2s;
        }
 
        .bpm-search-result:hover {
            background-color:#fff;
            border:4px solid #ccc;
        }
        #bpm-sb-helplink, #bpm-sb-flaglink, #bpm-sb-optionslink, #bpm-sb-srlink {
            bottom:8px;
            color:#00B7EB;
            font-size:14px;
        }
