css:
/* i really want this to be global */

body.fbIndex 
{ 
background: url("http://2.bp.blogspot.com/-hMjiiOBcOoA/U-eZe_VawQI/AAAAAAAAEw4/EjxUW6QmdzE/s1600/Anonymous+Wallpaper+by+PCbots.png") repeat fixed left center #f5f5f5 !important;background-size: cover !important;overflow:hidden;}
.login_form_label_field a {color:#fff !important;}

/** Hide Crap **/
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {display: none !important}
/** Move the black box **/
.fbIndex .loggedout_menubar_container {position: fixed !important;width: 425px !important;height: 82px !important;min-width: 0 !important;top: 50% !important;
left: 52% !important;margin: -60px 0 0 -240px !important;z-index: -1 !important;}

.loggedout_menubar_container {background:none !important;}
.loggedout_menubar {background:rgba(0, 0, 0, .3) !important;padding:0 10px 25px 10px;-webkit-border-radius: 4px;border-radius: 4px;-webkit-box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);}
.loggedout_menubar label,


/** Center Login form **/
.fbIndex .loggedout_menubar {width: auto !important;}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat {float: none !important;}
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {display: block !important;margin: 0 auto !important;}

.fbIndex .loggedout_menubar_container .lfloat img {display: block;margin: -60px auto 20px !important;}
#SetAsHomepage_Callout { display: none;}
.fbIndex .loggedout_menubar_container .lfloat i { margin: -70px auto 10px !important; }

.fb_logo {
	background-size:100% auto !important;
	width: 200px;
	margin: 12px 0 18px 115px;
}


