css:
@-moz-document domain("www.theverge.com") {

html {
    font-family: sans-serif;
    line-height: 1.4;
    -ms-text-size-adjust: 100%;
    -webkit-text-size-adjust: 100%;
    background: #0d0d0d !important;
}

body {
    background: #0D0D0D !important;
    background-repeat: no-repeat;
    background-position: center top;
}

.is-nav-open .m-nav-dropdown {
    border-bottom: 1px solid #000000 !important;
}

.m-nav-dropdown {
    background: #131313 !important;
    border-left: 1px solid #000000 !important;
    border-right: 1px solid #000000 !important;
}

.m-nav__menu li.is-selected a {
    background: #131313 !important;
}

.m-nav-dropdown__subsections {
    background: #0d0d0d !important;
}

.m-nav-dropdown__stories a:link, .m-nav-dropdown__stories a:visited {
    color: #c3c3c3 !important;
}

.l-page {
    background-color: #131313 !important;
}

.l-page {
    border: 1px solid #000 !important;
    border-width: 1px 1px 0 1px;
}

.p-entry-nav li a:hover {
    background-color: #1D1D1D !important;
}

.p-entry-nav {
    display: table;
    list-style-type: none;
    margin: 0;
    padding: 0;
    border-bottom: 1px solid #000 !important;
    width: 100%;
}

.m-entry-slot h3 {
    font-family: Georgia,serif;
    font-size: 1.5rem;
    font-weight: 400;
    line-height: 1.1;
    margin: 0;
    color: #ffffff !important;
}

.m-entry-slot .m-entry-slot__meta strong {
    display: inline-block;
    border-right: 1px solid #444;
    margin-right: .25rem;
    padding-right: .3rem;
    color: #888 !important;
}

.p-entry-nav li:first-child {
    position: relative;
    border-right: 1px solid #000 !important;
}

.m-rock-read-this h4 {
    font-family: Georgia,serif;
    font-size: 16px;
    font-weight: 400;
    color: #fff !important;
    margin: 0;
    line-height: 1.2;
}

.p-entry-header {
    position: relative;
    padding: 1rem;
    border-bottom: 1px solid #000 !important;
    word-break: break-word;
}

.m-entry__body .m-entry__sidebar {
    border-left: 1px solid #000 !important;
}

.p-entry-header h1 {
    font-family: Georgia,serif;
    font-size: 3.25rem;
    font-weight: 400;
    color: #7d7d7d !important;
    margin: 0;
    padding: 1rem 4rem 0 0;
    line-height: 1.25;
}

.p-entry-header h2 {
    color: #7d7d7d !important;
}

html, body {
    margin: 0;
    color: #7d7d7d !important;
    font-size: 15px;
}

.m-snippet h2 {
    font-family: Georgia,serif;
    font-size: 2rem;
    color: #c5c5c5 !important;
    line-height: 1.2;
}

.p-entry-header__comments:before {
    content: 'comments';
    position: absolute;
    top: 100%;
    left: 0;
    color: #C5C9C5 !important;
    font-size: 12px;
    font-weight: 600;
    display: inline-block;
    padding: 2px 0 0;
    text-align: right;
}

.m-comments .comments-header {
    background-color: #000000 !important;
    border: 1px solid #131313 !important;
    border-width: 1px 0;
    padding: 1rem 1rem .6rem;
}

.m-comments .cform {
    background-color: #131313 !important;
    padding: 1rem;
}

.m-comments .citem.new .cbody {
    color: #a29f9f !important;
}

.m-comments .citem .cbody blockquote {
    background-color: #0d0d0d !important;
    border-left: 3px solid #fa4b2a !important;
    padding: .75rem;
    margin: 1rem 0;
}

.m-forum-index__table tr:nth-child(even) {
    background-color: #2d2d2d !important;
}

.m-forum-index__table th:nth-child(1) a, .m-forum-index__table td:nth-child(1) a {
    font-family: Georgia,serif;
    font-size: 0.85rem;
    color: #fff !important;
}

.m-forum-header {
    background-color: #2d2d2d !important;
    overflow: hidden;
    height: 84px;
    position: relative;
}

.p-tabs ul li {
    font-family: Helvetica,Arial,sans-serif;
    font-style: italic;
    text-transform: uppercase;
    font-size: 0.9rem;
    font-weight: 500;
    display: inline-block;
    color: #FFF;
    background-color: #2d2d2d !important;
    padding: 0 .6rem;
    border-right: 0px solid #fff !important;
    position: relative;
}

.p-big-search form input[type='search'], .p-big-search form input[type='text'] {
    font-size: 3rem;
    padding: 1rem;
    color: #c5c5c5 !important;
}

.p-basic-article-list__node .body h3 {
    font-family: Georgia,serif;
    font-size: 1rem;
    font-weight: 400;
    color: #c5c5c5 !important;
    margin: -4px 0 0;
    padding: 0;
}

.m-article__entry blockquote, .m-stream__header blockquote {
    display: block;
    background-color: #0d0d0d !important;
    padding: 1rem 1.5rem;
    margin: 1.5rem;
    border-left: 5px solid #bab4b2;
    font-size: .9rem;
}

.m-new-articles {
    background: #131313 !important;
}

.m-new-articles {
    border-left: 1px solid #000000 !important;
    border-right: 1px solid #000000 !important;
    height: 0;
    overflow: hidden;
}

.realcsstransforms3d .is-new-articles-open .m-new-articles, .no-realcsstransforms3d .is-new-articles-open .m-new-articles {
    height: 460px;
    border-bottom: 1px solid #000 !important;
}

.m-new-articles__controls {
    text-indent: -1000px;
    background-color: #0d0d0d !important;
    display: block;
    position: absolute;
    top: 0;
    width: 46px;
    height: 460px;
    z-index: 2;
    overflow: hidden;
}

.m-new-articles__controls.-previous {
    border-right: 1px solid #000000 !important;
    left: 0;
}

m-new-articles__day {
    border-left: 1px solid #000000 !important;
    display: inline-block;
    position: relative;
    padding-top: 2rem;
    width: 311px;
    height: 460px;
    vertical-align: top;
}

.m-review__breakdown-meter li {
    background-color: #131313 !important;
    color: #fb7203 !important;
}

.m-review__breakdown h4 {
    color: #c5c5c5 !important;
}

.m-linkset h2 {
    color: #c5c5c5 !important;
}

.m-new-articles__story a:link {
    color: #c5c5c5 !important;
}

.m-new-articles__story a:visited {
    color: #515151 !important;
}

.m-review__wrap-up h3 {
    color: #fa602a !important;
}

.m-new-articles__story a:hover, .m-new-articles__story a:active {
    color: #fa4b2a !important;
}

.m-user-page__list.posts h3 a:link, .m-user-page__list.posts h3 a:active {
    color: #fff !important;
}

.m-user-page__list li:nth-child(odd) {
    background-color: #2d2d2d !important;
}

.m-user-page__log blockquote {
    background-color: #515151 !important;
}

.m-entry-slot {
    border-top: 1px solid #000 !important;
}

.m-entry-section.section1 .left:before {
    border-left: 1px solid #000 !important;
}

.m-entry-section.section1 .right:before {
    border-left: 1px solid #000 !important;
}
  
.m-entry-section.section2 .left:before {
    border-left: 1px solid #000 !important;
}

.m-entry-section.section2 .right:before {
    border-left: 1px solid #000 !important;
}  

.m-entry-section.section1 .left .m-entry-slot:first-child:after {
    border: 1px solid #000000 !important;
}

.m-entry-section.section1 .center .m-entry-slot:first-child:after {
    border: 1px solid #000000 !important;
}

.m-entry-section.section1 .center .m-entry-slot:first-child:before {
    border: 1px solid #000000 !important;
}

.m-entry-section.section2 .left .m-entry-slot:first-child:after {
    border: 1px solid #000000 !important;
}

.m-entry-section.section2 .center .m-entry-slot:first-child:after {
    border: 1px solid #000000 !important;
}

.m-entry-section.section2 .center .m-entry-slot:first-child:before {
    border: 1px solid #000000 !important;
}

.m-entry-section.section2 .right {
    border-top: 1px solid #000 !important;
}

.m-entry-section.section2 .center:before {
    border-left: 1px solid #000 !important;
}

.m-entry-section.section1 .center .m-entry-slot:first-child>:first-child:before {
    border: 1px solid #000000 !important;
}

.m-entry-section.section1 .center .m-entry-slot:first-child>:first-child:after {
    border: 1px solid #000000 !important;
}

.m-entry-section.section2 .center .m-entry-slot:first-child>:first-child:before {
    border: 1px solid #000000 !important;
}

.m-entry-section.section2 .center .m-entry-slot:first-child>:first-child:after {
    border: 1px solid #000000 !important;
}

.m-entry-section.section2 .center .m-entry-slot:last-child:after {
    border: 1px solid #000000 !important;
}

.m-entry-section.section2 .center .m-entry-slot:last-child:before {
    border: 1px solid #000000 !important;
}

.m-entry-section.section1 .center .m-entry-slot:last-child:after {
    border: 1px solid #000000 !important;
}

.m-entry-section.section1 .center .m-entry-slot:last-child:before {
    border: 1px solid #000000 !important;
}

.c-comments__post-form {
    background-color: #0d0d0d;
    padding: 16px;
}

.l-main-content {
    background-color: #131313 !important;
    border: 1px solid #000 !important;
}

.c-page-title {
    color: #7d7d7d !important;
}


.c-entry-hero__meta .p-comment-bubble:after {
    color: #fff !important;
}

.c-global-header.above:before {
    background-color: #0d0d0d !important;
}

.chorus-ad-placement__btf-leaderboard, .chorus-ad-placement__leaderboard {
    border-left: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
}

.above .c-global-header__social {
    background-color: #0d0d0d !important;
}

.c-global-header .l-wrapper {
    border: 1px solid #000 !important;
}

.c-tab-bar {
    background: hsla(0, 0%, 7%, 0.88) !important;
}

.c-tab-bar {
    border-bottom: 1px solid #000000 !important;
}

.c-rock-list__link {
    color: #7d7d7d !important;
}

.c-rock-list__link:hover {
    color: #e22805 !important;
}

.p-counter-link__count {
    background-color: #000000 !important;
    color: #969696 !important;
}

.c-entry-content blockquote {
    background-color: #0d0d0d !important;
}

.p-comment-notification {
    color: #7d7d7d !important;
}

.p-comment-notification:after {
    background-color: #7d7d7d !important;
}

.p-comment-notification:before {
    color: #7d7d7d !important;
}

.c-comments__recommended {
    background: rgba(57, 48, 146, 0.17);
}

.c-entry-sidebar {
    background-color: #0d0d0d !important
}
}
