#wp-calendar td#next {
     background: rgba(0, 0, 0, 0) none repeat scroll 0% 0%;
     padding: 0.1em 0px;
}

#wp-calendar td#prev {
     background: rgba(0, 0, 0, 0) none repeat scroll 0% 0%;
     padding: 0.1em 0px;
}

#wp-calendar td#next a, #wp-calendar td#prev a {
     display: inline;
}

#wp-calendar td#next {
     text-align: right;
}

#wp-calendar td#prev {
     text-align: left;
}

.myinlinepicture {
     padding: 0px;
}

.myinlinepictureleft {
     float: left;
     margin: 0px 10px 0px 0px;
     padding: 0px;
}

.myinlinepictureright {
     float: right;
     margin: 0px 0px 0px 10px;
     padding: 0px;
}
}
