css:
@-moz-document domain("www.wrike.com") {
@media (max-width:1000px) {
	.x-panel .x-border-layout-ct .x-border-panel {
		width: 100% !important;
		position: static !important;
		top: inherit !important;
		left: inherit !important;
		height: 69% !important;
		margin-bottom: 1% !important;
		overflow-x: hidden !important;
		overflow-y: auto !important;
	}
	#tasklist-panel {
		height: 29% !important;
		overflow-y: hidden !important;
	}
	.wspace-task-view {
		width: 100% !important;
	}
}
.viewport-center-west {
	width: 19px !important;
	left: 1px !important;
	top: 0px !important;
	height: 99% !important;
	border-right-width: 8px;
	border-right-style: solid;
	border-right-color: rgb(66, 75, 85);
	overflow: hidden;
	z-index: 100000;
	-webkit-transition: width .2s linear .5s;
	/* Safari */
	
	transition: width .2s linear .5s;
}
.viewport-center-west:hover,
#global-navigation-panel {
	-webkit-transition: width .2s linear .1s;
	/* Safari */
	
	transition: width .2s linear .1s;
	width: 250px !important;
}

ws-folder-header-title {
    font-weight: bold;
    text-decoration: underline;
}

div[wrike-task-view-adder-big] {
	padding:0 0 0 50px !important;
}
div[wrike-task-list-view-group] {
	top:31px !important;
}

ws-folder-header-bottom {
	padding: 0 10px 0 20px !important;
	min-height:30px !important;
}
.filter-bar,
.sorting-list{
	padding: 0 0 0 0 !important;
}

}
