css:
@-moz-document domain("bibliaonline.com.br") {
#body {
    background-color: #333;
}

.container {
    background: #333;
}

article {
    color: #888;
}

article header {
    color: #fff;
}

#ChapterView header, #HomeView h1, #BookIndexView h1 {
    margin-top: 30px;
    padding-top: 30px;
    margin-bottom: 30px;
    padding-bottom: 30px;
    border-bottom: 1px dashed #666;
    border-top: 1px dashed #666;
}

p sup {
	font-size: 60%;
    position: relative;
    vertical-align: top;
    top: 13px;
    width: 12px;
    display: inline-block;
    text-align: right;
    color: #fff;
}

.bible.verses p {
    margin-bottom: 15px;
    font-size: 22px;
    font-family: "Helvetica Neue",Arial,"Liberation Sans",FreeSans,sans-serif;
}

.text {
    display: inline-block;
    width: 95%;
    padding-left: 10px;
}

#ChapterView #ChapterSidebar {
    width: 200px;
}

a {
    color: #d64f4c;
}

a:hover {
    color: #ee645e;
}

ul.ChapterList li {
    display: inline;
    padding: 0px;
    width: initial;
}
ul.ChapterList a {
    display: inline-block;
    padding: 5px 5px;
    background-color: #2f2f2f;
    margin: 0px 10px 10px 0;
    width: 20px;
}

#BookIndexView ul.ChapterList a {
    width: 60px;
    padding: 10px;
}

#Footer {
    background: none;
    border-top: 1px dashed #666;
}

h4 {
    color: #eee;
    font-weight: normal;
}

#Navbar form input, select, #Navbar form button {
    background: #2f2f2f;
    border: none;
    color: #666;
}

select {
    padding: 10px;
}

#HomeView {
    color: #888;
}
#HomeView h1, #BookIndexView h1 {
    color: #fff;
}



}
