css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://tieba.baidu.com/i/")
{
    /* 始终显示下拉菜单 */
    #feed .p_favth_menu
    {
        display: block;
        right: 0;
        top: 15px;
        border: none;
    }
    
    /* 隐藏不需要的菜单项 */
    #feed .p_favth_canel_remind, #feed .p_favth_tri, #feed .favth_col4
    {
        display: none !important;;
    }
    
    /* 调整“取消收藏”菜单项 */
    #feed .p_favth_canel_kept2
    {
        width: 70px;
        text-align: center;
    }
}
