css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ecollege.com") {
  
    
  html, body, table, td, tr, p, h1, h2, h3, h4, h5{
    background: #1e1e1e!important;
    color: #A2A2A2!important;
  }
  .common-background {
    background-color: #000;
}
  .common-height {
    height: 100%;
    background: black;
}
  .courseCards {
    padding: 20px 10px;
    width: 300px;
    background: #393737;
}
  .emptyCardStyles {
    width: 280px;
    background-color: #25214A;
    border: 1px #D0D0D0 solid;
    text-align: center;
    height: 211px;
    padding-top: 90px;
}
  
  .ov-content-pane, .ov-top-menu {
      background: #1e1e1e;
}
  .ov-content-header .container-fluid {
      background: #1e1e1e;
}
  
  .ov-left-nav .discipline, .ov-left-nav #menu-list-holder ul {
    border-right: solid 1px #F0F0F0;
    border-bottom: solid 1px #FFF;
    background-color: #000;
    line-height: 45px;
}
  
  
  .ov-left-nav nav ul li {
    background: #1e1e1e;
  }
 
  /* sign in screen */
  
  .wrapper {  background: #000!important;  }
  .global-nav { background: rgb(0, 0, 0); }
  .extra-light-grey-bg, .dijitBorderContainerNoGutter { background-color: #000; }
  .dijitBorderContainer, .dijitBorderContainerNoGutter { background: #000!important; color: #A2A2A2; }

  
  
  .fNormal { color: #AAA!important; background-color: black !important; }
span.fInstructions { color: #F89038!important;  background-color: black !important; }
.dbootstrap .statusPanel { color: #7AA0D2;  background: #282828; }
.summary-bar {   border-top: 1px solid #ff6e6e;    border-bottom: 1px solid #FF9C9C; background-color: #000; }
 
  /* score */
  .summary-bar .text { color: #F8CECE!important; }
  .dbootstrap .palettePanel { background-color: #020202; color: #53b1f6; border-top: 1px solid #969696; }
  
  .dbootstrap .messagePanel {  background-color: #292525;  color: #9c9393;  border-top: 1px solid #A57E7E; }
  .dbootstrap .controlPanel { background-image: linear-gradient(to bottom, #000 0, #000 100%); }
  .title-bar {
    padding: 6px 6px 6px 6px;
    background-color: #1e1e1e;
    color: #ccd1d4;
}

}
