css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animekompi.web.id") {

/* ---------------- MENU CSS ---------------- */  
#genretop{margin-bottom:0px}  
  
/* ---------------- HEADER CSS ---------------- */
.recommended{display:none}  

/* ---------------- POSTBODY CSS ---------------- */
.postbody{width:1060px;float:left;line-height:21px;margin-right:18px}
.postbody .box{display:none}
.postbody .box .rld{display:none}  
  
.postbody .boxed .right{width:880px;float:right}  
.postbody .boxed .right .lts ul li .dtl{width:740px;padding:5px;height:70px; background:#F5F5F5; float:left}  
  
/* ---------------- ANIMEINFO CSS ---------------- */
.animeinfo .left{float:left;width:730px;font-size:13px;padding-right:5px;margin-right:5px;overflow:hidden;border-right:1px solid #D4D4D4}  
  
.iklanxxx {margin-top:15px;}
  
.wprc-container .wprc-content {right: 000px}  
    
.animeinfos .episodelist .offzone{overflow:hidden;font-size:11px;margin-bottom:1px}
.animeinfos .episodelist .offzone .leftoff{line-height:30px;width:55px}
.animeinfos .episodelist .offzone .lefttitle{line-height:30px;width:345px}
.animeinfos .episodelist .offzone .rightoff{line-height:30px;width:345px}
.animeinfos .episodelist .offzone .watch{line-height:30px;width:270px}  
  
.animeinfos .episodelist ul li .leftoff{width:64px}
.animeinfos .episodelist ul li .lefttitle{width:354px}
.animeinfos .episodelist ul li .rightoff{width:349px}
.animeinfos .episodelist ul li .watch{width:270px}
  
/* ---------------- ANIMELIST CSS ---------------- */
.animelist .nav_apb a{width:3.65%;margin:0 1px}  
.movlist ul li{width:33%;float:left}  
  
/* ---------------- SIDEBAR CSS ---------------- */
#sidebar{display:none;float:right;width:300px}    
}
