@-moz-document url-prefix(http://www.komar.org/cgi-bin/christmas_webcam) {
 * {
  font: 10pt "Ubuntu", "Droid Sans", "Trebuchet MS", "DejaVu Sans",
             "Bitstream Vera Sans", "Verdana", sans-serif !important;
 }
 a {
  background: transparent !important;
  text-decoration: none !important;
  color: #00A !important;
 }
 a:hover {
  color: #A00 !important;
 }
 h1 {
  margin-top: 1.32em !important;
  font-size: 28px !important;
  text-decoration: none !important;
 }
 h2, #circuit_layout + pre > span:first-child {
  font-size: 22px !important;
  text-decoration: none !important;
 }
 hr {
  border: 1px solid #000 !important;
  height: 2px !important;
  margin: .25em 0 !important;
 }
 body {
  background-attachment: fixed !important;
  background-position: 0 0 !important;
  background-color: #B1DBFF !important;
  /* Original BG color: #B1DBFF */
 }
 body, #webcam_maindiv, #webcam_table1 {
  width: auto !important;
  margin: 0 .5em !important;
 }
 #webcam_maindiv {
  max-width: 1000px !important;
  margin: 0 auto !important;
 }
 #webcam_table1 {
  position: absolute !important; left: 0 !important;
  width: 100% !important;
  margin: 0 !important;
  background-attachment: fixed !important;
  background-position: 0 0 !important;
  text-align: center !important;
 }
 #webcam_table1 td {
  display: inline !important;
  width: auto !important;
  font-size: 0 !important;
 }
 #webcam_table1 td a {
  font-size: 10pt !important;
  line-height: 1.85em !important;
  padding: 0 1.25em !important;
 }
 body, #webcam_table1 {
  background-image: url("data:image/png;base64,") !important;
 }
 #webcam_table1 * {
  font-weight: normal !important;
  font-style: normal !important;
  color: #FFF !important;
 }
 #w3clogo,
 #webcam_size,
 #webcam_size + h1 + div,
 #webcam_size + h1 + div + div + hr + div + ul *,
 #webcam_size + h1 + div + div + hr + div + ul + span,
 #webcam_size + h1 + div + div + hr + div + ul + span + span,
 #webcam_size + h1 + div + div + hr + div + ul + span + span + img,
 img[src$="/komar-santa-homer.gif"],
 img[src$="/komar-santa-homer.gif"]:first-child + span,
 #christmas_music,
 #christmas_music + img,
 #christmas_music + img + script + p + hr + div,
 #christmas_music + img + script + p + hr + div + div,
 #christmas_music + img + script + p + hr + div + div + div,
 #christmas_music + img + script + p + hr + div + div + div + div,
 div[style*="height:22px; background-color:#00B000;"],
 #circuit_layout + pre + * + br + br + h2 + div br + br,
 #jack {
  display: none !important;
 }
 #webcam_size + h1 + div + div,
 #webcam_size + h1 + div + div + hr + div,
 #circuit_layout + pre,
 #circuit_layout + pre + * + br + br + h2 + div {
  margin: .5em 0 !important;
 }
 
 /* Begin 'Put "Free Christmas Music" link and social networking crap on the same line' */
 #webcam_size + h1 + div + div + hr + div + ul + table,
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr + div {
  position: relative; top: -6px !important;
  margin-bottom: -0.5em !important;
 }
 #webcam_size + h1 + div + div + hr + div + ul + table {
  left: -2px !important;
  display: inline-table !important;
 }
 /* Get rid of extra whitespace to the left of "Free Christmas Music" */
 #webcam_size + h1 + div + div + hr + div + ul + table td {
  font-size: 0 !important;
 }
 #webcam_size + h1 + div + div + hr + div + ul + table span {
  font-size: 10pt !important;
 }
 /* Realign the GIF to its right */
 #webcam_size + h1 + div + div + hr + div + ul + table img {
  vertical-align: top !important;
  margin-left: 0.5em !important;
 }
 #webcam_size + h1 + div + div + hr + div + ul + table + script,
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p,
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr {
  display: none !important;
 }
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr + div {
  display: inline-block !important;
  margin-left: 20px !important;
  width: auto !important;
 }
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr + div > table td {
  width: auto !important;
 }
 /* Fix the vertical alignment of the Twitter widget */
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr + div > table td:first-child + td {
  vertical-align: middle !important;
 }
 /* Put some space in between the StumbleUpon and Google+ widgets*/
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr + div > table td:first-child + td + td + td {
  padding-right: 34px !important;
 }
 /* Fix the height of the Facebook widget */
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr + div > table td:first-child + td + td + td + td + td + td + td > iframe {
  height: 24px !important;
 }
 /* More spacing before/after temporary notices underneath the social widgets */
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr + div + div {
  line-height: 2em !important;
 }
 /* ... and put a fake horizontal rule under the social widgets because we removed the one before them */
 #webcam_size + h1 + div + div + hr + div + ul + table + script + p + hr + div + div + div + p {
  margin: 0 0 0.5em !important;
  border-top: 2px solid #000 !important;
 }
 /* End 'Put "Free Christmas Music" link and social networking crap on the same line' */
 
 #christmas_music + img + script + p + hr + div + div + div + div + p table,
 table[summary="Christmas Lights Webcam"] {
  width: auto !important;
  margin: 0 auto !important;
 }
 #christmas_music + img + script + p + hr + div + div + div + div + p table tr td:first-child,
 table[summary="Christmas Lights Webcam"] tr td:first-child {
  width: 500px !important;
  padding-right: .5em !important;
 }
 #christmas_music + img + script + p + hr + div + div + div + div + p table tr td + td,
 table[summary="Christmas Lights Webcam"] tr td + td {
  /*min-width: 500px !important; max-width: 100% !important;
  width: auto !important;*/
  width: 450px !important;
 }
 #webcams {
  display: block !important;
  width: 100% !important;
  height: 540px !important;
  margin-left: 0 !important;
 }
 #weather {
  display: block !important;
  width: 100% !important;
  height: 575px !important;
  overflow: auto !important;
 }
 #im_recent_div + hr + ul {
  margin: .5em 0 .5em -1.25em !important;
 }
 #im_recent_div + hr + ul + a,
 #im_recent_div + hr + ul + a + strong + a + p {
  margin: 1em 0 .5em 0 !important;
 }
 #circuit_layout + pre {
  padding-top: 8px !important;
 }
 #circuit_layout + pre > span:first-child {
  position: relative !important; top: -8px !important;
 }
}

@-moz-document url-prefix(http://www.komar.org/christmas/webcam/) {
 * {
  font: 9pt "Ubuntu", "Droid Sans", "Trebuchet MS", "DejaVu Sans",
            "Bitstream Vera Sans", "Verdana", sans-serif !important;
 }
 body {
  background: transparent !important;
 }
 a {
  background: transparent !important;
  text-decoration: none !important;
  color: #00A !important;
 }
 a:hover {
  color: #A00 !important;
 }
 table {
  padding-right: .5em !important;
 }
 table tr:first-child .light_box_titles * {
  font-weight: bold !important;
 }
 table tr td {
  width: 50% !important;
  padding-bottom: .7em !important;
 }
 table tr td:first-child {
  padding-right: .5em !important;
 }
 table tr td:last-child {
  padding-left: .5em !important;
 }
 table tr:first-child + tr + tr + tr td {
  border-top: 2px solid #000;
  padding-top: 1em !important;
 }
 .td_spacer {
  display: none !important;
 }
 .webcam_image_small {
  display: block !important;
  width: 100% !important; height: auto !important;
 }
 .webcam_box_small {
  width: 100% !important; height: auto !important;
  border-style: none !important;
 }
 .webcam_box_small > div {
  margin: 0 0 10px 0 !important;
 }
 .webcam_box_small > div > p {
  margin-top: 14px !important;
 }
 .donate_text {
  width: 200px !important;
 }
 .light_box_titles, .light_box_titles * {
  font-size: 12px !important;
  font-weight: normal !important;
  padding-bottom: .5em !important;
 }
 td.light_box_above {
  display: none !important;
  padding-top: .25em !important;
  font-size: 16px !important;
 }
 .webcam_image_large {
  width: 100% !important; height: auto !important;
  margin: .5em 0;
  border-style: none !important;
 }
 .light_box_above {
  font-size: 12px !important;
  text-decoration: none !important;
 }
}

