css:
@-moz-document domain("myfitnesspal.com") {
/* Header */

#logo a,
.header .logo a,
div.water-counter p a,
.top-menu .social-icons {
	-moz-filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale");
	-o-filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale");
	-webkit-filter: grayscale(100%);
	filter: gray;
	filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale");
}
#header #nav-bg,
.header .menu,
#wrap > #content > div > div > div > div.top-nav,
#wrap > #content > div > div > div > div.sub-nav,
.top-nav,
.top-nav > div,
div[class^="layouts--site-navigation"] > div.top-nav {
	background-color: #333 !important;
}
#header #subNav-bg,
.header .sub-menu,
.sub-nav > .ember-view,
.ember-view .top-nav a.active,
.ember-view .top-nav a:hover,
#header #nav .nav_button:hover,
#header #nav .nav_button.active,
.layouts--site-navigation-69bce4e8 .sub-nav,
.layouts--site-navigation-69bce4e8 .top-nav a.active,
.layouts--site-navigation-69bce4e8 .top-nav a:hover,
.header .menu .nav .active > a,
.header .menu .nav > li > a:hover,
.header .menu .nav > li > a:focus {
	background: #777;
}
.layouts--site-navigation-69bce4e8 ul a,
.layouts--site-navigation-69bce4e8 ul a {
	color: #e6e6e6;
}
.layouts--site-navigation-69bce4e8 ul a:hover,
.layouts--site-navigation-69bce4e8 ul a:hover {
	color: #fff;
}
/* Links */

a,
a:link,
a:visited,
.expensive-queries--popular-forum-topics-4d5a6f92 ul li a,
.activity--timeline-card--entry-actions--like-button-109a110b.user-liked,
body#excercise-diary table.table0 tbody tr.bottom td a,
body#food-diary table.table0 tbody tr.bottom td a,
body#yourTicker div#content div.block-1 div.col-1 ul.list-1 li a,
#footer .widget_nav_menu a,
.sidebar .widget_nav_menu a,
#text-2 a,
.post_detail a,
ul#menu-mfp-footer-menu-1 a,
ul#menu-mfp-footer-menu a,
ul#friends-list a {
	color: #e92f4a;
}
#footer .widget_nav_menu a,
.sidebar .widget_nav_menu a,
#text-2 a,
.post_detail a,
ul#menu-mfp-footer-menu-1 a,
ul#menu-mfp-footer-menu a,
ul#friends-list a:hover {
	color: #e92f4a !important;
}
a:hover,
.expensive-queries--popular-forum-topics-4d5a6f92 ul li a:hover,
.activity--timeline-card--entry-actions--like-button-109a110b.user-liked:hover,
body#excercise-diary table.table0 tbody tr.bottom td a:hover,
body#food-diary table.table0 tbody tr.bottom td a:hover,
body#yourTicker div#content div.block-1 div.col-1 ul.list-1 li a:hover,
#footer .widget_nav_menu a:hover,
.sidebar .widget_nav_menu a:hover,
#text-2 a:hover,
.post_detail a:hover,
ul#menu-mfp-footer-menu-1 a:hover,
ul#menu-mfp-footer-menu a:hover {
	color: #FF4762;
}
#footer .widget_nav_menu a:hover,
.sidebar .widget_nav_menu a:hover,
#text-2 a:hover,
.post_detail a:hover,
ul#menu-mfp-footer-menu-1 a:hover,
ul#menu-mfp-footer-menu a:hover {
	color: #FF4762 !important;
}
/* Buttons */

a.btn,
a.button,
input.button,
span.button,
.btn-primary,
.button.gray,
.app-block .app-cta,
.Button,
.NavButton,
.btn-primary-old,
.layouts--diary-display-f3f9aa0a .mfp-button.default.green,
.async-button.mfp-button.green,
a.button.blue,
input.button.blue {
	background: #e92f4a;
	border: none;
	border-radius: 5px;
	color: #fff !important;
}
a.btn:hover,
a.button:hover,
input.button:hover,
span.button:hover,
.btn-primary:hover,
.button.gray:hover,
.Button:hover,
.NavButton:hover,
.Button:focus,
.NavButton:focus,
.Button:active,
.NavButton:active,
.btn-primary-old:hover,
.layouts--diary-display-f3f9aa0a .mfp-button.default.green:hover,
.async-button.mfp-button.green:hover,
a.button.blue:hover,
input.button.blue:hover {
	background: #FF4762 !important;
	border: none;
	-webkit-box-shadow: none;
}
.btn-primary:hover,
.button.gray:hover,
a.button.style-1 span.full-report,
a.btn:hover,
.Button:hover,
.NavButton:hover,
.Button:focus,
.NavButton:focus,
.Button:active,
.NavButton:active {
	color: #fff !important;
}
.btn-default,
.step-tracker-widget-2da2da62 > li .mfp-button.pending,
a.button.style-2,
input.button.style-2,
a.button2,
input.button2,
.button.gray {
	background: #FFF;
	border: 1px solid #e92f4a;
	color: #e92f4a !important;
}
.btn-default:hover,
.step-tracker-widget-2da2da62 > li .mfp-button.pending:hover,
a.button.style-2:hover,
input.button.style-2:hover,
a.button2:hover,
input.button2:hover,
.button.gray:hover {
	background: #FFF;
	border: 1px solid #FF4762 !important;
	color: #FF4762 !important;
	-webkit-box-shadow: none;
}
.activity--timeline-card--entry-comments--comment-viewer-6ddebe7f .comment-actions .likes-count {
	color: #e92f4a;
}
/* Blue Text */

h1,
h2,
h3,
h4,
h5,
h6,
.main-title,
.main-title-2,
div#profile.block-1 div.col-2 h3,
.secondary-title,
body#excercise-diary table.table0 thead tr td.first,
body#food-diary table.table0 tbody tr.meal_header td.first,
#reports-menu label {
	color: #555 !important;
}
.expensive-queries--widget-6b6fb8b6 header h1 a,
.expensive-queries--popular-forum-topics-4d5a6f92 ul,
ul.tabbed li.active a,
ul.tabbed li:hover a {
	color: #555 !important;
}  
.layout-panel-6b313e40 header h2 {
	color: #fff !important;
}
/* Blue Boxes */

.layout-panel-6b313e40 header,
div.bar,
ul.tabbed li,
.input-group .row.header,
table.table0 thead tr td,
#sidebar table#side-menu tr td.active,
body.layout-mail #main table.table0 tfoot td,
body.layout-mail #main table.table0 tfoot td {
	background-color: #555;
}
.DataTable thead td,
.CategoryGroup .H,
.Group-Discussions thead td,
.Section-DiscussionList.Groups .content .DataTableWrap thead td,
.group-menu-title,
.Section-GroupList .content h2,
.Section-GroupList.browse .content h1,
.Section-CategoryList .Groups.H,
.apps-sidebar .categories ul li.active,
.apps-sidebar .categories ul li:hover {
	background-color: #555 !important;
	color: #fff !important;
}
ul.tabbed li.active,
ul.tabbed li:hover {
	border: 1px solid #555;
	border-bottom: none;
}
/* Date Controls */

#date_controls .date,
body#excercise-diary table.table0 thead tr td,
body#food-diary table.table0 tfoot tr td,
body#food-diary table.table0 tbody tr.meal_header td.alt.nutrient-column {
	background: #555;
}
#date_controls .date .prev:hover, #date_controls .date .next:hover {
	color: #FF4762 !important;
}
/* Form Elements */

input.text,
select.select,
textarea.textarea {
	background: white;
	border: 1px solid #ddd;
	padding: 8px 3px;
}
input.text {
	padding: 8px 7px;
}
/* Other */

.UpsellContent h1 .copyblock {
	color: #fff !important;
}
}
