css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.twitch.tv") {
#main_col,
#channel *:not(a),
#main_col,
#channel *:not(inputs),
#main_col,
#channel *:not(textarea),
.upsell-banner {
    color: #ffffff;
}
.player-column .close-hostmode a.hide-hostmode:before,
.player-column .close-hostmode a.hide-hostmode::after {
    color: #ffffff;
    border-color: transparent transparent rgba(255, 255, 255, 0.35);
}
.player-column .close-hostmode a.show-hostmode:before,
.player-column .close-hostmode a.show-hostmode::after {
    color: #ffffff;
    border-color: rgba(255, 255, 255, 0.35) transparent transparent;
}
#main_col,
#channel,
.upsell-banner,
.player-column,
.player-column .brick,
.player-column .close-hostmode {
    background: #17141f none repeat scroll 0 0;
    border: none 0px;
}
.activity-card__comments,
.metadata-box,
#channel inputs,
#channel textarea {
    background: rgba(32, 28, 43, 1) none repeat scroll 0 0;
    border: solid 1px rgba(44, 37, 65, 1) !important;
}
#left_col {
    border-right: 1px solid rgba(0, 0, 0, 1);
}
#main_col {
    border: 0px none transparent !important;
}
#right-column {
    border: 0px none transparent !important;
}
.rightcol-content .tab-container .chat-container,
.rightcol-content .tab-container .panels-contain,
.rightcol-content .top,
.rightcol-content #right_nav,
.rightcol-content .top .selected a,
.rightcol-content #right_nav .selected a {
    background: rgba(23, 20, 31, 1) none repeat scroll 0 0;
    color: #ffffff !important;
    border-left: 1px solid rgba(0, 0, 0, 1);
}
.rightcol-content .top,
.rightcol-content #right_nav {
    border-bottom: 1px solid rgba(0, 0, 0, 1);
}
.rightcol-content .top li:not(.selected),
.rightcol-content #right_nav li:not(.selected) a {
    border-bottom: solid 1px rgba(44, 37, 65, 1);
}
.rightcol-content .top li:not(.selected) a:hover,
.rightcol-content #right_nav li:not(.selected) a:hover {
    border-bottom: solid 1px rgba(44, 37, 65, 1);
    background: rgba(32, 28, 43, 1) none repeat scroll 0 0;
    color: #ffffff !important;
}
.tse-content .directory_header .nav .active,
.tse-content .directory_header .nav a:hover {
    color: #ffffff !important;
    border-bottom: solid 1px rgba(44, 37, 65, 1) !important;
    ;
}
ul.tabs::before,
.directory_header .nav::before,
ul.tabs_fake::before {
    border-top: solid 1px rgba(44, 37, 65, 1);
}
.cl-container .section-header .header-content,
.cl-container .cl-subsection,
.cl-container .cl-subheader {
    background: rgba(32, 28, 43, 1) none repeat scroll 0 0 !important;
    ;
}
.cl-container .section-header,
.cl-container .cl-subsection fieldset,
.cl-container .cl-subsection .input {
    border-bottom: solid 1px rgba(44, 37, 65, 1);
}
.cl-subsection div.input fieldset .input {
    border: 0px transparent !important;
}
.rightcol-content .chat-menu,
.rightcol-content .emoticon-selector-box .emote-set,
.rightcol-content .emoticon-selector-box .pd-t-1 {
    color: rgba(255, 255, 255, 0.35) !important;
    background: rgba(32, 28, 43, 1) none repeat scroll 0 0;
}
.rightcol-content .tse-content .js-panel-content * {
    color: #ffffff !important;
}
#main_col .drop::after {
    border-color: rgba(255, 255, 255, 0.35) transparent transparent;
}
#main_col .follow-button {
    border: solid 1px rgba(44, 37, 65, 1);
    border-right: 0px transparent !important;
    border-top: 0px transparent !important;
}
#main_col .notification-controls a.toggle-notification-menu {
    background: rgba(32, 28, 43, 1) none repeat scroll 0 0;
    border: solid 1px rgba(44, 37, 65, 1);
    border-left-color: transparent !important;
}
#main_col .notification-controls a.toggle-notification-menu::before {
    border-color: rgba(255, 255, 255, 0.5) transparent transparent;
}
#main_col .notification-controls .dropmenu {
    color: #ffffff !important;
    background: rgba(32, 28, 43, 1) none repeat scroll 0 0;
    border: solid 1px rgba(44, 37, 65, 1);
}
#main_col .follow-button button {
    border: solid 1px rgba(44, 37, 65, 1);
    border-right-color: transparent !important;
}
#main_col .channel-stats {
    background: rgba(255, 255, 255, 0.35);
}
.ember-view .activity-card {
    border-color: rgba(44, 37, 65, 1);
}
.ember-view .tabbed-split__panel ul.tabs li.selected a {
    box-shadow: 0 -1px 0 rgba(44, 37, 65, 1) inset;
    color: #ffffff;
}
.ember-view .tabbed-split__panel ul.tabs::before {
    border-color: rgba(44, 37, 65, 1);
}
.ember-view .activity-card .activity-card__status .activity-meta-divider::before {
    margin-top: 0px !important;
    background: rgba(23, 20, 31, 1) none repeat scroll 0 0;
    border-color: rgba(44, 37, 65, 1);
}
.ember-view .activity-card .activity-card__status .activity-meta-divider {
    box-shadow: 0 -1px 0 rgba(44, 37, 65, 1) inset;
}
.ember-view .activity-card .activity-card__status {
    background: rgba(23, 20, 31, 1) none repeat scroll 0 0;
}
.ember-view .activity-card .activity-card__status .activity-body {
    border-color: rgba(44, 37, 65, 1);
    margin-top: 0px !important;
}
.ember-view .activity-card .activity-card__status .activity-react .activity-button {
    background: rgba(32, 28, 43, 1) none repeat scroll 0 0;
    border-color: rgba(44, 37, 65, 1);
}
.ember-view .cn-bar {
    background: rgba(32, 28, 43, 1) none repeat scroll 0 0;
    color: #FFFFFF;
}
.ember-view .cn-bar a span,
.ember-view .cn-bar .cn-tabs__profile {
    color: #FFFFFF;
}
.cn-tabs__item--withseparator::after {
    border-right: 1px solid rgba(44, 37, 65, 1);
}
.cn-bar {
    box-shadow: 0 -1px 0 rgba(44, 37, 65, 1) inset, 0 -1px 1px rgba(0, 0, 0, 0.067);
}
}
