css:
@-moz-document domain("soundcloud.com") {
a {
    color: #ca9149!important;
}
a:hover {
    color: #fff!important;
}
.header {
    background-color: #413d3b !important;
}
.header__logo {
    margin-top: 0px !important;
    background: #d48a4d!important;
    border-bottom: 2px solid #b06f38!important;
}
.header__logoLink:focus {
    background: transparent url(https://a-v2.sndcdn.com/assets/images/header/cloud-e365a4.png) no-repeat 50%!important;
}
.g-dark textarea,
.g-dark select,
.g-dark input[type="text"],
.g-dark input[type="password"],
.g-dark input[type="search"],
.commentForm__input {
    color: #a29d99!important;
    background: #524d4a!important;
}
.g-dark a,
.g-dark .sc-type-light {
    color: #9b9389!important;
    font-weight: bold!important;
}
.header__navMenu>li>a.selected,
.header>li>a:focus {
    color: #ca9149!important;
    background-color: #363230!important;
    font-weight: bold!important;
}
#content,
body {
    background-color: #363230 !important;
}
.header__navMenu>li>a {
    border-color: transparent!important;
}
.playbackSoundBadge__context,
.playbackSoundBadge__title {
    color: #f2e8de!important;
}
.g-tabs,
.sc-border-light-right,
.sc-border-light-bottom,
.sc-border-light-top,
.sc-border-light,
.chartsMain_listHeader,
.collection.m-overview .collection__section:not(:last-child),
.audibleBadgePlaceholder:before,
.listenEngagement,
.l-listen-wrapper .l-about-rows,
.searchTitle__text,
.searchPremiumContentFooter {
    border-color: rgba(121, 113, 104, .4)!important;
}
.listenEngagement {
    box-shadow: none!important;
}
.l-footer,
.sound__soundActions {
    background-color: transparent!important;
    border-color: transparent!important;
}
.commentForm__wrapper {
    background-color: #413d3b !important;
    border-color: transparent!important;
}
.commentForm__input {
    border-color: transparent!important;
}
.sc-text {
    color: #fff!important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 ! important;
    box-shadow: none ! important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #d48a4d ! important;
}
*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #4c4947 ! important;
}
*::-webkit-scrollbar-button {
    display: none ! important;
}
.g-tabs-link.active {
    color: #fff!important;
    border-bottom: 2px solid #d48a4d!important;
}
.sc-background-light,
.readMoreBadge__countWrapper,
.uploadMain__chooserContainer,
.uploadMain__foot,
.g-upsell-container,
.stats__expand {
    background-color: #504b46!important;
    border: 1px solid #5f5b57!important;
}
.sc-button {
    color: #f6f2ec!important;
    background-color: #d48a4d!important;
    border-color: transparent!important;
}
.playbackTimeline__duration,
.playbackTimeline__timePassed {
    color: #d48a4d!important;
}
.playControls__icon {
    -webkit-filter: brightness(500%)!important;
    filter: brightness(500%)!important;
}
.repeatControl,
.volume__speakerIcon {
    -webkit-filter: brightness(300%)!important;
    filter: brightness(300%)!important;
}
.playbackTimeline__progressBackground {
    height: 3px!important;
    background-color: #20242b!important;
}
.playbackTimeline__progressBar {
    height: 3px!important;
    background-color: #d48a4d!important;
}
.playbackTimeline__progressHandle {
    border-color: #d48a4d!important;
    background-color: #d48a4d!important;
}
.volume__sliderWrapper {
    background-color: #363230!important;
}
.volume__sliderWrapper:after {
    border-color: transparent transparent #363230 #363230!important;
}
.volume__sliderWrapper:before {
    border-color: transparent transparent #363230 #363230!important;
}
.volume__sliderBackground {
    background-color: #20242b!important;
}
.volume__sliderProgress {
    background-color: #d48a4d!important;
}
.volume__sliderHandle {
    border-color: #d48a4d!important;
    background-color: #d48a4d!important;
}
.g-box-full {
    -webkit-filter: hue-rotate(15deg) sepia(50%)!important;
    filter: hue-rotate(15deg) sepia(50%)!important;
}
.compactTrackListItem__number,
.compactTrackListItem__trackTitle {
    color: #d2d2d2!important;
}
.compactTrackListItem.clickToPlay.active,
.compactTrackListItem.clickToPlay:hover,
.compactTrackListItem.clickToPlay:focus {
    background-color: #d48a4d!important;
}
.compactTrackListItem.clickToPlay.active .compactTrackListItem__additional,
.compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional,
.compactTrackListItem.clickToPlay:focus .compactTrackListItem__additional {
    background-color: transparent!important;
}
.sc-orange {
    color: #d48a4d!important;
}
.sc-button-small.sc-button-selected.sc-button-like:before,
.sc-button-small.sc-button-selected.sc-button-like.sc-button-lightfg:before,
.sc-button-small.sc-button-selected.sc-button-like.sc-button-visual:before,
.sc-button-small.sc-button-selected.sc-button-repost:before,
.sc-button-small.sc-button-selected.sc-button-repost.sc-button-lightfg:before,
.sc-button-small.sc-button-selected.sc-button-repost.sc-button-visual:before {
    -webkit-filter: brightness(0%) invert(100%)!important;
    filter: brightness(0%) invert(100%)!important;
}
.statsBadge__upsell-headline {
    color: #ababab!important;
}
.sc-tag,
.sc-tag:visited {
    background: #d48a4d;
    border-color: #c1824d;
    color: #fff!important;
}
::selection {
    background: #cc9e78!important;
    color: #ffffff!important;
}
::-moz-selection {
    background: #cc9e78!important;
    color: #ffffff!important;
}
.image__whiteOutline .image__full {
    width: 40px!important;
    height: 40px!important;
}
.usersList__item {
    width: inherit !important;
    height: inherit !important;
}
.dropbar__content {
    background-color: #413d3b!important;
    border-bottom: 2px solid #2f2b29!important;
}
.userDropbar__title {
    color: #9b9389!important;
}
.chartTrack.m-playing,
.chartTrack:hover {
    background-color: #565351;
}
.chartTrack__position {
    color: #7d7d7d!important;
}
.linkMenu__activeItem,
.linkMenu__item:hover,
.linkMenu__item>a:hover {
    color: #222!important;
}
.sc-toggle:before {
    background: #d48a4d!important;
}
.premiumContent__becomeProInfo,
.premiumContent__infoQuote,
.premiumContent__twoColumnBlock,
.premiumContent__becomeProQuote,
.premiumContent__footnotes,
.uploadMain__chooserContainer,
.uploadMain__foot,
.emptyNetworkPage__headline {
    color: #e8e8e8!important;
}
.l-fixed-top-one-column>.l-top {
    background: #413d3b!important;
}
.soundBadge__additional {
    background: linear-gradient(to right, rgb(54, 50, 48), #413d3b 17px)!important;
}
.sc-ministats-small.sc-ministats-sounds:before {
    -webkit-filter: invert(100%)!important;
    filter: invert(100%)!important;
}
.searchTitle {
    background-color: rgba(54, 50, 48, .95)!important;
}
.searchTitle__textContent {
    color: #9b9389!important;
}
.searchOptions__navigationItem.active {
    background: #524d4a!important;
}
.searchOptions__navigationItem.active:after {
    border-left-color: #524d4a!important;
}
.sc-snippet-badge-grey {
    color: #989898!important;
    border-color: #504b46!important;
}
.searchOptions__filtersTitle,
.searchTags__title {
    color: #929292!important;
}
.l-footer,
.sound__soundActions {
    background-color: #363230!important;
}
.truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after,
.truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after {
    background: linear-gradient(rgba(255, 255, 255, 0), #413d3b)!important;
}
.playControls__inner {
    background-color: #413d3b!important;
    border-top: 2px solid rgba(42, 38, 36, .7)!important;
}
.volume.expanded .volume__sliderWrapper {
    border-color: rgba(0, 0, 0, .2)!important;
}
.newItemBadge.newItems__some {
    background: #d48a4d!important;
    border-color: #413d3b!important;
}
.commentItem__body p {
    color: #b7afa5!important;
}
.commentItem:hover .commentItem__controls {
    background: none!important;
}
.modal.modalWhiteout {
    background-color: rgba(65, 61, 59, .5)!important;
}
.modal__modal {
    background: #363230!important;
}
.g-modal-title-h1 {
    border-color: rgba(0, 0, 0, 0.2)!important;
}
.recipientChooser .tokenInput__wrapper,
.composeTextfield .textfield__input {
    background-color: #9a683f!important;
    border-color: rgba(0, 0, 0, .2)!important;
}
.composeTextfield .textfield__input {
    color: #d4d2d0!important;
}
.composeMessage__bottomWrapper {
    border-color: #8e5e36!important;
    background-color: #aa6f3e!important;
}
div.truncatedUserDescription__content,
.spotlight__recent,
.spotlightTitle,
.statsOverview__title,
.userStatsLegend__item:last-child,
.statsTopList__header,
.sc-type-large {
    color: #b5afa9!important;
}
.stats__xAxis text {
    fill: #b5afa9!important;
}
.playlistTrackCount {
    background-color: rgba(212, 138, 77, .7)!important;
}
.g-upsell-container-content {
    color: #bfb7ad!important;
}
.sc-icon-large.sc-stats-icon {
    -webkit-filter: invert(90%)!important;
    filter: invert(90%)!important;
}
.commentPopover__avatar {
    width: 10px !important;
    height: 10px !important;
}
.g-nav-item.active>.g-nav-link {
    text-shadow: 0 1px 0 #825836!important;
}
.searchTitle__text {
    color: #9b9389!important;
}
.playbackSoundBadge__titleContextContainer {
    background-color: transparent!important;
}
.commentPopover.darkText .commentPopover__body {
    color: #d48a4d !important;
    opacity: 0.9
}
}
