css:
@-moz-document url-prefix("http://www.ohmydollz.com/forum/") {

#contenu {
    background: url("http://data.nimages.fr/4tyt.png") no-repeat fixed 50% 0 rgba(0, 0, 0, 0);
    height: 100%;
    margin: 0;
    padding: 3px 0;
    width: 100%;
}
/*HEADER*/

#head_forum {
    background-image: url("http://data.nimages.fr/010tgx.png");
    background-repeat: no-repeat;
    height: 300px;
    position: relative;
    width: 950px;
}
#head_links {
    bottom: 146px;
    left: 595px;
    position: absolute;
}
#head_links a {
    color: #FFFFFF;
    font-size: 14px;
    text-decoration: none;
}
#head_links a:hover {
    color: #b54646;
}
#brdmenu {
    background-image: url("http://data.nimages.fr/08gdl.png");
    background-repeat: no-repeat;
    height: 30px;
}
#footer_box {
    background-image: url("http://data.nimages.fr/021dxy.png");
    background-repeat: no-repeat;
    height: 100px;
    width: 950px;
}
#brdwelcome {
    background-image: url("http://data.nimages.fr/voter7zm.png");
    background-repeat: no-repeat;
    color: #FFF;
    font-weight: bold;
    height: 60px;
    padding: 10px;
    width: 950px;
}
.pun H2,
#brdmenu {
    background-image: url("http://data.nimages.fr/08gdl.png");
    background-repeat: no-repeat;
}
.pun {
    color: #f52f2f;
}
DIV.box,.pun BLOCKQUOTE,DIV.codebox,#adminconsole FIELDSET TH,.rowodd,.roweven {
    background-color: #fbe7c0;
}
.pun BLOCKQUOTE {
    border: 1px solid #87B170;
}
.box {
    border: 1px solid #CD1414;
}
.pun A:link,
.pun A:visited {
    color: #D8A716;
}
.pun A:hover {
    color: #B40D0D;
}
DIV.block DIV.inbox,
DIV.blockmenu DIV.inbox {
    padding: 0 6px;
    color: #CD1313;
}
.pun TH {
    background-image: url("http://data.nimages.fr/015oki.png");
    background-repeat: no-repeat;
    color: #CD1414;
    font-size: 13px;
    height: 100%;
    text-align: center;
}
.inbox tr {
    border: 1px solid #F6F2EE;
}
pun .tcr .byuser {
    color: #837062;
}
TD.tc2, TD.tc3, TD.tcmod, #postpreview, #viewprofile DD, DIV.forminfo, #adminconsole FIELDSET TD, DIV.blockmenu DIV.box, #adstats DD {
    background-color: #FBE7C0;
}
TD DIV.tclcon {
    color: #D41717;
    float: left;
    margin-left: 10px;
    width: 475px;
}
.pun .tcr .byuser {
    color: #BD0813;
}
.pun .tcr {
    border-right: 1px solid #BFAA9B;
}
DIV.postleft,
DIV.postfootleft,
DIV.blockpost LABEL {
    background-color: #C41111;
}

DIV.blockpost DIV.box {
    background-color: #FFF7E9;
}

DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {
    background-color: #C41111;
    color: #F5BA13;
}

DIV.postright, DIV.postfootright {
    background-color: #FFF7E9;
}

DIV.postright, DIV.postfootright {
    border-left: 18em solid #C41111;
}


DIV.postleft DT,
DIV.postmsg H4,
TD.tcl H3,
DIV.forminfo H3,
P.postlink,
DIV.linkst LI,
DIV.linksb LI,
DIV.postlinksb LI,
.blockmenu LI,
#brdtitle H1,
.pun SPAN.warntext,
.pun P.warntext {
    font-weight: bold;
    color: #31795E;
}

DIV.icon {
    background-image: url("http://data.nimages.fr/offirrk34.png") !important;
}
DIV.inew {
    background-image: url("http://data.nimages.fr/offirrsrg.png") !important;
}
DIV.blockpost H2 SPAN.conr {
    color: #52453C;
    text-align: right;
}
.pun H2 A:link,
.pun H2 A:visited {
    color: #115015;
}
DIV.linkst .conl,
DIV.linksb .conl,
DIV.postlinksb .conl {
    width: 18em;
    color: #8E6D6A;
}

}
