css:
@-moz-document domain("breeze.dreamstalk.ca") {
input[type="button"], input[type="submit"] {
    font-family: 'Source Sans Pro', sans;
    font-size: 18px;
    font-weight: 400;
    background-color: #ee862e;
    color: #fff;
    padding: 4px 10px;
    border: 0;
    border-radius: 2px;
    margin-top: 10px;
    cursor: pointer;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.2);
}

/* MAIN */
#top {
    box-shadow: 0px 1px 2px rgba(0,0,0,0.2);
}

/* APPS */
.apps .app img {
    transition: all 0.2s;
}
.apps .app:hover img {
    animation-name: none;
    animation-duration: 0s;
    transform: rotateX(15deg);
    transform-origin: 50% -20%;
    filter: drop-shadow(0px 4px 3px rgba(0,0,0,0.3));
}
.apps .text {
    font-size: 18px;
    font-weight: 400;
}

/* Hide the delete buttons */
input[value="Delete"] {
    display: none;
}

/* Make the selected resource visible */
.templates {
    position: relative;
    z-index: 3;
    border-radius: 0;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.2);
}
.layouts {
    position: relative;
    z-index: 2;
    margin: 0;
    border-radius: 0;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.2);
}
.resources {
    position: relative;
    z-index: 1;
    margin: 0;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.2);
}
.design-trail ul {
    padding: 0;
}
.design-trail li {
    display: inline-block;
    padding: 0;
}
.design-trail li.php,
.design-trail li.stylesheet,
.design-trail li.javascript {
    clear: both;
}
.design-trail li.php+.php,
.design-trail li.stylesheet+.stylesheet,
.design-trail li.javascript+.javascript {
    clear: none;
}

.design-trail a {
    display: inline-block;
    border-radius: 2px;
    margin: 2px 5px;
    padding: 3px 10px;
    transition: all 0.15s;
}
.design-trail a:hover {
    text-decoration: none;
    background-color: rgba(0,0,0,0.05);
    box-shadow: 0px 1px 2px rgba(0,0,0,0.2);
}
.design-trail .selected {
    text-decoration: none;
}
.design-trail .selected a {
    text-decoration: none;
    color: #fff;
    background-color: #353535;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.2);
}
}
