css:
@-moz-document domain("workflowy.com") {

.contentTag[title^="Filter #today"] {
  background-color: #FFC800 !important;
  color: black;
}

.contentTag[title^="Filter #today"]::after {
  content: " \25b6";
  position: relative;
  top: -0.15em;
}

.contentTag[title^="Filter #tomorrow"] {
  background-color: #FFE800 !important;
  color: black;
}

.contentTag[title^="Filter #tomorrow"]::after {
  content: " \23e9";
  position: relative;
  top: -0.15em;
}

.contentTag[title^="Filter #star"] {
  /*background-color: #FFC800 !important;*/
  /*color: black;*/
}

.contentTag[title^="Filter #star"]::after {
  content: " \2605";
  position: relative;
}

.contentTag[title^="Filter #dom-"],
.contentTag[title^="Filter #doy-"] {
  background-color: #FFE800 !important;
  color: black;
}

.contentTag[title^="Filter #dom-"]::after,
.contentTag[title^="Filter #doy-"]::after {
  content: " \21bb";
  position: relative;
  /*top: -0.15em;*/
}

.contentTag[title^="Filter #weekday"],
.contentTag[title^="Filter #weekend"],
.contentTag[title^="Filter #sunday"],
.contentTag[title^="Filter #monday"],
.contentTag[title^="Filter #tuesday"],
.contentTag[title^="Filter #wednesday"],
.contentTag[title^="Filter #thursday"],
.contentTag[title^="Filter #friday"],
.contentTag[title^="Filter #saturday"] {
  background-color: #D6E800 !important;
  color: black;
}

.contentTag[title^="Filter #weekday"]::after,
.contentTag[title^="Filter #weekend"]::after,
.contentTag[title^="Filter #sunday"]::after,
.contentTag[title^="Filter #monday"]::after,
.contentTag[title^="Filter #tuesday"]::after,
.contentTag[title^="Filter #wednesday"]::after,
.contentTag[title^="Filter #thursday"]::after,
.contentTag[title^="Filter #friday"]::after,
.contentTag[title^="Filter #saturday"]::after {
  content: " \1f4c6";
  position: relative;
  top: -0.05em;
  font-weight: normal;
}

.contentTag[title^="Filter #weekdays"]::after,
.contentTag[title^="Filter #weekends"]::after,
.contentTag[title^="Filter #sundays"]::after,
.contentTag[title^="Filter #mondays"]::after,
.contentTag[title^="Filter #tuesdays"]::after,
.contentTag[title^="Filter #wednesdays"]::after,
.contentTag[title^="Filter #thursdays"]::after,
.contentTag[title^="Filter #fridays"]::after,
.contentTag[title^="Filter #saturdays"]::after {
  content: " \1f4c6  \21bb";
  position: relative;
  top: -0.05em;
  font-weight: normal;
}

.contentTag[title^="Filter #201"], .contentTag[title^="Filter #202"], .contentTag[title^="Filter #203"] {
  background-color: #B2E800 !important;
  color: black;
}
.contentTag[title^="Filter #201"]::after,
.contentTag[title^="Filter #202"]::after,
.contentTag[title^="Filter #203"]::after {
  content: " \1f4c5";
  position: relative;
  top: -0.05em;
  font-weight: normal;
}

/* .contentTag[title^="Filter #every0"], */
.contentTag[title^="Filter #everylast"],
.contentTag[title^="Filter #first"],
.contentTag[title^="Filter #second"],
.contentTag[title^="Filter #third"],
.contentTag[title^="Filter #fourth"],
.contentTag[title^="Filter #last"],
.contentTag[title^="Filter #every0"],
.contentTag[title^="Filter #every1"],
.contentTag[title^="Filter #every2"],
.contentTag[title^="Filter #every3"],
.contentTag[title^="Filter #every4"],
.contentTag[title^="Filter #every5"],
.contentTag[title^="Filter #every6"],
.contentTag[title^="Filter #every7"],
.contentTag[title^="Filter #every8"],
.contentTag[title^="Filter #every9"] {
  background-color: #5f5 !important;
  color: black;
}

.contentTag[title^="Filter #first"]::after,
.contentTag[title^="Filter #second"]::after,
.contentTag[title^="Filter #third"]::after,
.contentTag[title^="Filter #fourth"]::after,
.contentTag[title^="Filter #last"]::after {
  content: " \1f4c5";
  position: relative;
  top: -0.05em;
  font-weight: normal;
}
.contentTag[title^="Filter #first"][title$="days"]::after,
.contentTag[title^="Filter #second"][title$="days"]::after,
.contentTag[title^="Filter #third"][title$="days"]::after,
.contentTag[title^="Filter #fourth"][title$="days"]::after,
.contentTag[title^="Filter #last"][title$="days"]::after,
.contentTag[title^="Filter #every0"]::after,
.contentTag[title^="Filter #every1"]::after,
.contentTag[title^="Filter #every2"]::after,
.contentTag[title^="Filter #every3"]::after,
.contentTag[title^="Filter #every4"]::after,
.contentTag[title^="Filter #every5"]::after,
.contentTag[title^="Filter #every6"]::after,
.contentTag[title^="Filter #every7"]::after,
.contentTag[title^="Filter #every8"]::after,
.contentTag[title^="Filter #every9"]::after {
  content: " \1f4c5  \21bb";
  position: relative;
  top: -0.05em;
  font-weight: normal;
}

.contentTag[title^="Filter #everyyear-"] {
  background-color: #0fa !important;
  color: black;
}
/* .contentTag[title^="Filter #every0"]::after, */
.contentTag[title^="Filter #everyyear-"]::after{
  content: " \1f4c5  \21bb";
  position: relative;
  top: -0.05em;
  font-weight: normal;
}

.contentTag[title^="Filter #star"],
.contentTag[title^="Filter #repeat"],
.contentTag[title^="Filter #nolink"] {
  color: #eee;
  background-color: #444;
}
.contentTag[title^="Filter #repeat"] {
  color: #ff0;
}

.contentTag[title^="Filter #repeat"]::after {
  content: " \21bb";
  position: relative;
}

.contentTag[title^="Filter #firstchild"],
.contentTag[title^="Filter #lastchild"],
.contentTag[title^="Filter #randomchild"] {
  background-color: #c9f !important;
  color: black;
}
.contentTag[title^="Filter #firstchild"] {
  background-image: linear-gradient(to bottom, #96f 0%, #c9f 10%, #edf 100%);
}
.contentTag[title^="Filter #lastchild"] {
  background-image: linear-gradient(to top, #96f 0%, #c9f 10%, #edf 100%);
}
.contentTag[title^="Filter #randomchild"] {
  background-image: linear-gradient(45deg, #c9f, #edf, #c9f, #edf);
}

.contentTag[title^="Filter #firstchild"]::after {
  content: " \2b71";
}
.contentTag[title^="Filter #lastchild"]::after {
  content: " \2b73";
}
.contentTag[title^="Filter #firstchildren"]::after {
  content: " \2b71\2b71";
}
.contentTag[title^="Filter #lastchildren"]::after {
  content: " \2b73\2b73";
}
.contentTag[title^="Filter #randomchild"]::after {
  content: " \2b7f";
}
.contentTag[title^="Filter #randomchildren"]::after {
  content: " \2b7f\2b7e";
}

}
