/* Edited by hiren_16 */

/* Search box on top */
.os-win body, .os-win button, .os-win input, .os-win textarea {
    font-family: Roboto, Verdana, Tahoma, sans-serif;
}

/* Menu on Top */
.os-win .layout-header-or-footer, .os-win .layout-header-or-footer button, .os-win .layout-header-or-footer input {
	font-family: Roboto, Verdana, Tahoma, sans-serif;
}

/* Footer */
.layout-header-or-footer, .layout-header-or-footer button, .layout-header-or-footer input {
	font-family: Roboto, Verdana, Tahoma, sans-serif;
}

/* For job listing and details */
body #content-outlet * {
    font-family: Roboto, "Source Sans Pro", Helvetica, Arial, sans-serif;
}

/* Pulse Articles */
.prose > div, .prose p, .prose ol, .prose ul {
	font-weight: 300;
    font-family: Roboto, "Source Sans Pro", Helvetica, Arial, sans-serif;
}

/* Messages, Textarea */
#compose-textarea .compose-area, #compose-textarea .mentions-highlighter {
	font-family:inherit;
}
