css:
@namespace "http://www.w3.org/1999/xhtml";
@-moz-document domain("ridus.ru") {
body {
    background: #161d25 none repeat scroll 0 0;
    color: #a5bbdc;
    font: 14px/18px "pt_sansregular";
}
header {
    padding: 20px 0 0 !important;
}
.headerCont {
    background: #233040 none repeat scroll 0 0;
    height: 60px;
    margin: 0 auto;
    position: relative;
    width: 1020px;
}
.columns, .mainNews.updated, .itemsList {
    background: #1d232c none repeat scroll 0 0 !important;
    border: 1px solid #212832 !important;
}
.article blockquote, .itemList .text {
    border: medium none;
    color: #ccc;
}
.moreArticles {
    background: #222933 none repeat scroll 0 0;
    border: 1px solid #313842;
    padding: 46px 0 0;
}
.articleTitle h1 {
    color: #d84c1f;
    font-size: 30px;
    line-height: 30px;
}
.itemList .text h2 a {
    color: #9b59b6;
}
.itemList .text h2 a {
    background: #232f3d none repeat scroll 0 0;
    color: #9b59b6;
    padding: 2px 5px 5px;
}
.footerTop {
    background: #24303d none repeat scroll 0 0;
    border-top: 1px solid #304156;
}
.footerBott {
    background: #304156 none repeat scroll 0 0;
    font-size: 13px;
    padding: 21px 31px 18px;
}
.newsBest {
    border: 1px solid #ffd500;
    border-radius: 0;
    margin: 0 0 0 -5px;
    padding: 0 0 20px;
    text-align: center;
}
.newsBest h2 {
    background: #24303d none repeat scroll 0 0;
    color: #ccc;
    font: 20px/42px "open_sans_condensedbold";
    height: 42px;
    text-transform: uppercase;
}
.mainNews.updated .newsBest {
    border: 1px solid #24303d;
    width: 200px;
}
.newsBest li, .newsMost li {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
    border-bottom: 1px solid #000000;
    border-top: 1px solid #283644;
    margin-bottom: 0;
}
.mainNews.updated .newsMost {
    border: 1px solid #74478b;
    width: 240px;
}
.newsMost {
    border: 3px solid #f5f5f5;
    border-radius: 0;
    float: left;
    padding: 0 0 15px;
    text-align: center;
    width: 189px;
}
a {
    color: #777;
    text-decoration: none;
}
.newsMost h2 {
    background: #74478b none repeat scroll 0 0;
    color: #000;
    font: 20px/42px "open_sans_condensedbold";
    height: 42px;
    text-transform: uppercase;
}
.mostMost {
    border: 1px solid #f5f5f5;
    border-radius: 1px;
}
.bItems {
    background: #000 none repeat scroll 0 0;
    overflow: visible;
    position: relative;
}
.b-today .b-rate_value {
    color: white;
    font: 14px/12px "open_sans_condensedbold";
}
.b-tesaser .table, .b-tesaser .row {
    background: #304156 none repeat scroll 0 0;
    display: block;
    float: left;
}
.headerPanel {
    border-bottom: 15px solid #74478b;
    margin-top: -60px;
    overflow-y: hidden;
    transition-duration: 2s;
    transition-property: all;
    transition-timing-function: cubic-bezier(0, 0.5, 0.5, 1);
}
.headerPanel:hover {
    border-bottom: 15px solid #ffd103;
    margin-top: 0;
    transition-duration: 1s;
    transition-property: all;
    transition-timing-function: cubic-bezier(0, 0.5, 0.5, 1);
}
.newsMost a, .newsBest a, .moreBtn, .bCommentReplyBtn a, .footerTop a, .post-likes .item a i, .article img, .text h4 a, .itemList .text h2 a, .photo img {
    transform: scale(1);
    transition: all 0.3s ease 0s;
}
.newsMost a:hover, .newsBest a:hover, .moreBtn:hover, .bCommentReplyBtn a:hover, .footerTop a:hover, .post-likes .item a i:hover, .article img:hover, .text h4 a:hover, .photo img:hover {
    transform: scale(1.07);
}
.text .info a, .ya-share2__container_size_m .ya-share2__icon, .socList i, .post-likes .item a i {
    transform: scale(1);
    transition: all 0.3s ease 0s;
}
.text .info a:hover, .ya-share2__container_size_m .ya-share2__icon:hover, .socList i:hover, .post-likes .item a i:hover {
    transform: scale(1.3);
}
.article ul.list li a:hover, .article p a:hover {
    color: #ccc;
}
.bAddFormLogin .socList li a {
    color: #999;
}
.text h2:hover, .itemList .photo img:hover {
    border: 1px solid #666;
    box-shadow: 0 0 10px 0 rgba(4, 162, 220, 0.5);
    z-index: 100;
}
.footerTop .footInfo .socList i {
    left: -30px;
}
.headerPanelCont .menu li.menuItem a.menuItemLink {
    background: #000 none repeat scroll 0 0;
    border: 1px solid #666;
    color: #fff;
    font-family: Roboto,Raleway,DIN Pro,Verdana,Helvetica,Arial,sans-serif;
    font-size: 13px;
    font-weight: 100;
    margin: 2px;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f;
    text-transform: uppercase;
}
.headerPanelCont .menu li.menuItem a.menuItemLink:hover {
    background: #000 none repeat scroll 0 0;
    border: 1px solid #fff !important;
    box-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
    font-family: Roboto,Raleway,DIN Pro,Verdana,Helvetica,Arial,sans-serif;
    font-weight: 500;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
    text-transform: uppercase;
}
.headerPanelCont .menu {
    float: left;
    padding: 10px 0 0;
    width: auto;
}
.text h2 {
    font: 20px/20px "pt_sansbold";
}
.bComments .bComment, .bComments .title, .bAddFormLogin, .b-tesaser .title {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    border-bottom: 1px solid #74478b;
}
.bCommentReplyBtn a:hover, .bCommentMore a:hover {
    color: #74478b;
}
.banner2cont, .banner2 {
    display: none;
}
}
