body {
    background: url(http://www.zupimages.net/up/16/16/130n.png),url(http://www.zupimages.net/up/16/10/e47u.png), #000A1E;
     background-position: top center, bottom center;
    background-repeat: no-repeat;
    background-attachment:fixed;
  background-size:100%;
  cursor:  url(http://www.zupimages.net/up/16/14/r24o.png), auto;
}

/*body {
   background: url('http://www.zupimages.net/up/16/16/130n.png'), url(http://www.zupimages.net/up/16/10/e47u.png), #000A1E;
    background-position: top center, bottom center;
