css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("calendar.live.com") {

body {
    font: 13px/1.4 'Helvetica neue',Helvetica,Arial,sans-serif !important;
    color: #333;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
}
.monthGrid TD.lightbg {
    background-color: white;
}
.monthGrid TBODY TR TD {
    background-color: #f5f5f5;
}
.monthGrid TD.today {
    color: #2789e4;
}
DIV.monthGrid TD.today A.dateCellTitle, .dayView .monthGrid TR.headerRow TD.lastToday A.dateCellTitle {
    color: #2789e4;
}

/* avator */
#icTmMeControlMedium0_usertile {
    border-radius: 999px;
    width: 40px;
    height: 40px;
    position: relative;
    top: 5px;
    left: 5px;
}

/* color */
.color9 {
/*     background-color: #FBFCFC !important;
    border-left-color: #ECF0F1 !important;
    color: #547171 !important; */
    background-color: #BCF6EA !important;
    border-left-color: #ECF0F1 !important;
    color: #0F715D !important;
}

/* border */
.monthGrid TD.weekday {
    border-bottom: 1px solid #d9d9d9;
    border-right: 1px solid white;
    background-color: white;
}
.monthGrid .backgroundRow TD {
    border-right: 1px solid #d9d9d9;
    border-bottom: 1px solid #d9d9d9;
}
.monthGrid .headerRow TD {
    border-right: 1px solid #d9d9d9;
}
.monthGrid .backgroundRow TD.today {
    border-right: 1px solid #2789e4;
    border-bottom: 1px solid #2789e4;
    background-color: #F2F8FE;
}
.monthGrid .headerRow TD.today {
    background-color: #F2F8FE;
    border-right: 1px solid #2789e4;
}
.monthGrid .backgroundRow TD.yesterday {
    border-right: 1px solid #2789e4;
}
.monthGrid .headerRow TD.yesterday {
    border-right: 1px solid #2789e4;
}
.monthGrid TD.wbt {
    border-bottom: 1px solid #2789e4;
}
.monthGrid .backgroundRow TD.wbt {
     border-bottom: 1px solid #2789e4;
}


/* cell */
.dateCellTitle {
	color: black;
    font-size: 12px !important;
    font-weight: bold;
}

/* item */
.segmentContainer {
    height: auto !important;
}
.segmentContainer .inside {
    padding: 0 !important;
}
.inside .eventText {
    top: 2px;
}
/* fix position */
.monthGrid .segmentContainer {
    margin-left: 2px;
}

/* leftmenu */
.t_hdbg {
    background-color: #f5f5f5;
}

.t_hdbg span {
    font: 13px/1.4 'Helvetica neue',Helvetica,Arial,sans-serif;
    color: #b3b3b3;
    font-weight: bold;
    font-size: 14px;
}
#c_clogoc {
    background-color: inherit;
}

/* rightmemu */
#c_cb0 {
    background: #f5f5f5;
    border: 1px solid #d9d9d9;
    border-width: 1px 0;
}
#c_cme {
    background: #f5f5f5;
}
#c_cme span {
    color: #b3b3b3;
    font-weight: bold;
    font-size: 14px;
}

/* bar */
#icTmMeControlMedium0_bar {
    display: none;
    visibility: collapse;
}
a:visited, .t_lnk a:visited {
    color: #b3b3b3;
}

img.is_img {
    filter: invert(45%) !important;
}

#CB_NewItem .is_img {
    display: none;
}

/* border */
ul.c_cc {
    padding-left: 0;
}

div.c_clogo a {
    border-right: 1px solid #d9d9d9 !important;
}


ul.c_cc li {
    border-right: 1px solid #d9d9d9;
}

/* name */
#c_melink {
    padding-left: 0px;
    padding-right: 10px
}

#c_meun {
    display: none;
}

tbody {
    border: solid 1px red;
}

/* iframe */
#c_copt {
    border-top-width: 2px;
}

}
