css:
@-moz-document domain("www.bloxcity.com") {
nav[style="box-shadow:none;box-shadow: 1px 2px 2px #ccc;"] {
    box-shadow: none!important;
}
.white {
    background-color: #2258CD!important;
}
.entire-page-wrapper {
    background-image: url("http://i.imgur.com/HAUOQqP.png")!important;
    background-position: center top!important;
    background-repeat: no-repeat!important;
    background-color: #070b2b!important;
    background-size: 120% auto!important;
}
element, .style {
    background: #010e39!important
}
col-md-6 text-right {
    color: #FFF!important;
}
col-md-6 {
    color: #FFF!important;
}
i {
    color: #FFF!important;
}
e {
    color: #FFF!important;
}
.side-menu-title {
    color: #FFF!important;
}
div[style*="background:#fff;border:2px solid #D5D6D8;border-radius:5px;padding:30px;"], .bc-content {
    background-color: rgba(0, 0, 0, 0.6)!important;
    color: #0F0F0F!important;
    box-shadow: 0 1px 2px #040122!important;
    border: none!important;
}
p, h1, h2, h3, h4, h5, h6, h7, e, div, label, input, i, a, .active, th, td, strong, tr, font, .caret {
    color: #FFF!important;
}
td, .table-responsive {
    color: #FFF!important;
}
ul > li > ul[style*="width: 191px; position: absolute; top: 0px; left: 1334.95px; opacity: 1; display: block;"], #mobile-demo {
background-color: transparent!important;
}
.side-menu-link:hover {
    background: rgba(0, 0, 0, 0.25)!important;
}
.footer-push {
    background-color: #070b2b!important;
}
.site-footer {
    background-color: #000618!important;
}
.circle {
    border: 0px solid #010e39!important;
}
table.highlight>tbody>tr:hover {
    background-color: rgba(0, 0, 0, 0.55)!important;
}
.modal, .open, .modal-footer, #ConvertCurrencies, .select-dropdown {
    background-color: #333!important;
    color: #FFF!important;
}
.select-wrapper input.select-dropdown {
    background-color: transparent!important;
    top: inherit!important;
}
.dropdown-content {
    background-color: unset;
    top: inherit!important;
}
.dropdown-content li:hover, .dropdown-content li.selected {
    background-color: rgba(238, 238, 238, 0.25)!important;
}

div[style*="word-break:break-word;padding-top:10px;color:#666;font-size:14px;"] {
    color: #A8A8A8!important;
}
div[style*="border-top:1px solid #eee;"] {
    border-top: 1px solid rgba(255, 255, 255, 0.25)!important;
}
tbody > tr[style*="border-bottom:1px solid #eee;"] {
    border-bottom: 1px solid rgba(255, 255, 255, 0.25)!important;
}
div >img[style*="top:2px;right:2px;position:absolute;"] {
	content: url("http://i.imgur.com/Io1rV76.png")!important;
    height: 25px!important;
    width: 25px!important;
}
img[style*="padding:25px;border:1px solid #eee;"] {
	border: none!important;
}
.disabled>span {
	color: white!important;
}
}
