css:
@-moz-document url-prefix("http://www.4chan.org/") {
:root {
--bg: #1d1e2a !important;          /*  var(--bg)   */
--bg2: #1a1b26 !important;          /*  var(--bg2)   */  
--bg-dark: #14151F !important;             /*  var(--bg-dark)   */
--bg3: rgba(20,21,31,0.89) !important;      /* semi transparent bg */
--first-color: #888 !important;             /*  grey  */  
--second-color: #74857c !important;             /*  green   */  
--third-color: #556b82 !important;             /* blue  */
}    

body, html{
    background: var(--bg);
}

#boards ,#popular-threads, #site-stats{
	box-shadow: 0px 0px 25px -4px rgba(0,0,0,0.75);
}

.top-box {
    background: var(--bg2);
}

.box-outer {
    border: 0px solid;
    margin-top: .5em
}

.boxbar{
    background-color:var(--bg-dark);
}

h2 ,#boards a, .c-board {
  color: var(--first-color);
}

h3, .c-teaser, .stat-cell{
    color: var(--second-color);
}

sup{
    color: var(--third-color);
    
}

#filter-container, #options-container {
    opacity:0;
}

.c-thread img {
    border: 0px solid var(--second-color);
}

#ft li.fill {
    border: 0;
    padding-bottom: 4px;
    border-top: 0px solid;
    width: 68px;
}

#ft li.fill {
    opacity:0;
}

#ft ul {
    border-top: 0px solid;
}

#ft li, #copyright a, #copyright {
    opacity:0;
    font-size:0px;
    width:0px;
    height:0px;
}

#ft{
    font-size: 0%;
}

#logo-fp{
    width: 0px;
    height: 0px;
}

#boards a:hover {
    color: var(--third-color);
    text-decoration: none;
}
}
