css:
@-moz-document domain(djangoproject.com) {
 
body,
  [role="main"],
  .mdzr-boxshadow.container.sidebar-right {
  	background-color: #222 !important;
  	background-image: url(data:image/png;base64,) !important;
  	background-clip: border-box !important;
  	background-origin: padding-box !important;
  	background-attachment: scroll !important;
  	background-repeat: repeat !important;
  	background-size: auto !important;
  	background-position: left top !important;
  }
  
  @media screen and (min-width: 768px) {
  	.mdzr-boxshadow .sidebar-right [role="main"] {
  		box-shadow: 0px 0px 0 0 rgba(255, 255, 255, 0), 0px 0px 0 0 rgba(255, 255, 255, 0), -460px 0 0 0 rgba(255, 255, 255, 0), -1000px 0 0 0 rgba(255, 255, 255, 0), 0px 600px 0 0px rgba(255, 255, 255, 0), -460px 600px 0 0px rgba(255, 255, 255, 0);
  	}
  }
  
  .mdzr-boxshadow .container.sidebar-right {
  	box-shadow: -1200px 0 0 0px rgba(255, 255, 255, 0);
  }
  
  [role="main"] h1,
  h2,
  body,
  h3,
  h4,
  h5,
  h6,
  tt,
  code,
  kbd,
  pre,
  samp,
  .reference.internal {
  	color: #D1D1D1;
  }
  
  ul>li:first-child>.reference.internal,
  a,
  span.pre {
  	color: #44b78b;
  }
  
  .admonition,
  .admonition.warning,
  .admonition.note,
  .admonition.danger,
  pre.literal-block,
  .literal-block {
  	background-color: rgba(0, 0, 0, 0.2);
  }
  
  .highlight {
  	background-color: rgba(255, 255, 255, 0.2);
  }
  
  table.django-supported-versions tr {
  	background-color: rgba(201, 240, 221, 0.5);
  }
  
  table.django-supported-versions tr.unsupported {
  	background-color: rgba(255, 186, 186, 0.5);
  }
  
  .a.reference,
  #docs-content a.reference {
  	color: rgb(175, 67, 67);
  }
   /* your code goes here */
}
